/* i really want this to be global */
/*###############################背景图片#################################  */
html body {
    color: #eee;
    background-image: url(https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=4204780978,2668555092&fm=26&gp=0.jpg);
    background-repeat: no-repeat;
    background-size: 100%;
    background-position: center;
    background-attachment: fixed;
}



/* ##############################导航栏############################# */
#head {
    background: #5959594a;
    backdrop-filter: blur(5px);
    border-bottom: 4px solid #48484863;
    border-left: 4px solid #48484863;
    border-right: 4px solid #48484863;
    border-radius: 10px;
    margin: 0;
    padding: 0;
}

.fm {
    margin: 3px;
}

/* 删掉百度的logo */
#result_logo img {
    width: 0;
}

/* 搜索框往右移动 */
.s_form {
    height: 10px;
    padding: 0px 123px 6px 109px;
}

/* 搜索框加圆角 */
.s_ipt_wr.bg,
.s_btn_wr.bg,
#su.bg {
    border-radius: 20px;
    padding-left: 10px;
}

/* 搜索框文字颜色 */
#kw {
    color: #fff;
}

/* 按图搜索的按钮 */
.soutu-btn {
    background: #54545400;
    color: #66d8ea;
    border: 2px solid;
    height: 10px;
    width: 10px;
    border-radius: 10px;
}

/* 百度一下按钮 */
#form {
    display: flex;
    height: 100%;
}
.s_btn {
    background: #00000088;
    border: 3px solid #66666688;
    margin: 0 auto;
    align-self: center;
    color: #3ccac2;
}
/* 百度一下按钮指针放上去 */
.s_btn.btnhover {
    background: #5e5e5e88;
    border: 3px solid #95959588;
    box-shadow: 0px 0px 0px #000;
}
/* head bar右边的 百度首页, 设置啥的 */
#u {
    vertical-align: middle;
}
#u a {
    text-decoration: none;
    color: #3ccac2;
    margin: 0 7px 0 0;
}
.wrapper_s #u {
    margin: 20px 10px 0px 0px;
}

/* ##################################body ################################*/
/* 网页,资讯,视频...那个条 */
#s_tab {
    line-height: 36px;
    height: 45px;
    padding: 55px 0 0 121px;
    float: none;
    zoom: 1;
    background: #545454aa;
    backdrop-filter: blur(10px);
}
#s_tab a,
#s_tab b {
    color: #e1e1e1;
    font-size: 10px;
    padding: 0;
    margin: 0;
}
#s_tab b {
    border-bottom: 2px solid #3ccac2;
    border-radius: 2px;
}
/* 为您找到多少结果 */
.nums {
    color: #ccc;
}

/* 搜索工具 */
.search_tool,
.search_tool_conter span {
    cursor: pointer;
    color: #cacaca;
}

/*######################搜索结果 ########################### */
/* 结果中的文字 */
em {
    font-style: normal;
    color: #ff7e7e;
}
a:-webkit-any-link {
    color: #3ccac2;
    cursor: pointer;
    text-decoration: none;
}

#content_left {
    width: 60%;
}

/* 搜索结果加背景 */
#content_left .result-op,
#content_left .result {
    width: 100%;
    margin-bottom: 14px;
    border-collapse: collapse;
    border-radius: 10px;
    background: linear-gradient(145deg, #20202075, #5e5e5e00);
    padding: 10px;
}
/* 搜索结果日期颜色 */
.m,
a.m {
    color: #b5b5b5;
}

/* ######################底部########################### */
#rs,
#page a,
#page a,
#page strong,
#foot,
#foot span {
    background: #54545400;
}

#foot {
    margin-top: 0;
    background: #545454aa;
    backdrop-filter: blur(10px);
    border: 0;
}

#page {
    margin: 0;
}

/* 下面12345678页的按钮 */
#page .n,
#page .pc {
    border-radius: 10px;
    border: 2px solid #6a6a6a75;
}
#page .fkd .c-icon-bear-pn,
#page .fk .c-icon-bear-pn,
#page .fk_cur .c-icon-bear-p {
    display: none;
}


/* #######################整个页面毛玻璃########################## */
#container {
    width: 100%;
    background: #545454aa;
    backdrop-filter: blur(10px);
}

/* ###################滚动条样式############################### */
::-webkit-scrollbar {
    width: 15px;
}
/* 这是针对缺省样式 (必须的) */
::-webkit-scrollbar-track {
    /*   border-radius: 10px; */
    background: #626262;
}
/* 滚动条的滑轨背景颜色 */
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    background: linear-gradient(145deg, #7a7e80, #464646);
    box-shadow: 3px 3px 5px #12272c;
}
/* 滑块颜色 */
::-webkit-scrollbar-button {
    /*       height :0 */
}
/* 滑轨两头的监听按钮颜色 */
::-webkit-scrollbar-corner {
    background-color: #2D343B;
}
/* 横向滚动条和纵向滚动条相交处尖角的颜色 */
/* #wrapper{
    background: #545454aa;
    backdrop-filter: blur(10px);
} */
.c-showurl {
    color: rgb(126, 221, 109);
}


* {
    color: #ccc;
}

#header_top_bar {

    /*     margin: 100px 100px 100px 100px; */
    margin: 0px 0px 0px 0px;
    width: 100%;
    /*     padding: 10px 100px 10px 10px; */
}
