css:
@-moz-document domain("seznam.cz") {
body {
 font:1em/1.25 Arial,sans-serif;
 color:#555;
}
.button--scroll-up,
.button--submit {
    background-color:gray;
    color:silver
}
.button--scroll-up.button--pressed-down,
.button--scroll-up:focus,
.button--scroll-up:hover,
.button--submit.button--pressed-down,
.button--submit:focus,
.button--submit:hover {
    background-color: maroon;
}
.link {
 color: gray
}
.link:hover {
 color: silver
}
.link--show-visited,
.color-link,
.color-link:visited {
    color: Gray 
}
.link--show-visited:visited,
.szn-trending-topics__link,
.szn-trending-topics__link:visited { 
    color: #555 
}

/* email */
.email.email--unread:hover {
    background-color: maroon;
}

/* nadpis bloku kromě Radia Poznámek Kurzů */
.link--gadget-title {
 color: silver
}
.gadget__header {
    border-bottom: .0625rem solid maroon;
}

/* selfpromo, svatek-jmeno */
.style-tags a {
 text-decoration:none;
 color:gray
}
/* tv graf */
.gadget--tv .running-show__progress-perc {
 background-color:maroon
}
/* loga radií */
.c-RadioSeekBar {
    display: none !important;
}
.c-RadioCarousel {
    opacity: .1 !important;
}
.c-RadioCarousel:hover {
    opacity: 1 !important;
}
/* search */
.input.input--hp-search:focus {
 border: none;
 box-shadow: none;
}
.svg-tab-pointer__path {
    display: none;
}
/* gadget__promo Novinky */
.gadget__promo {
 color:silver;
 background:#555
}
}
