css:
@-moz-document domain("clipr.xyz") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background: linear-gradient(-225deg, #6441a5 0%, #8D0B93 50%, #321575 100%);
        }

body {
    background: #151515;
	color: #d1d5d9;
}
.clipr-card.card, .clipr-info-card.card, .card {
    background: #191919;
}
#clip_url, .clipr-textbox {
    background: #252525 !important;
    color: #fff;
}
.footer > .container.text-center:after {
    content: 'Dark Theme Made by blyad';
    color: #9caab7a6;
}
.blog-text, .blog-text-md {
    color: #306384 !important;
}
.page_speed_1455266486 {
    color: #3668a4 !important;
}
.wg-drop.country-selector, .wg-drop.country-selector:not(.closed) ul {
	background: #151515;
}
.wg-drop.country-selector .wgcurrent, .wg-drop.country-selector:not(.closed) ul {
    border: 1px solid #222;
}
.wg-drop.country-selector a {
	color: #fff;
}
.wg-drop.country-selector .wgcurrent:after {
	filter: invert(1);
}
.clipr-textinfo {
    color: #c8eaff!important;
}
.clipr-tinfolink, .page_speed_2040448061 {
    color: #6d45ca!important;
}
.page_speed_635900675, .page_speed_1641389603 {
    color: #417bbf !important;
}
.alert-warning {
    color: #d09e0a;
    background-color: #2b2514;
    border-color: #483e1e;
}
.text-muted {
    color: #9caab7!important;
}
.card-header {
    background-color: rgba(255, 255, 255, .03);
    border-bottom-color: rgba(255, 255, 255, .125);
}
}
