css:
@-moz-document domain("wnet.fm") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #ffae2b #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #ffae2b !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
    text-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #ffae2b !important;
}
input[type="submit"]:hover{
    background: #ffae2b !important;
    color: #fff !important;
}
/*HEADER*/
#menu-primary .menu-primary li a span{
    color: #ffae2b !important;
}
#menu-primary .menu-primary li a:hover span{
    color: #fff !important;
}
#wnet-site-heading .heading-module-logo img{
    background: #fff;
    border: 3px solid #ffae2b;
}
#menu-primary .menu-primary{
    background: #1c1c1c;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.container{
    background: #1c1c1c;
}
.excerpt-label{
    background: #1c1c1c;
    border: 1px solid #ffae2b;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.entry-tags{
    border-color: #ffae2b;
}
.entry-tags a{
    background: #1c1c1c;
    border: 1px solid #ffae2b;
}
.entry-tags a:hover{
    background: #ffae2b;
    color: #1c1c1c !important;
}
article .entry-toolbar div span img.button-img{
    filter: invert(65%) sepia(62%) saturate(563%) hue-rotate(349deg) brightness(102%) contrast(101%);
}
hr.separator-black, hr.separator-black-fb{
    background: #ffae2b;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
/*LOGOWANIE*/
.login form{
    background: #1c1c1c;
    border: 1px solid #ffae2b;
}
.login .message,.login #login_error,.login .success{
    background: #1c1c1c;
}
/*LOGOWANIE*/
/*PODSTRONY*/
/*FOOTER*/
.site-footer .list-inline li a,.author-mail a{
    color: #1c1c1c !important;
}
.site-footer .copyright-info span:first-child:before{
    content: "Ciemny motyw by Maciejozaman ";
    color: #1c1c1c;
    cursor: none;
}
/*FOOTER*/
}
