css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userscripts.org") {

body {
  font-family: verdana, arial, helvetica, sans-seriff !important; 
  width:85%!important;
  margin-left: auto !important;
  margin-right: auto !Important;
  }

div#header {
  background-color: #ff8800 !important;
  border-bottom: 20px solid #ffdd00 !important;
  background-repeat: no-repeat !important;
  }

div#header h1 > a > img {
  width: 0 !important;
  background-image:url("data:x-png/image;base64,") !important;
  padding-right: 250px !important;
  height: 36px !important;
  }

table[class="wide\ forums"] > tbody > tr > td,
table[class="wide\ forums"] > tbody > tr + tr {
  max-height: 42px !important;
  height: 42px !important;
  border: 1px dashed #cccccc !important;
  }

table[class="wide\ forums"] > tbody > tr + tr {overflow: hidden !important;}

td.script-meat {padding-top:0px!important}

td.script-meat > a.title, td.c1 > a.title {
  font-size:medium !important;
  max-height: 20px !important;
  height: 20px !important;
  line-height: 86% !important;
  }

td.c1 > p.desc,
td.script-meat > p.desc
{
  max-height: 22px !important;
  height: 22px !important;
  line-height: 104% !important;
  }

td.script-meat, td.script-install,
td.c1 {background-color: #f0f8ff !important}

div#content tr[class^="post"] > td[class="author\ vcard"] > img.photo {font-size: 0 !important}

div#right > form + h5 {
  background-color:#f0f8ff!important;
  border: 1px dashed #cccccc !important;
  margin-bottom:0!important;
  border-bottom: 0px !important;
  line-height: 400% !important;
  }

div#right > form + h5 + p {
  background-color:#f0f8ff!important;
  border: 1px dashed #cccccc !important;
  margin-top: 0 !important;
  border-top: 0px !important;
  }

div#right {
  background-color: #fff8f0 !important;
  border: 1px solid #ffc580 !important;
  }

div.stats {
  background-color: #eeeeee !important;
  color: #ff8800 !important;
  border-color: #dddddd !important;
  }

p.pages {
  background-color: #fff8f0 !important;
  border: 1px solid #ff8800 !important;
  }

p.credit {display: none !important}

div#right > *:not(form) {padding-left: 2px !important}

div#right > ul {padding-left:24px !important;}

}


@-moz-document url-prefix(http://userscripts.org/scripts), url-prefix(http://www.userscripts.org/scripts) {

div#content > h1 {
  color: #0088ff !important;
  }

div#full_description {
  font-size: small !important;
  border: 1px solid #ffc580 !important;
  text-indent: 1% !important;
  }

div#content > h1 + p {
  font-size: small !important;
  color: #0088ff !important;
  background-color: #fff8f0 !important;
  border: 1px solid #ffc580 !important;
  text-indent: 1% !important;
  }

div#content table[class^="posts"], div#content tr[class^="post"] {border: 1px solid #cce8ff !important}

div#content tr[class^="post"] > td[class="body\ entry-content"] {background-color: #f0f8ff !important}

div#content tr[class^="post"] > td[class="author\ vcard"] {
  background-color: #f0f8ff !important;
  border-right: 1px solid #cce8ff !important;
  }

div#content tr[class^="post"] > td[class="author\ vcard"] > span > a {color: #0088ff !important}

div#content tr[class^="post"] {border: 1px solid #cce8ff !important}

div#right {
  background-color: #fff8f0 !important;
  border: 1px solid #ffc580 !important;
  }

div#right > p > a.button {
  color: #0088ff !important;
  background-color: #f0f8ff !important;
  font-weight: bold !important;
  text-transform: uppercase !important;
  }

div#right > p > br + a, div#right a {padding-left: 4% !important;}

}


@-moz-document url-prefix(http://userscripts.org/users/), url-prefix(http://www.userscripts.org/users/), url-prefix(http://www.userscripts.org/people/), url-prefix(http://userscripts.org/people/) {

div#content > p + h4 + br + h4, div#content > a +br + br + h4 {
  background-color: #fff8f0 !important;
  border: 1px solid #ffc580 !important;
  border-bottom: 0px !important;
  }

div#content > p + h4 + br + h4 + p.subtitle, div#content > a +br + br + h4 + p.subtitle {
  background-color: #fff8f0 !important;
  border: 1px solid #ffc580 !important;
  border-top: 0px !important;  
  }

div#content > p > img.photo {font-size:8pt!important}

div#content > p + h4 + br + h4 + p.subtitle + h4, div#content > a +br + br + h4 + p.subtitle + h4 {
  background-color:#f0f8ff!important;
  border: 1px solid #cccccc !important;
  border-bottom: 0px !important;
  }

div#content > p + h4 + br + h4 + p.subtitle + h4 + p.subtitle, div#content > a +br + br + h4 + p.subtitle + h4 + p.subtitle {
  background-color:#f0f8ff!important;
  border: 1px solid #cccccc !important;
  border-top: 0px !important;
  }

div#content > p + h4 + br + h4 + p.subtitle + h4 + p.subtitle + h4, div#content > a +br + br + h4 + p.subtitle + h4 + p.subtitle + h4 {
  background-color: #dddddd !important;
  border: 1px solid #cccccc !important;
  border-bottom: 0px !important;
  }

div#content > p + h4 + br + h4 + p.subtitle + h4 + p.subtitle + h4 + p.subtitle, div#content > a +br + br + h4 + p.subtitle + h4 + p.subtitle + h4 + p.subtitle {
  background-color: #dddddd !important;
  border: 1px solid #cccccc !important;
  border-top: 0px !important;
  }

table[class="comments\ wide"] > tbody:first-child {
  background-color: #f0f8ff !important;
  border: 1px dashed #cccccc !important;  
  }
}


@-moz-document url-prefix(http://userscripts.org/scripts/source/), url-prefix(http://www.userscripts.org/scripts/source/), url-prefix(http://userscripts.org/scripts/review/) {
body {width:98% !important}
}
