@-moz-document domain("www.lightnovel.cn"), domain("lightnovel.cn"), domain("www.lightnovel.us"), domain("lightnovel.us") {
button,
button.pn {
    background: var(--style-color);
    color: white;
    border: none;
    border-radius: 1000px;
    box-shadow: none;
    transition-duration: 0.3s;
    outline: none;
}

button:hover,
button.pn:hover {
    background: var(--style-highlight-color);
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
}

input[type="text"],input[type="password"], input.px, select {
    border-radius: 5px;
    border: 1px solid #ddd;
    background: white;
    transition-duration: 0.3s;
    outline: none;
}

select {
    border: 1px solid #ddd !important;
    background: white !important;
}

input[type="text"]:focus,input[type="password"]:hover, input.px:hover,input[type="text"]:focus,input[type="password"]:focus, input.px:focus,select:hover, select:focus {
    border: 1px solid var(--style-highlight-color);
    box-shadow: 0px 0px 4px 4px var(--style-shadow-color);
}

select:hover, select:focus {
    border: 1px solid var(--style-highlight-color) !important;
}

body {
    overflow-x: hidden;
}

#toptb {
    display: none;
}

.xi1 {
    color: var(--style-darken-color);
}

#pt {
    position: relative;
}

#pt .z {
    position: absolute;
}

#hd .wp {
    padding: 0;
}

#hd .wp .hdc,
#hd .wp {
    min-height: 50px;
    height: 50px;
}

body {
    background: #f7f7f7 !important;
}

.hdc.cl h2 {
    display: block;
}

.hdc.cl h2 img {
    display: none;
}

.hdc.cl h2 a {
    display: block;
    background: transparent;
    position: fixed;
    top: 0;
    left: 100px;
    height: 40px;
    width: 40px;
    z-index: 101;
}

.hdc.cl h2 a::before {
    content: " ";
}

.fastlg_fm.y {
    display: none;
}

.y.pns table tbody tr:nth-of-type(1),
.y.pns table tbody tr:nth-of-type(2) td:nth-of-type(1),
.y.pns table tbody tr:nth-of-type(2) td:nth-of-type(2) {
    display: none;
}

#lsform {
    position: fixed;
    right: 15px;
    top: 10px;
    z-index: 100;
}

#lsform .fastlg_l button {
    width: 50px !important;
    height: 50px;
    border: 0;
    background: var(--style-color);
    border-radius: 50%;
    margin: 0;
    transition-duration: 0.3s;
    outline: none;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
}

#lsform .fastlg_l button:hover {
    background: var(--style-highlight-color);
}

#lsform .fastlg_l button em {
    color: white;
    font-size: 14px;
}

#lsform td.fastlg_l {
    display: block;
    padding: 0 !important;
    border: 0 !important;
}

#lsform td.fastlg_l + td {
    display: block;
    font-size: 0;
    line-height: 0;
    height: 50px;
    width: 50px;
    padding: 0!important;
    margin-top: 10px;
    background: var(--style-color);
    border-radius: 50%;
    text-align: center;
    transition-duration: 0.3s;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
}

#lsform td.fastlg_l + td:hover {
    background: var(--style-highlight-color);
}

#lsform td.fastlg_l + td:hover a.xi2.xw1 {
    border-bottom: 0;
}

#lsform .fastlg a.xi2.xw1 {
    text-decoration: none;
    border: none !important;
}

#lsform .fastlg a.xi2.xw1::after {
    content: "注册";
    font-size: 14px;
    color: white;
    line-height: 50px;
}

#um {
    position: fixed;
    right: 15px;
    top: 10px;
    padding: 0;
    z-index: 101;
}

#um .avt.y {
    float: unset;
    margin: 0;
}

#um .avt img {
    border: 0;
    background: transparent;
    height: 50px;
    width: 50px;
    padding: 0;
    margin: 0;
    border-radius: 50%;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
}

#um .avt::after {
    content: "";
    display: block;
    position: fixed;
    top: 60px;
    height: 12px;
    width: 50px;
}

#um p a {
    display: none;
}

#um p:nth-of-type(1) .vwmy,
#um p:nth-of-type(1) #myitem,
#um p:nth-of-type(1) a[href="home.php?mod=spacecp"],
#um p:nth-of-type(1) #pm_ntc,
#um p:nth-of-type(1) #myprompt,
#um p:nth-of-type(1) a[href^="portal.php?mod=portalcp"],
#um p:nth-of-type(1) a[href^="admin.php"],
#um p:nth-of-type(1) a[href^="forum.php?mod=modcp"],
#um p:nth-of-type(1) a[href^="member.php?mod=logging&action=logout"],
#um p:nth-of-type(2)::before,
#um p:nth-of-type(2) #extcreditmenu,
#um p:nth-of-type(2) #g_upmine {
    display: block;
    margin-top: 10px;
    text-align: center;
    height: 45px;
    width: 45px;
    border-radius: 50%;
    padding-top: 5px;
    padding-left: 5px;
    background: white;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
    transform: translateX(70px);
    opacity: 0;
    color: #555;
    padding-right: 0;
    line-height: 19.5px;
    text-decoration: none;
}

#um p:nth-of-type(1) .vwmy {
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.75s cubic-bezier(0.23, 1, 0.32, 1) 0s, opacity 0.75s linear 0s;
}

#um p:nth-of-type(1) #myitem {
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.75s cubic-bezier(0.23, 1, 0.32, 1) 0.05s, opacity 0.75s linear 0.05s;
}

#um p:nth-of-type(1) a[href="home.php?mod=spacecp"] {
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.75s cubic-bezier(0.23, 1, 0.32, 1) 0.1s, opacity 0.75s linear 0.1s;
}

#um p:nth-of-type(1) #pm_ntc {
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.75s cubic-bezier(0.23, 1, 0.32, 1) 0.15s, opacity 0.75s linear 0.15s;
}

#um p:nth-of-type(1) #myprompt {
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.75s cubic-bezier(0.23, 1, 0.32, 1) 0.2s, opacity 0.75s linear 0.2s;
}

#um p:nth-of-type(1) a[href^="portal.php?mod=portalcp"],
#um p:nth-of-type(1) a[href^="admin.php"],
#um p:nth-of-type(1) a[href^="forum.php?mod=modcp"] {
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.75s cubic-bezier(0.23, 1, 0.32, 1) 0.25s, opacity 0.75s linear 0.25s;
}

#um p:nth-of-type(1) a[href^="member.php?mod=logging&action=logout"] {
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.75s cubic-bezier(0.23, 1, 0.32, 1) 0.3s, opacity 0.75s linear 0.3s;
}

#um p:nth-of-type(2) #extcreditmenu {
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.75s cubic-bezier(0.23, 1, 0.32, 1) 0s, opacity 0.75s linear 0s;
}

#um p:nth-of-type(2) #g_upmine {
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.75s cubic-bezier(0.23, 1, 0.32, 1) 0.05s, opacity 0.75s linear 0.05s;
}


#um p:nth-of-type(1) .vwmy:hover,
#um p:nth-of-type(1) #myitem:hover,
#um p:nth-of-type(1) a[href="home.php?mod=spacecp"]:hover,
#um p:nth-of-type(1) #pm_ntc:hover,
#um p:nth-of-type(2) #extcreditmenu:hover,
#um p:nth-of-type(2) #g_upmine:hover,
#um p:nth-of-type(1) #myprompt:hover,
#um p:nth-of-type(1) a[href^="portal.php?mod=portalcp"]:hover,
#um p:nth-of-type(1) a[href^="admin.php"]:hover,
#um p:nth-of-type(1) a[href^="forum.php?mod=modcp"]:hover,
#myprompt_menu:hover ~ #hd #um p:nth-of-type(1) #myprompt {
    color: white;
    background: var(--style-highlight-color);
}

#um p:nth-of-type(1) a[href^="portal.php?mod=portalcp"],
#um p:nth-of-type(1) a[href^="admin.php"],
#um p:nth-of-type(1) a[href^="forum.php?mod=modcp"] {
    letter-spacing: 6px;
}

#um p:nth-of-type(1) {
    position: fixed;
    right: 15px;
    top: 60px;
}

#um p:nth-of-type(2) {
    position: fixed;
    right: 15px;
    top: 60px;
    transition: 0.5s;
}

#um p:nth-of-type(2)::before {
    font-family: "Material Icons";
    content: "person";
    color: white;
    background: var(--style-color);
    opacity: 1;
    transform: translateX(0px);
    font-size: 35px;
    line-height: 35px;
    padding-left: 0;
    width: 50px;
    transition: background 0.3s linear 0s, color 0.3s linear 0s, transform 0.3s cubic-bezier(0.23, 1, 0.32, 1) 0s, opacity 0.3s linear 0s;
}

#um p:nth-of-type(2):hover::before {
    background: var(--style-highlight-color);
}

#um p:nth-of-type(1) .vwmy::before {
    content: "我的空间";
    display: block;
    line-height: 19.5px;
    letter-spacing: 6px;
    font-weight: 400;
}

#um p:nth-of-type(1) .vwmy a {
    font-size: 0;
    display: block;
    position: absolute;
    height: 50px;
    width: 50px;
    top: 0;
    left: 0;
}

#um p:nth-of-type(1) #loginstatus,
#um p .pipe,
#um p:nth-of-type(1) #loginstatus + .pipe + a {
    display: none;
}

#um p:nth-of-type(1) #myprompt {
    font-weight: 400;
}

#um p:nth-of-type(1) #myprompt::before {
    padding-left: 3px;
    font-size: 12px;
    content: "我的消息";
    letter-spacing: 6px;
    white-space: pre-wrap;
    width: 40px;
    height: 40px;
    display: block;
}

#um p:nth-of-type(1) #myprompt.new + #myprompt_check {
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
    display: block;
    content: "";
    position: absolute;
    top: -50px;
    right: 0px;
    background: red;
    height: 12px;
    width: 12px;
    border-radius: 50%;
}

#um p:nth-of-type(1) #myprompt.new::after,
#um p:nth-of-type(1) #pm_ntc.new::after {
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
    display: block;
    content: "";
    position: absolute;
    top: 0px;
    right: 0px;
    background: red;
    height: 12px;
    width: 12px;
    border-radius: 50%;
}

#um p:nth-of-type(1) #myitem,
#um p:nth-of-type(1) a[href="home.php?mod=spacecp"],
#um p:nth-of-type(1) #pm_ntc,
#um p:nth-of-type(1) #myprompt {
    padding-right: 0;
    font-size: 0;
    line-height: 19.5px;
    text-decoration: none;
}

#um p:nth-of-type(1) #myitem::before {
    padding-left: 3px;
    font-size: 12px;
    content: "我的足迹";
    letter-spacing: 6px;
    white-space: pre-wrap;
    width: 40px;
    height: 40px;
    display: block;
}

#um p:nth-of-type(1) a[href="home.php?mod=spacecp"]::before {
    padding-left: 3px;
    font-size: 12px;
    content: "设置中心";
    letter-spacing: 6px;
    white-space: pre-wrap;
    width: 40px;
    height: 40px;
    display: block;
}

#um p:nth-of-type(1) #pm_ntc::before {
    padding-left: 3px;
    font-size: 12px;
    content: "我的私信";
    letter-spacing: 6px;
    white-space: pre-wrap;
    width: 40px;
    height: 40px;
    display: block;
    font-weight: 400;
}

