.pgs.cl {
    z-index: 100;
    position: sticky;
    bottom: 15px;
    height: 60px;
    margin: 20px -70px 20px -70px;
    border-radius: 30px;
    background: var(--style-color);
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
    padding: 0 30px !important;
}

.pgs.cl .pg,
.pgs.cl .pgb {
/*     display: inline-block; */
    height: 20px;
    margin: 20px 0;
    float: right;
}

.pgs.cl .pg a,
.pgs.cl .pg label,
.pgs.cl .pgb a {
    height: 20px;
    border: none;
    line-height: 19px;
    background: var(--style-darken-color);
    border-radius: 10px;
    color: white;
    transition-duration: 0.3s;
    overflow: visible;
}

.pgs.cl .pg strong {
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
    margin-top: -2.5px;
    padding: 0 10px;
    height: 25px;
    border: none;
    line-height: 24px;
    background: var(--style-highlight-color);
    border-radius: 15px;
    color: white;
    transition-duration: 0.3s;
    overflow: visible;
    font-size: 14px;
}

.pgs.cl .pg a:hover,
.bm.bw0.pgs.cl .pgb a:hover {
    background: var(--style-highlight-color);
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
}

.pgs.cl .pgb a {
    padding-left: 5px;
}

.pgs.cl .pg a.nxt {
    padding-right: 5px;
}

.pgs.cl .pg a.nxt::after {
    display: inline-block;
    content: "navigate_next";
    font-size: 20px;
    line-height: 20px;
    font-family: "Material Icons";
    vertical-align: -0.3em;
}

.pgs.cl .pg label input.px {
    position: relative;
    border: none;
    width: 40px;
    background: var(--style-darken-color);
    color: white;
    text-align: center;
    transition-duration: 0.3s;
    box-shadow: none;
}

.pgs.cl .pg label input.px:focus {
    display: inline-block;
    border: none;
    color: white;
    background: var(--style-highlight-color);
    outline: none;
    height: 25px;
    margin-top: -3px;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.08), 0 2px 8px 0 rgba(0, 0, 0, 0.08);
    border-radius: 20px;
}

.pgs.cl .pg .prev {
    font-size: 0;
}

.pgs.cl .pg .prev::before {
    display: inline-block;
    content: "navigate_next";
    font-size: 20px;
    line-height: 20px;
    font-family: "Material Icons";
    vertical-align: -0.3em;
    transform: rotate(-180deg);
}

.pgs.cl .pg .prev::after {
    font-size: 12px;
    content: "上一页";
}

.pgs.cl label[for="delete_all"] {
    color: white;
    line-height: 60px;
}

.pgs.cl button.pn {
    background: var(--style-darken-color);
}

.pgs.cl button.pn:hover {
    background: var(--style-highlight-color);
}

#threadlist.slst .pbw {
