css:
@-moz-document domain("etsy.com") {
body, .ui-toolkit body, .ui-toolkit .body-max-width-980 #content {
    background-color: #111 !important;
}


.wt-bg-white, .ui-toolkit .bg-white, .ui-toolkit .btn-secondary:disabled, .ui-toolkit .btn-secondary.is-disabled, .ui-toolkit .btn-secondary, .ui-toolkit .global-nav, .wt-action-group .wt-action-group__item:not(.wt-btn--transparent):after, .nav-search-overlay .overlay-view, .ui-toolkit .tag-body, .ui-toolkit .panel-heading, .ui-toolkit .panel-header, .ui-toolkit thead, .primary .section, .ui-toolkit .wt-bg-white {
    background-color: #333 !important;
}

.ui-toolkit .alert-notice-lighter, .wt-bg-beeswax-tint, .ui-toolkit .card, .ui-toolkit .bg-gray-darker, .ui-toolkit .panel, .seller-nav .primary-bg-color, .ui-toolkit table, .ui-toolkit .bg-blinding-sandstorm, .ui-toolkit .bg-gray, .appstore-subapp .hero-background, .grace-management-subapp .clickable-row:hover .plan-table-row-content, .grace-management-subapp .clickable-row.is-open .plan-table-row-content, .ui-toolkit .help-center-background, .wt-bg-lavender-tint  {
    background-color: #222 !important;
}

.gnav-header .sub-nav, a.wt-transparent-card:before, div.wt-transparent-card:before, button.wt-transparent-card:before, .wt-bg-gray, .wt-input, .wt-textarea, .wt-input-btn-group__input, .ui-toolkit .wt-bg-denim-tint, .ui-toolkit .select-multi, .wt-input-btn-group, .ui-toolkit .dropdown, .tabs li.active>a, .tabs li.active .tab-title, .tabs li>a.active, .tabs li .tab-title.active, .tabs li.ui-state-active>a, .tabs li.ui-state-active .tab-title, .tabs li>a.ui-state-active, .tabs li .tab-title.ui-state-active, .primary .section .section-header, .primary .section h2 {
    background-color: #444 !important;
}

.textarea, .input-group input.text, .input-group textarea.text {
     background-color: #444 !important;
    color: #ccc !important;
}


/** text **/
.ui-toolkit .text-gray-darker, .gnav-header .text-link, .ui-toolkit body, .ui-toolkit .text-gray, a:link, a:visited, .wt-btn.wt-btn--transparent, input, .ui-toolkit .btn-secondary, .wt-text-gray, .ui-toolkit .responsive-nav-experiment, .wt-btn.wt-btn--outline, .ui-toolkit .alert-notice-lighter, .gnav-user-card .name, .gnav-shop-card .name, .gnav-user-card .profile-link, .gnav-shop-card .shop-link, .gnav-shop-card .edit-shop-link, .ui-toolkit .nav-group-item, .ui-toolkit .underlined-nav-item, .ui-toolkit .badge-transparent, .ui-toolkit .text-gray-lightest, .ui-toolkit .unstyled-button, .ui-toolkit .select-custom, .wt-text-black, .wt-input, .wt-textarea, .wt-input-btn-group__input, .wt-action-group .wt-action-group__item:not(.wt-btn--transparent), .ui-toolkit .list-nav-item, .ui-toolkit .btn-transparent, .input-group .label, .input-group label, .section-alt .inline-message, #shop-status .section-help-pair strong, #shop-name .section-help-pair strong, .your-etsy-header, .page-header, .primary .section .section-header, .primary .section h2, .ui-toolkit  {
    color: #ccc !important;
}

.ui-toolkit .text-gray-lighter, .ui-toolkit .text-link-secondary, .ui-toolkit .btn-link, #shop-status .section-help-pair p, #shop-name .section-help-pair p  {
   color: #888 !important; 
}

/** Icons **/
.nav-link .nav-icon, .wt-input-btn-group .wt-input-btn-group__btn, .stars-svg .rating.lit, .stars .rating.lit, .ui-toolkit .btn-secondary:disabled .ss-icon, .ui-toolkit .btn-secondary.is-disabled .ss-icon, .ui-toolkit .etsy-icon svg {
    color: #ccc !important;
}

/** Search bar **/
.wt-input-btn-group .wt-input-btn-group__input, input {
    background: #222 !important;
}

.wt-input-btn-group.global-enhancements-search-input-btn-group, .ui-toolkit .wt-input-btn-group.global-enhancements-search-input-btn-group {
    background: #444 !important;
}

.ui-toolkit .select-custom {
        background: #666 !important;
}


/** borders **/
.ui-toolkit .border-darker, .ui-toolkit .bt-xs-1, .ui-toolkit .bt-lg-1, .wt-btn.wt-btn--outline:before, .wt-btn.wt-btn--outline:after, .ui-toolkit .alert-notice-lighter, .ui-toolkit .b-xs-1, .ui-toolkit .btn-secondary, .ui-toolkit .card, .ui-toolkit .panel, .ui-toolkit .bl-xs-1, .ui-toolkit .bb-xs-1, .ui-toolkit .panel-body-row, .ui-toolkit table, .ui-toolkit th, .ui-toolkit td, .primary .section.section-alt, .primary .section .section-header, .primary .section h2, .tabs li.active>a, .tabs li.active .tab-title, .tabs li>a.active, .tabs li .tab-title.active, .tabs li.ui-state-active>a, .tabs li.ui-state-active .tab-title, .tabs li>a.ui-state-active, .tabs li .tab-title.ui-state-active, .tabs li>a, .tabs li .tab-title, .tabs, .primary .section {
    border-color: #666 !important;
}

.ui-toolkit .btn-secondary:active, .ui-toolkit .btn-secondary.is-active, .ui-toolkit .btn-secondary.is-selected {
    border-color: #ddd !important;    
}

/** footer **/
.wt-fill-denim, .wt-fill-orange-tint  {
    fill: #222 !important; 
}

.wt-bg-denim, .wt-bg-orange-tint {
    background-color: #222 !important;
}

.wt-bg-denim-dark {
    background-color: #333 !important;
}

.wt-fill-denim-tint {
    fill: #444 !important;     
}

.wt-bg-denim-tint {
    background-color: #444 !important;
}

/** Description listing **/
.wt-content-toggle__body.wt-content-toggle__body--truncated:after {
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0), #333)
}


/** Icon Digital Download  Effects other paths...
path{
fill: #ccc !important;
}
**/

.wt-badge.wt-badge--sale-01 {
   background:#307339 !important
}

#advector-setup-view, .appstore-subapp .hero-background, .grace-management-subapp .hero-background, .grace-management-subapp .switch-plans-section, .ui-toolkit .seal-the-deal-background, .external-ads-subapp .banner-background {
    background-image: none !important;
}

}
