css:
@-moz-document domain("shkola.nso.ru") {
/* ОГНЕННЫЙ ФОН (УДАЛИТЕ СТРОЧКУ НИЖЕ, ЧТОБЫ ОТКЛЮЧИТЬ) */
body {background-image: url(https://thumbs.gfycat.com/WatchfulObeseBarasingha-size_restricted.gif)}


/* ОБЩЕЕ */
body {
	background-color: #121212;
	background-clip: border-box;
	color: #ccc;
}
.page {
	background: none;
}
.content .content_body ul.listing {
	background-color: #000D;
	border: none;
}
.main__left {
	background: #000D;
}
.wrapper > .right {
	padding: 7px 7px 0px 10px;
}
td {
	color: #666;
}
h1, h2, h3, h4, h5, h6 {
	color: #818f9d;
}
.header > div > div:first-child > span {
	color: #eee;
}
.footer {
	display: none;
}
img.transparent {
	color: #222;
	background-color: #999D;
}
.popup label.clearfix {
	color: #999;
	font-weight: bold;
}
.popup {
	color: #ccc;
	border: 2px solid #666D;
	background: #333D;
	box-shadow: 0px 2px 5px #0006;
}
.popup .title {
	border-bottom: 1px solid #666;
}
.popup > p {
	margin-bottom: 10px;
	color: #888;
	text-shadow: 0px 0px 5px #333;
}
.popup > .ico {
	top: -16px;
	right: -28px;
	transition: transform 0.5s;
}
.popup > .ico:hover, .popup > .ico:active {
	background-position: -131px -60px;
	transform: rotate(180deg);
}
textarea, input[type="text"], input[type="tel"], input[type="email"], input[type="password"], select, .input_wrap .statusclick input {
	color: #353535;
	border: 2px solid #ccc;
	background: #fffD;
}
.right .top .search .ico {
	top: 11px;
	background-position: -202px -23px;
	filter: invert() brightness(2.5) grayscale();
}
.btn.big2 .charter.ico {
	top: 50%;
	margin-top: -14px;
	filter: none;
}
.popup > p > span {
	font-weight: bold;
	color: #EEE;
}
a.load .ico, a.load .icon {
	width: 22px;
	height: 22px;
	background-position: -119px -46px;
	filter: none;
}


/* ХЭДЭР */
.header .logo {
	filter: invert() brightness(7) grayscale();
	border: none;
	background: none;
}
.header > div {
	border: none;
	background: none;
}
.header .gpa > div {
	color: #ddd;
	background: #3339;
}
.header .profile > p {
	background: #333D;
	color: #ddd;
}
.header .profile > p:hover {
	color: #fff;
	background: #666D;
}
.header .profile .more {
	border-color: #666D;
	background: #333D;
	box-shadow: 0px 2px 4px #0003;
}
.header .profile .more > div:first-child {
	border-bottom: none;
}
.header .profile .more .corner.top span {
	border-bottom-color: #333;
}
.header .profile .more .corner.top span {
	border-bottom: 9px solid #333;
}
.header .profile .more .corner.top {
	border-bottom-color: #666;
}
.header .profile .more > div:first-child img {
	border: 3px solid #EFEFEF;
}


/* ПОДДЕРЖКА СТРАНИЦ */
.profile_page .content > div {
	border: none;
	background: #000D;
}
.school .content > div {
	border: none;
	background-color: #000D;
}
.diary .content > ul li table td.homework {
	max-width: 350px;
}
.overlay-tab {
	background: #000D;
	opacity: 1;
	width: 80%;
	height: 80%;
}
.overlay, .view-overlay {
	background: #ddd7;
	filter: none;
}
.loader {
	border: 0.5em solid #f3f3f3;
	border-radius: 100%;
	border-top: 0.5em solid #666;
}
.tab-icon {
	filter: invert() grayscale();
	margin-left: 45%
}


/* ЛЕВЫЕ ПАНЕЛИ */
.wrapper > .left {
	background: none;
}
ul.menu li a .ico, ul.menu li a:active .ico {
	background: #FFF3;
}
ul.menu li a .ico::before {
	filter: grayscale() brightness(1.5);
}
ul.menu li a:hover {
	color: #fff;
	background: #333D;
}
ul.menu li a {
	color: #ccc;
	border: none;
	background: #000D;
}
ul.menu li a:active, ul.menu li a.active {
	color: #fff;
	border-top: 2px solid #666D !important;
	border-right: 2px solid #666D;
	border-bottom: 2px solid #666D;
	border-left: none;
	background: #666D;
}
ul.menu > li:first-child a {
	padding-top: 0px;
	border-top: none
}
.info_block.birthdays {
	border-color: transparent;
	background-color: #000D;
}
.info_block.meeting {
	border-color: transparent;
	background-color: #000d;
}
.info_block.activity {
	border-color: transparent;
	background-color: #000D;
}
.info_block {
	color: #eee;
}
.info_block h3 {
	color: #ccc;
	font-weight: bold;
}
.btn.rnd.green {
	background: #FFF3;
}
.info_block.meeting .time-place {
	color: #999;
}
ul.menu li > a > .hint {
	color: #fff;
	background: #FFF3;
}


/* ЗАГОЛОВКИ ОСНОВНОЙ ЧАСТИ */
.right .top {
	color: #ccc;
	border: 2px solid #333D;
	background: #333D;
}
.diary .top .left_el > span {
	border-bottom: 1px dashed #ddd;
}
.right .top .close_all {
	border-right: 1px solid #666;
}
.right .top .calendar {
	border-left: 1px solid #666D;
}
.ico, .icon {
	filter: grayscale() brightness(1.5);
}
.right .top .sel_week .container {
	color: #eee;
}
.tabs .top .tab a:hover {
	background: #444D;
	border-top: 0px solid #444D;
	color: #EEE;
}
.tabs .top .tab a {
	color: #ccc;
	border-right: 1px solid #888;
}
.right .top .print a {
	filter: brightness(1.5) grayscale();
}
.right .top .print {
	border-right: 1px solid #888;
}
.tabs .top .tab a.active, .tabs .top .tab a.active:hover {
	color: #fff;
	border: 2px solid #666D;
	background: #666D;
}


/* КОНТЕНТ ОСНОВНОЙ ЧАСТИ */
.content > ul > li > div {
	background: #000D;
	border: 2px solid #000d;
}
.content > ul.dynamic > li > div {
	color: #fff;
}
.content > ul > li > div .week_day, .content > ul li > div .title {
	color: #ccc;
}
.content > ul.dynamic > li > div:hover .week_day {
	color: #fff;
}
.content > ul > li > div {
	background: #000D;
	border: 2px solid #000d;
}
.content > ul.dynamic > li.open > div, .content > ul.dynamic > li > div:hover {
	background: #333D;
	border: 2px solid #333D;
	cursor: pointer
}
.content > ul > li.today > div .text {
	color: #fff;
}
.content > .line {
	border-bottom: 0px solid #000D;
	background: transparent;
}
.content > ul > li {
	border-top: 2px solid #000B;
}
.content > ul {
	border-bottom: 2px solid #000B;
}
.content > ul > li > div > .ico {
	top: 4px;
}
}
