@-moz-document domain("tutorialsteacher.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

header .header-main .navigation .nav-links li .main-menu
{
	text-transform: capitalize!important;
}

.CodeMirror,
.ViewTitleBarDiv,
.bg-light,
.codeLabel,
.navigation .btn,
.viewIframe,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.btn.btn-green:after,
.edugate-layout-3:after,
.lb-ad,
.pg-loaded,
.rect-ad,
.rightSidebar,
.str-adunit-banner-only.str-adunit-banner-fixed-size.w300h600,
.videoAd
{
	display: none!important;
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.list li:before {
	left: -13px;
}

/* Margins 
_____________________________________________________________________________________________________*/

#middleContent .container-fluid {
    margin: 0 19.2rem!important;
}

/* Code Editor ______________*/

.container-fluid {
    margin: 0 2.2rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.card,

pre
{
	padding: 1.2rem!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

img
{
	height: auto!important;
}


/* Widths 
_____________________________________________________________________________________________________*/

.col-xl-8,
.col-xl-9
{
	max-width: none!important;
}

selector {
	width: auto!important;
}

.col-xl-9 {
	flex: 0 0 100%;
}
	
}

@-moz-document domain("tutorialsteacher.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
/* Fonts 
_____________________________________________________________________________________________________*/

	.csharpcode code,

	pre
	{
		font-size: 2.5rem!important;
	}

	.table tbody td,

	a,
	body,
	code,
	label,
	p,
	span
	{
		font-size: 1.85rem!important;
	}

	.csharpcode code
	{
		line-height: 2.5rem!important;
	}

	p
	{
		line-height: 2.28rem!important;
	}

/* Widths 
_____________________________________________________________________________________________________*/

	.col-xl-8 {
		max-width: none!important;
	}

	.container {
		width: 98%!important;
	}

	.container-fluid {
		width: 82%!important;
	}

	.edugate-layout-3 {
		width: 78%;
	}

	.col-xl-8 {
		flex: 0 0 98.666667%;
	}

	.navbar .dropdown-menu.mega-menu {
		width: 55rem;
	}

	#navbarLeft, .menuVertical, .menuVerticalParent {
		width: 33rem;
	}
	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		
/* Fonts 
_____________________________________________________________________________________________________*/

	.csharpcode code,

	pre
	{
		font-size: 1.5rem!important;
	}

	.table tbody td,

	a,
	body,
	code,
	label,
	p,
	span
	{
		font-size: 1.25rem!important;
	}

	.csharpcode code
	{
		line-height: 2.5rem!important;
	}

	p
	{
		line-height: 2.28rem!important;
	}

/* Widths 
_____________________________________________________________________________________________________*/

	.container {
		width: 98%!important;
	}

	.container-fluid {
		width: 78%!important;
	}

	.edugate-layout-3 {
		width: 78%;
	}

	.col-xl-8 {
		flex: 0 0 98.666667%;
	}

	.navbar .dropdown-menu.mega-menu {
		width: 55rem;
	}

	#navbarLeft, .menuVertical, .menuVerticalParent {
		width: 33rem;
	}

}
}

@-moz-document domain("tutorialsteacher.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.table tbody td:first-child,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.edugate-content .description,
.noteDiv .card-body,
.table tbody td,
.tips-block,

body,
li,
p,
pre
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.author.item,
.card-header,
.edugate-content .title,
.edugate-layout-3:hover .edugate-content .title,
.menuVertical .menu-title,
.navbar .dropdown-menu.mega-menu.v-2 .sub-title,
.table thead th,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.text-dark,

figcaption,
h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.article-info .date-time,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
	z-index: 9999;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

code,
.csharpcode {
	color: #fff!important;
}

.token.selector,
.token.attr-name,
.token.string,
.token.char,
.token.builtin,
.token.inserted {
	color: #af0!important;
}

.token.atrule,
.token.attr-value,
.token.keyword {
	color: #00b3ff!important;
}

.token.property,
.token.tag,
.token.boolean,
.token.number,
.token.constant,
.token.symbol,
.token.deleted {
	color: #ff6cbe!important;
}

.csharpcode .kwrd {
	color: #00e5ff;
}

.csharpcode .html {
	color: #f00;
}

.csharpcode .rem {
	color: #8f908f;
}

.csharpcode .str {
	color: #ff1f1f;
}

.csharpcode .userclass {
	color: #3ff1b0;
}



}

@-moz-document domain("tutorialsteacher.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.CodeMirror {
    background: #0e0d0d;
    color: black;
}

/* Background 
_____________________________________________________________________________________________________*/

footer.section.footer,
header,
header .header-main {
	background-color: #060505!important;
}

.special-color-dark {
	background-color: #0a0909!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.ViewTitleBarDiv,
.codeLabel,
.topic-list a,
.bg-white,
.bg-light,
.faq,
.footer-main,
.further-reading .card-header,
.navigation .btn,
.navbar-dark .navbar-nav .active>.nav-link,
.navbar-dark .navbar-nav .nav-link.active,
.navbar-dark .navbar-nav .nav-link.show,
.navbar-dark .navbar-nav .nav-link:focus,
.navbar-dark .navbar-nav .nav-link:hover,
.navbar-dark .navbar-nav .show>.nav-link,
.noteDiv .card-header,
.table .table,
.table tbody tr:nth-child(even),
.table tbody tr:nth-child(odd),
.table thead th,

