css:
@-moz-document url-prefix("https://hegartymaths.com/") {
:root {
	--darknotblack: #2C2F33;
	--dark: #23272A;
	--greyple: #99AAB5;
	--white: #fff;
	--blurple: #7289DA
}

/* make placeholder text inside input forms lighter */
::placeholder {
	color: #ccc !important
}
:-ms-input-placeholder {
	color: #ccc !important
}
::-ms-input-placeholder {
	color: #ccc !important
}

.navbar > .container,
.navbar,
.form-control {
	background-color: var(--blurple) !important;
	color: white !important
}
#navbar-main > ul > li > a {
	color: white !important
}
#navbar-main > ul > li > a:hover {
	/* hovering turns bg to white, which makes text unreadable. I think the blurple -> white is nice though. */
	color: grey !important
}
.navbar-brand>img {
	background-color: #414e80 !important;
	padding: 6px;
	border-radius: 4px
}

.container,
#content,
.grey-bg {
	background-color: var(--darknotblack)
}
.Assessment__questions-container, .Review__meta-container {
	background-color: var(--darknotblack) !important
}
.light-grey-bg {
	background-color: var(--greyple)
}
.container, #content, .grey-bg {
	padding: 20px !important;
	border-radius: 6px
}
.White-container.padding,
.White-container.with-padding,
.white-bg {
	background-color: var(--dark);
	color: whitesmoke
}
.chartjs-render-monitor {
	color: #ccc !important
}
.Footer {
	background: rgba(0, 0, 0, 0) url("https://hegartymaths.com/img/graphics/dark-grey-pattern.png") !important
}

/* /progress.html */
.Progress__item {
	background-color: var(--greyple);
	color: #fff
}
li, .Review__message {
	color: #fff
}

.active {
	color: #000
}
li.active .hidden-sm,
li.active * {
	color: #000 !important
}

.col-xs-12 {
	padding: 12px !important
}
}
