css:
@-moz-document domain("iwara.tv") {
body, div.container, div.menu-bar {
    background-color: black;
}
.video-js .vjs-progress-holder {
     height:.8em
}
.video-js .vjs-progress-holder .vjs-play-progress,
.video-js .vjs-progress-holder .vjs-load-progress,
.video-js .vjs-progress-holder .vjs-load-progress div {
    height:.8em;
}
section#content > .container {
    background-color: #000;
}
.node.node-full.node-video .node-info, .node.node-full.node-image .node-info {
    background: #000;
    padding: 15px;
    overflow: hidden;
}
body {
    color: #0080ff;
}
.node.node-full .node-buttons {
    background: #000;
}
.node.node-teaser h3.title a {
    color: #ec0000d9;
}
footer {
    border-top: 1px solid #000;
    background-color: #000;
}
header .sub-menu {
    background-color: #000;
}
.jumbotron {
    padding: 30px;
        padding-top: 30px;
        padding-bottom: 30px;
    margin-bottom: 30px;
    color: inherit;
    background-color: #000;
}

.node-video.node-wide_teaser .video-info {
   margin-bottom: 30px;
}
.node h1.title {
    top: 0px;
}
h1, h2, h3, h4, h5, h6 {
    border-bottom: 1px solid #000;
}
.col-xs-1, .col-sm-1, .col-md-1, .col-lg-1, .col-xs-2, .col-sm-2, .col-md-2, .col-lg-2, .col-xs-3, .col-sm-3, .col-md-3, .col-lg-3, .col-xs-4, .col-sm-4, .col-md-4, .col-lg-4, .col-xs-5, .col-sm-5, .col-md-5, .col-lg-5, .col-xs-6, .col-sm-6, .col-md-6, .col-lg-6, .col-xs-7, .col-sm-7, .col-md-7, .col-lg-7, .col-xs-8, .col-sm-8, .col-md-8, .col-lg-8, .col-xs-9, .col-sm-9, .col-md-9, .col-lg-9, .col-xs-10, .col-sm-10, .col-md-10, .col-lg-10, .col-xs-11, .col-sm-11, .col-md-11, .col-lg-11, .col-xs-12, .col-sm-12, .col-md-12, .col-lg-12 {
    min-height: 20px;
}
.view-profile.view-display-id-block {
    background-color: #000;
    box-shadow: 0px 5px 7px -6px #0091ff;
}
.view-profile.view-display-id-block .views-field-picture img {
    border: 2px solid #0091ff;
}
.form-textarea-wrapper textarea {
    background-color: #000;
}
.view-profile.view-display-id-block .views-field-field-about {
    margin-top: 15px;
    background: hsl(159, 95%, 52%);
    border: 1px solid #0091ff;
}
.panel {
    background-color: #000;
}
.panel-default {
    border-color: #0091ff;
}
.panel-default > .panel-heading {
    color: #d50000;
    background-color: #000;
}
.table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {
    background-color: #000;
}
.table > thead > tr > th {
    vertical-align: bottom;
    border-bottom: 2px solid #680000;
}
.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    padding: 8px;
    line-height: 1.72222;
    vertical-align: top;
    border-top: 1px solid #0091ff;
}
.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    padding: 8px;
    line-height: 1.72222;
    background-color: #000;
    border-top: 1px solid #0091ff;
}
.well {
    background-color: #000;
    border: 1px solid #f00;
}
.panel-footer {
    padding: 10px 15px;
    background-color: #000;
    border-top: 1px solid #600;
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
}
.panel-default > .panel-heading {

    border-color: #600;
}
.pager {
    border-top: 1px solid #0091ff;
}
}
