@-moz-document domain("tiktok.com") {
:root{
   --main-color: #0b0d0e;
   --accent-color: #242529;
   --font-color: #ffffff
  }

/* Main Background */
  .jsx-4269593484, .home-container, .jsx-2546051494, .jsx-2763103118{
    background-color: var(--main-color) !important;
    background-size: auto !important;
  }

/* Header */
  .jsx-1445771017{
    background-color: var(--main-color) !important;
  }

/* Hamburger */
  .hamburger-menu .hamburger-menu-wrapper span{
    background-color: var(--font-color) !important;
  }

/* Mid Section */
  .jsx-3993048833, .jsx-2550293475{
    background-color: var(--accent-color) !important;
  }

/* Video Cards */
  .jsx-3993048833, .jsx-2550293475, .jsx-1870413825{
    background-color: var(--accent-color) !important;
  }

/* Video Cards Large */
  .jsx-2690581477{
    background-color: var(--accent-color) !important;
    
  }

/* Side Panel */
  .drawer-content.drawer-enter-done{
    background-color: var(--accent-color) !important;
  }

/* Logo */
.logo-container .logo-text, .logo-container-drawer .logo-text{
	height: 0 !important;
	width: 0 !important;
	padding-left: 100px !important;
	padding-top: 23px !important;
	background: url(https://i.imgur.com/gsF9uHR.png) no-repeat !important;
	}

.logo-container .logo-icon, .logo-container-drawer .logo-icon{
	height: 0 !important;
	width: 0 !important;
	padding-left: 28px !important;
	padding-top: 32px !important;
	background: url(https://i.imgur.com/naTn2Bx.png) no-repeat !important;
	}

.upload-wrapper img{
	height: 0 !important;
	width: 0 !important;
	padding-left: 34px !important;
	padding-top: 25px !important;
	background: url(https://i.imgur.com/Z1Jahu8.png) no-repeat !important;
	}

/* Discover Carousel */
