@-moz-document url-prefix("http://www.thegamesmachine.it/forum/"), url-prefix("https://www.thegamesmachine.it/forum/") {
/* fatto al volo perch√© non c'ho voglia */
/* Temi compatibili: "Metro" e "Metro Gray" */

/*FORUMS LIST */
body
{background: #323232;}

p
{color: #909090;}

#ad_global_below_navbar
{background: #202020;}

.popupbody, .floatcontainer
{background: #202020;}

#top_header
{background: #202020;}

#pagetitle
{background: #202020;}

.forumhead +
.childforum
.L2:first-child
.forumrow, .forumhead +
.L2 .forumrow
{background: #2e3539;
color: #909090}

.forumbit_nopost
.forumbit_nopost
.forumrow,
.forumbit_post
.forumrow
{background: #202020;
color: #909090}

div.forumrow.table
{background: #2e3539}

.forumbit_post .foruminfo .forumdata .forumtitle a, .forumbit_nopost .forumbit_nopost .forumrow .forumtitle a
