.navlinks
{background: #202020;}

.wgo_block .blocksubhead
{background: #202020;}

.registrati
{color: #909090;}

.wgo_block .section
{background: #202020;}

/* TOPICS LIST */
li, dd, span
{color: #909090;}

.toolsmenu div
{background:  #202020;}

.pagination span a
{background: #181818;
color: #d3d3d3}

.above_threadlist
{background: #202020;}

.body_wrapper
{background: #202020;}

.threadbit .sticky
{background: #2e3539;
 border: 1px solid #000;}

.threadbit .nonsticky
{background: #2e3539;
 border: 1px solid #000;}

.threadbit .threadinfo 
{background: #2e3539;}

.threadbit .alt
{background: #2e3539;}

.td
{background: #2e3539;}

.label
{color: #909090;}

.forum_info .blockbody
{background: #202020;}

/* POSTS */
dt
{color: #909090;}

a
{color: #91590f;}

.bbcode_container div.bbcode_quote
{background: #2e3539; }

span.usertitle
{color: #909090;}

.postrow
{background: #202020;
    color: #909090;}

.title
{color: #909090;}

.threadbit .threadtitle_unread
{color: #4e7292;}

.postcontent
{background: #202020;
    color: #909090;}

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody
{background: #202020;
    border-left: 1px solid #3284cb}

.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols
{background: #202020;
border-top: 1px solid #3284cb}

.postbitlegacy .postdetails, .eventbit .eventdetails
{background: #202020;}

.postbitlegacy .userinfo
{background: #202020;}

.formcontrols .blockrow
{background: #202020;}

body.forum
{background: #323232;
color: #909090}

.thread_info .blockbody
{background: #202020;}

.showsignature
{background: #202020;}

.postbitlegacy .postfoot .textcontrols a, .postbit .postfoot .textcontrols a, .eventbit .eventfoot .eventcontrols a, .postbitlegacy .postfoot .textcontrols span.mobile, .postbit .postfoot .textcontrols span.mobile
{color: #307bbb;}

/* Settings */

#usercp_nav .blockrow
{background: #202020;}

#usercp_nav .blockbody
{background: #202020;
border: 1px solid #202020;}

#usercp_nav .blocksubhead
{background: #464646;
border: 1px solid #202020;}

.blockbody.settings_form_border
{border-left: 1px solid #202020; 
border-right: 1px solid #202020;
border-bottom: 1px solid #202020;}

