css:
@-moz-document domain("rubyguides.com") {
body {
	background-color: #313131 !important;
	color: #999 !important;
}

.tve_lp_content-focused-homepage, p *, h1 *, .thrv-page-section {
    background-color: #313131 !important;
	color: #999 !important;
}
.tve_lp_content {
    background-color: #313131 !important;
	color: #999 !important;
	border: none !important;
}
.tcb-post-title a * {
	--tcb-applied-color: #999 !important;
	color: #999 !important;
}
[data-css].tcb-post-list .tcb-post-title[data-css] a {
    font-weight: var(--g-bold-weight, bold);
    --tcb-applied-color: #999 !important;
    color: #999 !important;
}
[data-css].tve-content-box-background{
	border: 4px solid rgb(0, 17, 32) !important;
}
.mashsb_above_buttons,  h1.entry-title *, .mry .awr-e p, .bSe h3, .bSe h1, .bSe p, .bSe h2, [data-css="tve-u-16ac6231a15"], [data-css="tve-u-1641eaa742b"], h6, .ccr * {
    color: #999 !important;
}
.tve_lp_content-focused-homepage, p *, h1 *, .thrv-page-section {
    color: #227abd !important;
}
blockquote {
    background-color: #674b84;
    border-left: 3px dashed #0f70d4 !important;
    border-right: 3px dashed #0f70d4 !important;
}
.bSe blockquote::before {
    color: #0f70d4;
}
.bSe blockquote * {
    color: #b0b0b0 !important;
}
code {
    background-color: #000;
}

header, footer, .btn.small, .arp {
	filter: invert(100%);
}
.tve_p_lb_background .tve_p_lb_content {
    background-color: #303030;
}
.thrv_lead_generation_container input[type="password"], 
.thrv_lead_generation_container input[type="email"], 
.thrv_lead_generation_container input[type="url"], 
.thrv_lead_generation_container input[type="text"], 
.thrv_lead_generation_container input[type="tel"], 
.thrv_lead_generation_container button, 
.thrv_lead_generation_container select, 
.thrv_lead_generation_container textarea,
.lrp input[type="text"], .lrp textarea{
    background-color: #7e7e7e;
}
#tve_22_set .thrv-leads-form-box.tve_white {
    background-color: #494949;
    border: 2px solid #000 !important;
}

.lrp {
    background: #494949;
    border: 1px solid #000;
    padding: 30px 20px;
}
.cmc.byAut {
    background: #232323;
}
.tcb-button-texts * {
	color: white !important;
}
.tve_image_caption .tve_image {
	filter: invert(0%)
}
#toc_container{
	background-color: #515151;
}
#toc_container > .bSe a, .cnt a {
		color: #86c8de
}
.bSe table thead th {
    background: #3292ce;
    border: 3px solid #16496d;
    color: #fff;
}
.bSe table td {
    background: #494949;
	border: 3px solid #16496d;
    color: #999;
    font-size: 1em;
    font-weight: 300;
    padding: 17px;
}
}
