#navigation .c,
#navigation .c1,
#navigation .c2,
#navigation .c3,
#navigation .c *,
#location .c,
#location .c1,
#location .c2,
#location .c3 {
  background-color: transparent !important;
  border-color: #111 !important;
}

#navigation {background: #606060 url("data:image/gif;base64,R0lGODlhAQAKAJEAAG5ubnJycoSEhAAAACH5BAAAAAAALAAAAAABAAoAAAIEhIAJUQA7") top left repeat-x !important;  }

#navigation li { border-color: #848484 !important; }

#navigation ul ul {
  border-color: #848484 !important;
  background-color: #fff !important;
  border-top: 3px solid #606060 !important;
}

.button {background-color: #ddd !important; }

#location, #location .c * {background-color: #555 !important}

#navigation .c1, #location .c1 {margin: 0 0px !important; border-width: 0 2px !important}

.maintable tr { background-color: #fff !important; color: #000 !important}
.maintable tr.d { background-color: #f0f1f2 !important; }
.maintable th { background-color: #777 !important; color: #fff !important;}

a:link { color: #ccc !important; }
a:visited { color: #999 !important; }
a:active { color: #fff !important; }

h2 > a:link,
h2 > a:visited,
h2 > a:active,
a.rss {color: #fff !important}

tr td a:link,
tr td a:visited,
tr td a:active,
li a:link,
#searchsuggestions a:link,
#searchsuggestions a:visited,
#searchsuggestions a:active,
#navigation ul ul li a:link,
#navigation ul ul li a:visited,
#navigation ul ul li a:active {color: #000 !important}

#navigation ul li a:link,
#navigation ul li a:visited,
#navigation ul li a:active,
th a:link,
th a:visited,
th a:active,
#location li {color: #fff !important}

#location a:link,
#location a:visited,
#location a:active {color: #ccc !important}

#downloadbox {
background-color: #444 !important;
border: 1px solid #555 !important;
}

#torrentgeneral p.left, #footer, #footer .left, #tabs {
border-color: #555 !important;
}

hr {color: #555 !important;}

#tabs a {
color: #fff !important;
background-color: #444 !important;
border: 1px solid #555 !important;
}

#tabs .current a {
color: #000 !important;
background-color: #fff !important;
}

#tabs a:hover { background-color: #555 !important; }

#pagination a {
color: #fff !important;
border-color: #555 !important;
}

#pagination a:hover {
color: #fff !important;
border-color: #555 !important;
background-color: #555 !important;
}

#pagination li.current span {
color: #fff !important;
background-color: #777 !important;
border: 1px solid #777 !important;
}

p#altdownload { display: none !important; }

