css:
@-moz-document url-prefix("https://2b2t.io/") {
    body,.highcharts-background {
        fill: /*[[background]]*/;
    	background-color:  /*[[background]]*/;
    	color: /*[[text]]*/;
	}

  	.highcharts-series-0 .highcharts-graph {
      	stroke:  /*[[que]]*/;
    }
  
	.highcharts-color-1 .highcharts-graph {
    	stroke:  /*[[total]]*/;
	}
  
    .highcharts-series-2 .highcharts-graph {
    	stroke:  /*[[prio-que]]*/;
    }

	.highcharts-axis-labels text {
    	fill: /*[[text]]*/ !important;
	}

	.highcharts-legend-item text {
    	fill: /*[[text]]*/ !important;
	}

	.highcharts-axis text {
    	fill: /*[[text]]*/ !important;
	}

	.highcharts-credits {
    	fill: /*[[text]]*/ !important;
	}
  
   .highcharts-yaxis-grid .highcharts-grid-line {
   		stroke:  /*[[chart-lines]]*/;
   }

   .highcharts-xaxis .highcharts-axis-line, .highcharts-xaxis .highcharts-tick {
   		stroke:  /*[[chart-lines]]*/;
   }
}
