css:
@-moz-document domain(kidscancode.org)
{
  body {
      background-color: #101010;
      color: #9e9e9e;
  }
  main {
      background-color: #101010;
      color: #9e9e9e;
  }
  .progress {
      background-color: #101010;
      border: thin solid #aaa;
  }
  #body #breadcrumbs {
      color: #101010;
  }
  #body a.highlight {
      color: #8451a1;
  }
  #body .nav:hover {
      background-color: #33a1ff21;
  }
  #body div.notices.note p {
      background-color: #202020;
      color: #9e9e9e;
  }
  #body div.notices.info p {
      border-top: 30px solid #8451a1;
      background-color: #202020;
      color: #9e9e9e;
  }
  h1 {
      color: #9c6fb6;
  }
  code {
      color: #000;
      background-color: #33a1ff;
      border: 1px solid #33a1ff;
  }
  .copy-to-clipboard {
      background-color: #33a1ff;
  }
  .copy-to-clipboard:hover {
      background-color: #ff8888;
  }
  code + .copy-to-clipboard {
      height: 20px;
  }
  :root {
      --MAIN-TEXT-color: #9e9e9e;
      --MENU-SECTION-ACTIVE-CATEGORY-color: #000;
      --MENU-SECTION-ACTIVE-CATEGORY-BG-color: #8451a1;
  }
  #top-bar {
      background-color: #74b559;
  }
}
