css:
@-moz-document domain("finobe.com") {
/*
 *   Conkley's Finobe Darker Theme
 *   https://finobe.com/user/109480
 *   Last Update: 25 April 2020
*/
body {
    background: #090909;
    color: #d4d4d4!important;
}

.card {
    background: #151515;
    color: #d4d4d4!important;
}

.footer>.container {
    background: #151515;
    color: #d4d4d4!important;
}

.dropdown-menu {
    background: #151515;
}

.dropdown-item, .dropdown-item:focus {
    background: #151515;
}

.form-control {
    background: #151515;
}

.form-control:focus {
    background: #26282a;
}

.game-card {
    background: #151515;
}

.finobe__message-item {
    background: #151515;
}

.nav-finobe-pills>.nav-item>.nav-link.active {
    background-color: #3f3f3f;
}

.forum-section-info, .forum-post {
    background-color: #151515;
}

.forum-post:hover {
    background-color: #191919;
}

.forum-vote-total {
    border: 1px solid #373737;
}

.finobe-dark .forum-voter > .up[data-v-73be696c], .finobe-dark .forum-voter > .down[data-v-73be696c] {
    background-color: #151515;
}

.breadcrumb {
    background: #151515;
}

.forum-new-main-area>.textarea-container>textarea.verse, .forum-new-main-area>.textarea-container>textarea.verse:active, .forum-new-main-area>.textarea-container>textarea.verse:focus {
    background: #151515;
}

.forum-new-main-area>.textarea-container>.top {
    border-bottom: 1px solid #2E3032;
}

.forum-new-main-area>.textarea-container>.bottom {
    border-top: 1px solid #2E3032;
}

.finobe-tabs>.tab, .finobe-tabs>a.tab:active, .finobe-tabs>a.tab:hover, .finobe-tabs>a.tab:link, .finobe-tabs>a.tab:visited {
    background: #151515;
}

.page-link, .page-link:focus, .page-link:hover {
    background: #151515;
    border: 1px solid #373737;
}

.page-item.disabled .page-link {
    background: #151515;
    border: 1px solid #373737;
}

.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active {
    background: #1b1d1f;
}

.nav-pills .nav-link.active, .nav-pills .show>.nav-link {
    color: #ececec!important;
    background-color: #2c75b9;
}

.custom-control-input:checked~.custom-control-label:before, .custom-control-label:before {
    background-color: #151515;
    border: 1px solid hsla(0,0%,96.5%,.125);
}

.place-list-menu-dark[data-v-4350f98c], .place-list-menu-dark[data-v-4350f98c]:before {
    background: #151515;
}

.game-card-link:hover > .game-card > .visits[data-v-5ad0ed22] {
    border-top: 1px solid #2E3032;
}

.modal-header {
    border-bottom: 1px solid #2E3032;
}

.modal-footer {
    border-top: 1px solid #2E3032;
}

.modal-body, .modal-content, .modal-header {
    background: #151515;
}

.modal-header .close {
    outline: none;
}

.dropdown-divider {
    border-top: 1px solid #2E3032;
}

.datepicker.dropdown-menu {
    background-color: #151515!important;
}

select, option {
    background-color: #151515;
}

.card, .card-footer, .card-header {
    background: #151515;
    color: #d4d4d4!important;
}

.btn-primary {
    color: #ececec!important;
    background-color: #2c75b9;
    border-color: #2c75b9;
}


.btn-danger {
    color: #ececec!important;
    background-color: #e12424;
    border-color: #e12424;
}

.btn-theme {
    background-color: #2e3032;
    border-color: #1f2123;
}

.btn-secondary {
    background-color: #2e3032;
    border-color: #1f2123;
}

.thead-default th {
    background-color: #2e3032;
}
}
