css:
@-moz-document domain("ae.utbm.fr") {
* {
    outline: none;
}
body, #page nav .dropdown-content, #forum .forum, #forum .topic, .picture {
    background: #393a39;
}
#page nav, #page #content, header, footer div, #page nav .dropdown-content, .picture {
    box-shadow: #111 0 0 15px;
}
header, #page #content {
    background: #18181a;
}
input, textarea[type="text"], [type="number"], select {
    background: #393a39;
    color: white;
}
input[type="button"], input[type="submit"], input[type="reset"], input[type="file"], #page .tool_bar .tools, #forum .button {
    color: white;
    background: #FF5722;
    box-shadow: none;
}
input[type="button"]:hover, input[type="submit"]:hover, input[type="reset"]:hover, input[type="file"]:hover, #page .tool_bar .tools a:hover, #forum .button:hover {
    background: #D84315;
}
#page nav .dropdown-content a, #page .tool_bar .tools a, #page #content {
    color: white;
}
footer {
    color: white;
}
tbody > tr:hover {
    background: initial;
}
tbody > tr:nth-child(2n) {
    background: rgba(0, 0, 0, 0.5);
}
#page #news #left_column h3, #page #news #agenda #agenda_title, #page #news #agenda #birthdays_title, #page #news #birthdays #agenda_title, #page #news #birthdays #birthdays_title {
    background: #FF5722;
    box-shadow: none;
    border-radius: 5px 5px 0 0;
    color: white;
}
#page #news #left_column h3 {
    border-radius: 5px;
}
#page #news .news_events_group {
    margin-left: 0;
    box-shadow: none;
}
#page #news .news_events_group .news_events_group_date {
    color: #354a5f;
    background: #f9fafb;
}
#page #news .news_events_group .news_events_group_items .news_event {
    color: white;
}
#page #news .news_events_group .news_events_group_items .news_event:nth-of-type(2n+1) {
    background: #393a39;
}
#page #news .news_date {
    color: white;
    opacity: .7;
}
#page #news .news_events_group .news_events_group_items .news_event .club_logo img {
    margin-bottom: 15px;
}
#page #news #agenda, #page #news #birthdays {
    background: none;
}
#page #news #agenda #agenda_content, #page #news #birthdays #agenda_content, #birthdays_content {
    box-shadow: none;
    background: #393a39;
    color: white;
}
#page #news #agenda #agenda_content ul.birthdays_year > li:nth-child(2n), #page #news #agenda #birthdays_content ul.birthdays_year > li:nth-child(2n), #page #news #birthdays #agenda_content ul.birthdays_year > li:nth-child(2n), #page #news #birthdays #birthdays_content ul.birthdays_year > li:nth-child(2n) {
    color: black;
}
.album, #basket, button {
    background: #393a39;
}
.picture {
    border: 0 none;
    padding: 0;
}
/* page du calendrier */
 .page_content {
    background: none;
}
#forum .forum *, #forum .topic * {
    vertical-align: middle;
}
#forum > div {
    margin-bottom: 20px;
}
#forum > div:last-child {
    margin-bottom: 0;
}
#forum .category {
    background: #1a78b3;
    border-radius: 5px 5px 0 0;
}
#forum .forum a, #forum .topic a {
    color: inherit;
}
#forum .topic {
    border: 0 none;
}
#forum .topic + p, #forum > p:last-child, #forum .search_bar + p {
    background: none !important;
}
#forum .message {
    background: #393a39;
    padding: 20px 0;
    margin-bottom: 20px;
}
#forum .message:nth-child(2n+1) {
    background: #263238;
}
#forum .message.unread {
    background: #393a39;
}
}
