css:
/* i really want this to be global */

ytd-app {
    --app-drawer-width: 240px;
    background: #ffffff;
    display: block;
    left: 0;
    min-height: 100%;
    position: absolute;
    right: 0;
    top: 0;
}

#search-icon-legacy.ytd-searchbox {
    cursor: pointer;
    width: 65px;
    border: 1px solid #EDEDED;
    background-color: #EDEDED;
    border-radius: 0 2px 2px 0;
    margin: 0;
    --yt-icon-button-icon-width: 24px;
    --yt-icon-button-icon-height: 24px;
    border-radius: 0 2.5rem 2.5rem 0;
}

#container.ytd-searchbox {
    position: relative;
    align-items: center;
    background-color: #ededed;
    border: 1px solid #ededed;
    border-right: none;
    border-radius: 20px 0 0 20px;
    box-shadow: inset 0 1px 2px #ededed;
    color: #ededed;
    padding: 2px 6px;
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
}

#container.ytd-searchbox input.ytd-searchbox {
    color: #888888;
}

ytd-masthead {
    --paper-input-container-input-align: baseline;
    display: block;
    width: 100%;
    background: rgb(255 255 255);
    border-radius: 0 0 20px 20px;
}

ytd-multi-page-menu-renderer {
    display: inline-block;
    background: rgb(255 255 255);
    border: 1px solid #e8e8e8;
    border-top: none;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    border-radius: 20px;
}

h2.ytd-simple-menu-header-renderer {
    font-size: 20px;
    font-weight: var(--ytd-subheadline_-_font-weight);
    line-height: 5rem;
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
}

ytd-simple-menu-header-renderer {
    background-color: var(--yt-spec-brand-background-primary);
    color: var(--yt-spec-text-primary);
    border-bottom: 1px solid rgb(0 0 0 / 0);
    min-height: 48px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
}

.reason.yt-player-error-message-renderer {
    color: #000;
    font-size: 30px;
    font-weight: 400;
    line-height: 40px;
}

yt-icon.yt-player-error-message-renderer {
    --iron-icon-fill-color: #000000;
    width: 144px;
    height: 144px;
}

ytd-rich-metadata-renderer {
    background-color: #ffffff;
    min-width: 280px;
    max-width: 50%;
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
}

#title.ytd-rich-metadata-renderer {
    color: var(--yt-spec-text-primary);
    font-size: 21px;
    font-weight: var(--ytd-subheadline_-_font-weight);
    line-height: 3rem;
}

img.yt-img-shadow {
    display: block;
    margin-left: var(--yt-img-margin-left, auto);
    margin-right: var(--yt-img-margin-right, auto);
    max-height: var(--yt-img-max-height, none);
    max-width: var(--yt-img-max-width, 100%);
    border-radius: var(--yt-img-border-radius, none);
    border-radius: 20px;
}

.html5-video-player .video-click-tracking, .html5-video-player .video-stream {
    display: block;
    width: 100%;
    height: 100%;
    position: absolute;
    border-radius: 20px;
}

ytd-thumbnail-overlay-side-panel-renderer {
    position: absolute;
    right: 0;
    top: 0;
    bottom: 0;
    background-color: rgb(255 255 255 / 0.68);
    color: #000;
    text-align: center;
    font-size: var(--ytd-subheadline_-_font-size);
    font-weight: var(--ytd-subheadline_-_font-weight);
    line-height: var(--ytd-subheadline_-_line-height);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    text-shadow: black;
}

yt-formatted-string.ytd-thumbnail-overlay-side-panel-renderer {
    color: #000;
}

paper-button.ytd-subscribe-button-renderer[subscribed] {
    background-color: #e8e8e8;
    color: #858585;
    border-radius: 15px;
}

paper-button.ytd-subscribe-button-renderer {
    background-color: #0097FE;
    border-radius: 15px;
    color: var(--yt-spec-static-brand-white);
    padding: var(--yt-button-padding);
    margin: auto var(--ytd-subscribe-button-margin, 4px);
    white-space: nowrap;
    font-size: var(--ytd-tab-system_-_font-size);
    font-weight: var(--ytd-tab-system_-_font-weight);
    letter-spacing: var(--ytd-tab-system_-_letter-spacing);
    text-transform: var(--ytd-tab-system_-_text-transform);
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
}

paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {
    background-color: #0097FE;
}

.toggle-button.paper-toggle-button {
    position: absolute;
    top: -3px;
    left: 0;
    right: auto;
    height: 20px;
    width: 20px;
    border-radius: 50%;
    box-shadow: 0 1px 5px 0 rgb(255 255 255 / 0);
    transition: -webkit-transform linear .08s, background-color linear .08s;
    transition: transform linear .08s, background-color linear .08s;
    will-change: transform;
    background-color: #e8e8e8;
}

yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string:visited {
    color: #000000;
}

a.yt-simple-endpoint.yt-formatted-string:visited {
    color: #0285F0;
}

a.yt-formatted-string {
    color: #0285F0;
}

ytd-menu-popup-renderer {
    display: inline-grid;
    grid-template-columns: min-content;
    background-color: var(--yt-spec-brand-background-primary);
    border-radius: 15px;
    box-shadow: grey;
}

iron-overlay-backdrop {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: #00000052;
    opacity: 0;
    transition: opacity 0.2s;
    pointer-events: none;
}

paper-dialog {
    display: block;
    margin: 24px 40px;
    background: var(--paper-dialog-background-color, var(--primary-background-color));
    color: var(--paper-dialog-color, var(--primary-text-color));
    font-family: var(--paper-font-body1_-_font-family);
    -webkit-font-smoothing: var(--paper-font-body1_-_-webkit-font-smoothing);
    font-size: var(--paper-font-body1_-_font-size);
    font-weight: var(--paper-font-body1_-_font-weight);
    line-height: var(--paper-font-body1_-_line-height);
    border-radius: 15px;
}

#title.yt-share-panel-title-v15-renderer {
    color: var(--yt-spec-text-primary);
    display: var(--layout-horizontal_-_display);
    -ms-flex-direction: var(--layout-horizontal_-_-ms-flex-direction);
    -webkit-flex-direction: var(--layout-horizontal_-_-webkit-flex-direction);
    flex-direction: var(--layout-horizontal_-_flex-direction);
    -ms-flex-pack: var(--layout-start-justified_-_-ms-flex-pack);
    -webkit-justify-content: var(--layout-start-justified_-_-webkit-justify-content);
    justify-content: var(--layout-start-justified_-_justify-content);
    font-size: 25px;
    font-weight: var(--ytd-subheadline_-_font-weight);
    line-height: 30px;
}

#copy-button.yt-copy-link-renderer {
    padding: 0 var(--ytd-margin-2x);
    color: #0285F0;
}

#bar.yt-copy-link-renderer {
    justify-content: space-between;
    border: 1px solid #e8e8e8;
    background-color: #e8e8e8;
    border-radius: 20px;
    display: var(--layout_-_display);
    -ms-flex-align: var(--layout-center_-_-ms-flex-align);
    -webkit-align-items: var(--layout-center_-_-webkit-align-items);
    align-items: var(--layout-center_-_align-items);
}

yt-start-at-renderer.yt-third-party-network-section-renderer {
    margin-top: var(--ytd-margin-6x);
    padding-top: var(--ytd-margin-6x);
    border-top: 1px solid rgb(0 0 0 / 0);
}

.scroll-button.yt-third-party-share-target-section-renderer {
    position: absolute;
    width: 40px;
    height: 40px;
    top: 16px;
    border-radius: 50%;
    background-color: #e8e8e8;
    color: #818181;
    overflow: hidden;
}

#checkbox.paper-checkbox {
    position: relative;
    box-sizing: border-box;
    height: 100%;
    border: solid 2px;
    border-color: var(--paper-checkbox-unchecked-color, var(--primary-text-color));
    border-radius: 15px;
    pointer-events: none;
    -webkit-transition: background-color 140ms, border-color 140ms;
    transition: background-color 140ms, border-color 140ms;
    -webkit-transition-duration: var(--paper-checkbox-animation-duration, 140ms);
    transition-duration: var(--paper-checkbox-animation-duration, 140ms);
}

#embed-panel.yt-sharing-embed-renderer {
    background: var(--yt-dialog-background);
    min-width: var(--embed-panel-default-width);
    height: var(--embed-panel-default-height);
    display: var(--layout-vertical_-_display);
    -ms-flex-direction: var(--layout-vertical_-_-ms-flex-direction);
    -webkit-flex-direction: var(--layout-vertical_-_-webkit-flex-direction);
    flex-direction: var(--layout-vertical_-_flex-direction);
    border-radius: 20px;
}

#title-bar.yt-sharing-embed-renderer {
    border-bottom: 1px solid #ffffff;
    padding: var(--ytd-margin-4x);
    display: var(--layout-horizontal_-_display);
    -ms-flex-direction: var(--layout-horizontal_-_-ms-flex-direction);
    -webkit-flex-direction: var(--layout-horizontal_-_-webkit-flex-direction);
    flex-direction: var(--layout-horizontal_-_flex-direction);
}

.ytp-cued-thumbnail-overlay-image {
    background-size: cover;
    -moz-background-size: cover;
    -webkit-background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    width: 100%;
    height: 100%;
    position: absolute;
    border-radius: 20px;
}

yt-button-renderer[is-paper-button] {
    border-radius: 20px;
    margin: var(--yt-button-margin, 0 0.29em);
}

yt-button-renderer.style-dark-on-black[is-paper-button] {
    background-color: #0097fe;
    border: none;
    color: hsl(0deg 0% 100%);
}

yt-playability-error-supported-renderers {
    display: block;
    background: white;
    -ms-flex: var(--layout-flex_-_-ms-flex);
    -webkit-flex: var(--layout-flex_-_-webkit-flex);
    flex: var(--layout-flex_-_flex);
    -webkit-flex-basis: var(--layout-flex_-_-webkit-flex-basis);
    flex-basis: var(--layout-flex_-_flex-basis);
    display: var(--layout-vertical_-_display, block);
    -ms-flex-direction: var(--layout-vertical_-_-ms-flex-direction);
    -webkit-flex-direction: var(--layout-vertical_-_-webkit-flex-direction);
    flex-direction: var(--layout-vertical_-_flex-direction);
}

ytd-video-secondary-info-renderer {
    display: block;
    border-bottom: 1px solid rgb(255 255 255 / 10);
    margin-bottom: 24px;
    padding-bottom: 16px;
}

.title.ytd-video-primary-info-renderer {
    display: block;
    max-height: 4.8rem;
    overflow: hidden;
    font-size: 1.8rem;
    font-weight: 400;
    line-height: 3.4rem;
    color: var(--ytd-video-primary-info-renderer-title-color, var(--yt-spec-text-primary));
    font-family: var(--ytd-video-primary-info-renderer-title-font-family, inherit);
    font-size: 25px;
    font-variant: var(--ytd-video-primary-info-renderer-title-font-variant, inherit);
    transform: var(--ytd-video-primary-info-renderer-title-transform, none);
    text-shadow: var(--ytd-video-primary-info-renderer-title-text-shadow, none);
}

.count-text.ytd-comments-header-renderer {
    vertical-align: middle;
    font-size: 30px;
    font-weight: var(--ytd-subheadline_-_font-weight);
    line-height: 4rem;
}

ytd-video-primary-info-renderer {
    --yt-button-icon-size: 36px;
    display: block;
    border-bottom: 1px solid rgb(255 255 255 / 10);
    padding: 20px 0 8px 0;
}

#placeholder-area.ytd-comment-simplebox-renderer {
    cursor: text;
    padding-bottom: 4px;
    border-bottom: 1px solid rgb(255 255 255 / 10);
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
}

#simplebox-placeholder.ytd-comment-simplebox-renderer {
    color: #757575;
    outline: none;
    -webkit-flex-basis: 100%;
    flex-basis: 100%;
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    font-size: var(--ytd-user-comment_-_font-size);
    font-weight: var(--ytd-user-comment_-_font-weight);
    line-height: 4rem;
}

ytd-button-renderer.style-primary[disabled][is-paper-button] {
    background-color: #ffffff;
    color: #adadad;
}

ytd-button-renderer.style-primary[is-paper-button] {
    background-color: #0097fe;
    color: var(--yt-spec-text-primary-inverse);
    border-radius: 20px;
}
