css:
@-moz-document url-prefix("https://www.acfun.cn/search"), url-prefix("https://www.acfun.cn/v/list"), url-prefix("https://www.acfun.cn/rank/list/"), regexp("^https?://(www\\.acfun\\.cn)."), regexp("^https?://(www\\.acfun\\.cn)/+v+.*"), regexp("^https?://(www\\.acfun\\.cn)/+a+.*"), regexp("https://www.acfun.cn/u/.*"), regexp("https://live.acfun.cn/live/\\d*") {
  /* Commonlibs*/
  @-webkit-keyframes fadeInDown {
      0% {
          opacity: 0;
          -webkit-transform: translate3d(0, -100%, 0);
          transform: translate3d(0, -100%, 0)
      }
      to {
          opacity: 1;
          -webkit-transform: translateZ(0);
          transform: translateZ(0)
      }
  }
  @keyframes fadeInDown {
      0% {
          -webkit-transform: translate3d(0, -20%, 0);
          transform: translate3d(0, -20%, 0);
          opacity: 0;
      }
      100% {
          -webkit-transform: none;
          transform: none;
          opacity: 1;
      }
  }
  
  @keyframes breathe {
      from {
          box-shadow: inset 0 0 2px rgba(255, 255, 255, 0);
      }
      to {
          box-shadow: inset 0 0 10px rgba(0, 0, 0, .2);
      }
  }
  @keyframes fade-in {
      0% {
          opacity: 0;
      }
      /*初始状态 透明度为0*/
      40% {
          opacity: 0;
      }
      /*过渡状态 透明度为0*/
      100% {
          opacity: 1;
      }
      /*结束状态 透明度为1*/
  }
  @-webkit-keyframes fade-in {
      /*针对webkit内核*/
      0% {
          opacity: 0;
      }
      40% {
          opacity: 0;
      }
      100% {
          opacity: 1;
      }
  }
  @keyframes fadelogIn {
      0% {
          -webkit-transform: translate3d(0, 100%, 0);
          transform: translate3d(0, 100%, 0);
      }
      100% {
          -webkit-transform: none;
          transform: none;
      }
  }
  @-webkit-keyframes fadelogIn {
      0% {
          -webkit-transform: translate3d(0, 100%, 0);
      }
      100% {
          -webkit-transform: none;
      }
  }
  @keyframes fadeleftIn {
      0% {
          -webkit-transform: translate3d(100%, 0, 0);
          transform: translate3d(100%, 0, 0);
      }
      100% {
          -webkit-transform: none;
          transform: none;
      }
  }
  @-webkit-keyframes fadeleftIn {
      0% {
          -webkit-transform: translate3d(100%, 0, 0);
      }
      100% {
          -webkit-transform: none;
      }
  }
  @-webkit-keyframes popIn {
      0% {
          -webkit-transform: scale3d(0, 0, 0);
          transform: scale3d(0.5, 0.5, 0.5);
          opacity: 0;
      }
      50% {
          -webkit-animation-timing-function: cubic-bezier(0.47, 0, 0.745, 0.715);
          animation-timing-function: cubic-bezier(0.47, 0, 0.745, 0.715);
      }
      100% {
          -webkit-transform: scale3d(1, 1, 1);
          transform: scale3d(1, 1, 1);
          -webkit-animation-timing-function: cubic-bezier(0.25, 0.46, 0.45, 0.94);
          animation-timing-function: cubic-bezier(0.25, 0.46, 0.45, 0.94);
          opacity: 1;
      }
  }
  @keyframes popIn {
      0% {
          -webkit-transform: scale3d(0, 0, 0);
          transform: scale3d(0.5, 0.5, 0.5);
          opacity: 0;
      }
      50% {
          -webkit-animation-timing-function: cubic-bezier(0.47, 0, 0.745, 0.715);
          animation-timing-function: cubic-bezier(0.47, 0, 0.745, 0.715);
      }
      100% {
          -webkit-transform: scale3d(1, 1, 1);
          transform: scale3d(1, 1, 1);
          -webkit-animation-timing-function: cubic-bezier(0.25, 0.46, 0.45, 0.94);
          animation-timing-function: cubic-bezier(0.25, 0.46, 0.45, 0.94);
          opacity: 1;
      }
  }
  @keyframes breath-border {
      0% {
          box-shadow: 0 0 0 0 rgba(255, 255, 255, .5);
      }
      100% {
          box-shadow: 0 0 0 20px rgba(255, 255, 255, 0);
      }
  }
  @keyframes lightOverAndOver {
      from {
          box-shadow: 0px 0px 4px #949494;
      }
      to {
          box-shadow: 0px 0px 16px #949494;
      }
  }
  
  .bubbles_btn {
      --hue: 190;
      --btn-bg-color: hsl(var(--hue), 100%, 50%);
      --btn-bg-color-darker: hsl(var(--hue), 100%, 45%);
      position: relative;
      padding: 0.75rem 1.5rem;
      margin: 1rem;
      font-size: 1rem;
      font-family: Lato, sans-serif;
      line-height: 1.5;
      color: white;
      text-decoration: none;
      background-color: var(--btn-bg-color);
      border: 1px solid var(--btn-bg-color);
      border-radius: 4px;
      box-shadow: 0 0.1px 0.7px rgba(233, 30, 99, 0.141), 0 0.1px 1.7px rgba(233, 30, 99, 0.202), 0 0.3px 3.1px rgba(233, 30, 99, 0.25), 0 0.4px 5.6px rgba(233, 30, 99, 0.298), 0 0.8px 10.4px rgba(233, 30, 99, 0.359), 0 2px 25px rgba(233, 30, 99, 0.5);
      outline: transparent;
      overflow: hidden;
      cursor: pointer;
      user-select: none;
      white-space: nowrap;
      transition: 0.25s;
  }
  .bubbles_btn-pink {
      --hue: 330;
  }
  .bubbles_btn-bubbles {
      overflow: visible;
      transition: transform ease-in 0.1s, background-color ease-in 0.1s, box-shadow ease-in 0.25s;
  }
  .bubbles_btn-bubbles::before {
      position: absolute;
      content: "";
      left: -2em;
      right: -2em;
      top: -2em;
      bottom: -2em;
      transition: ease-in-out 0.5s;
      background-repeat: no-repeat;
      background-image: radial-gradient(circle, var(--btn-bg-color) 20%, transparent 20%), radial-gradient(circle, var(--btn-bg-color) 20%, transparent 20%), radial-gradient(circle, transparent 20%, var(--btn-bg-color) 20%, transparent 30%), radial-gradient(circle, var(--btn-bg-color) 20%, transparent 20%), radial-gradient(circle, var(--btn-bg-color) 20%, transparent 20%), radial-gradient(circle, var(--btn-bg-color) 20%, transparent 20%), radial-gradient(circle, transparent 20%, var(--btn-bg-color) 20%, transparent 30%), radial-gradient(circle, transparent 20%, var(--btn-bg-color) 20%, transparent 30%), radial-gradient(circle, var(--btn-bg-color) 20%, transparent 20%), radial-gradient(circle, transparent 20%, var(--btn-bg-color) 20%, transparent 30%), radial-gradient(circle, var(--btn-bg-color) 20%, transparent 20%), radial-gradient(circle, transparent 20%, var(--btn-bg-color) 20%, transparent 30%), radial-gradient(circle, transparent 20%, var(--btn-bg-color) 20%, transparent 30%), radial-gradient(circle, var(--btn-bg-color) 20%, transparent 20%), radial-gradient(circle, transparent 20%, var(--btn-bg-color) 20%, transparent 30%), radial-gradient(circle, transparent 20%, var(--btn-bg-color) 20%, transparent 30%);
      background-size: 10% 19%, 15% 19%, 11% 15%, 11% 14%, 18% 17%, 13% 17%, 19% 13%, 12% 11%, 16% 10%, 17% 20%, 10% 15%, 20% 12%, 10% 12%, 17% 20%, 20% 13%, 13% 11%;
      background-position: 18% 40%, 20% 31%, 30% 30%, 40% 30%, 50% 30%, 57% 30%, 65% 30%, 80% 32%, 15% 60%, 83% 60%, 18% 70%, 25% 70%, 41% 70%, 50% 70%, 64% 70%, 80% 71%;
      animation: bubbles ease-in-out 0.75s forwards;
  }
  .bubbles_btn-bubbles:active {
      transform: scale(0.95);
      background: var(--btn-bg-color-darker);
  }
  .bubbles_btn-bubbles:active::before {
      animation: none;
      background-size: 0;
  }
  
  @keyframes bubbles {
      0% {
          background-position: 18% 40%, 20% 31%, 30% 30%, 40% 30%, 50% 30%, 57% 30%, 65% 30%, 80% 32%, 15% 60%, 83% 60%, 18% 70%, 25% 70%, 41% 70%, 50% 70%, 64% 70%, 80% 71%;
      }
      50% {
          background-position: 10% 44%, 0% 20%, 15% 5%, 30% 0%, 42% 0%, 62% -2%, 75% 0%, 95% -2%, 0% 80%, 95% 55%, 7% 100%, 24% 100%, 41% 100%, 55% 95%, 68% 96%, 95% 100%;
      }
      100% {
          background-position: 5% 44%, -5% 20%, 7% 5%, 23% 0%, 37% 0, 58% -2%, 80% 0%, 100% -2%, -5% 80%, 100% 55%, 2% 100%, 23% 100%, 42% 100%, 60% 95%, 70% 96%, 100% 100%;
          background-size: 0% 0%;
      }
  }
  :root {
      --acred: #fd4c5d;
  }
  
  /*顶栏*/
  #header-guide > li.guide-item.guide-msg:hover > div,
  #header-guide > li.guide-item.guide-feed:hover > div,
  #header-guide > li.guide-item.guide-cretive:hover > div,
  #header-guide > li.guide-item.guide-upload:hover > div,
  #header-guide > li.download-app:hover,
  #header-guide > li.guide-item.guide-user:hover > div,
  #header-guide > li.guide-item.guide-history:hover > div {
      animation: fade-in;
      animation-duration: 0.5s;
      -webkit-animation: fade-in .4s;
  }
  /* #footer .footer-con .footer-avatar-ac{
      animation: lightOverAndOver;
      animation-duration: 0.5s;
      -webkit-animation: lightOverAndOver 2s ease-in-out infinite alternate;;
  } */
  div.second-container,
  div.header-sub-nav {
      animation: fadeInDown;
      animation-duration: 0.3s;
      -webkit-animation: fadeInDown .3s;
  }
  .top-nav .first-container .first-item .second-container ul {
      background-color: #fff;
      box-shadow: 0 3px 3px rgba(0, 0, 0, .3);
  }
  .top-nav .first-container .first-item .second-container {
      background-color: #fff0;
      box-shadow: none;
      padding-bottom: 5px;
  }
  .second-link {
      transition: all .2s ease;
  }
  .second-link:hover {
      border-bottom: 2px solid var(--acred);
      transition: all .1s ease;
  }
  /*顶栏二级导航，取消最后一个元素的外边距*/
  #pagelet_navigation > div > ul > li > div > ul > li:last-child {
      margin: 0px;
  }
  /*创作者中心入口*/
  [data-c-w-header] .header-guide .guide-item.guide-cretive .guide-item-con {
      width: 90px;
  }
  [data-c-w-header] .header-guide .guide-item li:hover {
      background-color: #f4f4f4 !important;
  }
  [data-c-w-header] .header-guide .guide-item-con {
      text-align: center !important;
  }
  [data-c-w-header] .header-guide .guide-item ul {
      padding: 0 0;
  }
  /*历史*/
  #header-guide > li.guide-item.guide-history > div > ul {
      padding: 0px 12px;
  }
  /*消息*/
  [data-c-w-header] .header-guide .guide-msg .guide-item-con {
      width: 110px;
  }
  /* 投稿下拉菜单 */
  [data-c-w-header] .header-guide .guide-item.guide-upload .guide-item-con {
      width: 100px;
  }
  [data-c-w-header] .header-guide .guide-item-con {
      font-size: 15px;
  }
  #header-guide > li.guide-item.guide-upload > div > ul > li {
      padding: 10px 0;
  }
  #header-guide > li.guide-item.guide-upload > div > ul > li:hover {
      background-color: #f4f4f4 !important;
  }
  /*评论框边界*/
  .edui-container {
      transition: border .2s ease-in-out;
  }
  .edui-container:hover {
      transition: border .2s ease-in-out;
      border: 1px solid #ff5454;
  }
  /* tpm测速 */
  #footer .footer-con .footer-avatar-ac {
      right: -40px;
  }
  /* 评论区表情放大 */
  .ubb-emotion {
      max-height: 70px !important;
  }
  .ac-comment-list .area-comment-more {
      transition: transform .2s ease;
  }
  .ac-comment-list .area-comment-more:hover {
      transform: scale(1, 1.1);
      transition: transform .2s ease;
  }
  
  /*Logo*/
  #header .nav-entrance-wrap a:hover,
  .logo:hover {
      animation: breath-border;
      animation-duration: 0.5s;
      -webkit-animation: breath-border .4s ease-in-out;
  }
  /*AcFunHelper*/
  #acfun-helper-div {
      font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif;
      box-shadow: 0px 5px 10px #949494;
      border-radius: 5px 0px 0px 5px;
      right: -10px !important;
      transition: all .2s ease;
  }
  #acfun-helper-div:hover {
      transition: all .2s ease;
      right: 0px !important;
  }
  
  }
  
  @-moz-document url("https://www.acfun.cn/") {
  /*Index*/
  .global-slider {
      box-shadow: 0px 5px 10px #949494;
  }
  
  .recommend-video {
      box-shadow: 0px 5px 10px #949494;
  }
  #top_slider_area .slider-right-images,
  .home-main-content #pagelet_top_area {
      height: 260px;
  }
  .recommend-video:hover {
      transform: scale(1.01, 1.01);
      transition: transform .2s cubic-bezier(0, 1.06, 0.61, 0.99);
  }
  [data-c-w-header] .search-box .form input:hover {
      border-color: #fd4c5d;
  }
  #back-top {
      transition: box-shadow .2s ease;
  }
  #back-top:hover {
      transition: box-shadow .2s ease;
      box-shadow: 0px 0px 5px 0px #777777;
  }
  div.video-list,
  .banana-rank .rank-left .banana-list {
      transition: transform .2s cubic-bezier(0, 1, 0.6, 1);
  }
  div.video-list:hover,
  .banana-rank .rank-left .banana-list:hover {
      transform: scale(1.02, 1.02);
      transition: transform .2s cubic-bezier(0, 1, 0.6, 1);
  }
  /*所有封面的mask*/
  .danmaku-mask {
      background: rgb(0 0 0 / 40%);
  }
  /*番剧-本季安利*/
  .bangumi-list .area-right .season-rec {
      box-shadow: 0 0px 5px rgba(0, 0, 0, .1);
      transition: box-shadow .2s ease;
  }
  .bangumi-list .area-right .season-rec:hover {
      box-shadow: 0 0px 6px rgba(0, 0, 0, .3);
      transition: box-shadow .2s ease;
  }
  .bangumi-list .area-right {
      height: 585px;
  }
  div.ac-comment-list > div.ac-comment-root-list > div > div {
      box-shadow: 0 0px 5px rgb(0 0 0 / 20%);
  }
  /*去掉按钮的边线*/
  .normal-module .module-left .module-left-header .left-area .header-change {
      border: 0px;
  }
  /*分区-更多*/
  .normal-module .module-left .module-left-header .right-area .header-right-more,
  .bangumi-list .area-left .area-header .header-right-more,
  .more{
      transition: all .2s ease;
  }
  .normal-module .module-left .module-left-header .right-area .header-right-more:hover,
  .bangumi-list .area-left .area-header .header-right-more:hover,
  .more:hover{
      letter-spacing: 3px;
      transition: all .2s ease;
  }
  /*榜单类型*/
  .banana-rank .rank-left .left-header .right-area .rank-item {
      transition: all .2s ease;
      border-radius: 0px;
  }
  .banana-rank .rank-left .left-header .right-area .rank-item:hover {
      border-bottom: 2px solid #fd4c5d;
      border-radius: 0px;
      transition: all .2s ease;
  }
  
  }
  
  @-moz-document url-prefix("https://www.acfun.cn/v/list") {
  /*PartIndex*/
  .slider-small {
      height: 260px;
  }
  .slider-wrap{
      box-shadow: 0px 5px 10px #949494;
  }
  .channel-slider .slider-right .slider-small .slider-small-item a {
      box-shadow: 0px 5px 10px #949494;
      transition: transform .2s cubic-bezier(0, 1, 0.6, 1);
  }
  .channel-slider .slider-right .slider-small .slider-small-item a:hover {
      transform: scale(1.02, 1.02);
      transition: transform .2s cubic-bezier(0, 1, 0.6, 1);
  }
  .recommend-video {
      transition: transform .2s cubic-bezier(0, 1, 0.6, 1);
  }
  .recommend-video:hover {
      transform: scale(1.03, 1.03);
      transition: transform .2s cubic-bezier(0, 1, 0.6, 1);
  }
  .to-top {
      transition: all .2s ease;
  }
  .to-top:hover {
      transition: all .2s ease;
      box-shadow: 0px 0px 5px 1px #b4b4b4;
  }
  .channel-monkey-recommend .recommend-video .video-item .video-image:hover .mask {
