  }
  .channel-banana-rank .banana-list .video-item .video-image {
      transition: all .5s ease;
  }
  .channel-banana-rank .banana-list .video-item:hover .video-image {
      box-shadow: 0px 5px 10px #949494;
      transition: all .2s ease;
  }
  /*更多正能量UP主推荐*/
  .channel-rank .fame-hall .fame-hall-main li {
      padding-top: 20px !important;
  }
  #app > div > main > div > div.ac-channel-main > div.channel-main > div.main-bottom > div.channel-rank-list > div > div.fame-hall.rank-item > div.fame-hall-main > ul > li > p > a {
      font-size: larger !important;
  }
  .channel-rank .fame-hall .fame-hall-main li .uper-title {
      margin-top: 70px !important;
  }
  
  /*创作者中心入口*/
  #header .nav .user-creative-dropdown {
      width: 90px;
  }
  #header .nav .user-creative-dropdown a:hover {
