      transition: all .2s ease;
  }
  .channel-rank .fame-hall .fame-hall-main li .user-info .btn-follow {
      border: none !important;
  }
  }
  
  @-moz-document regexp("^https?://(www\\.acfun\\.cn)/+v+.*") {
  /** VideoPage */
  .float-text {
      visibility: hidden
  }
  /*弹幕列表展开按钮*/
  #danmaku .danmaku-fold {
      border: 0px solid !important;
      border-radius: 0 0 0 0 !important;
  }
  /*播放器下方分割线*/
  .player-box,
  .ac-comment-list .area-comment-top hr {
