  }
  /*大家都在看*/
  .recmd .block-box {
      transition: all .4s cubic-bezier(0, .5, 0, 1);
      border-radius: 3px;
      padding-right: 5px;
  }
  .recmd .block-box:hover {
      box-shadow: 0px 5px 10px #949494;
      transition: all .4s cubic-bezier(0, .5, 0, 1);
  }
  .recmd .block-video .block-title h3 a:hover {
      color: #333;
  }
