css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("madfanboy.com") {
@media (min-width: 1200px) {

    .body-layout-Fluid .wrap,
    .body-layout-Fluid #inner-wrap {
        width: 100%;
        min-width: 1200px;
    }
}

/* width */
::-webkit-scrollbar {
  width: 12px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #000;
}

/* Handle */
::-webkit-scrollbar-thumb {
  background: rgb(58, 58, 58);
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #5a5a5a;
}



html{
    background-color: rgb(27, 27, 27);
}


h1, h2, h3, h4, h5, h6 {
    color: #d8d8d8;
}

#quick-link ul li a {
    color: #d8d8d8;
}

#content {
    background-color: #2c2c2c;
}

.article.teaser h2.title a {
    color: #d8d8d8;
}

.block-views.no-bg-color li, .view-article-block li, .view-blog-block li, .vab-base.attachment li, .vbb-base.attachment li, .pab-list article, .pab-grid .meta .inner, .page-block-block-grid .meta, .pab-category header, .pab-category li, .pab-category.grid.alter .section, .page-block-block-category h3.title {
    border-color: #5a5a5a;
}

.bb-chart-arc path {
    stroke: rgb(27, 27, 27);
}

.status-message, .content-tab ul.secondary, .content-tab ul.primary a.is-active, .content-tab ul.primary a:hover {
    background-color: #2c2c2c;
}

.block-views .no-link-color h4 a, .vab-popular h4 a, .pab-list h2.title a, .block-views-blockpage-block-block-grid h4 a, .pab-category h4 a {
    color: #ccc;
}

ul.links, ul.links a, .breadcrumb, .breadcrumb a, #share a, #comment-form fieldset {
    color: #d8d8d8;
}

.showcase-gallery.alter h4.title, .showcase-gallery.alter h4.title a, .showcase-headline h4.title a, .slider-newsflash h4.title a {
    color: #eeeeee;
}




.meta, .meta a {
    color: #999;
}

.node.basic.teaser, .node.basic.full ul.links, .article.teaser, .article .meta, .article ul.links, .blog ul.links li, .blog .meta ul.links li > a, .blog .meta.alter ul.links li > a, .blog .meta .share, #comment header, #comment-form fieldset {
    border-color: rgb(70, 70, 70);
}

.block > .inner, .slider-merging .slick-prev, .slider-merging .slick-next, .tab-merging ul.tab_nav li.active, .alter-merging ul.tab_nav {
    background-color: #2c2c2c;
}

input.sonic_button[type=submit] {
    background-image: url(https://i.imgur.com/ZECpvyD.png);
}

input.sonic_button[type=submit]:hover {
    background-image: url(https://i.imgur.com/HOV30HU.png?1);
}

#header {
    background-color: rgb(27, 27, 27);
}

.article.full, .blog .content, #comment-post, #comment-box, .node-type-blog #comment .comments, .node-type-blog #comment .comment-form {
    background-color: #2c2c2c;
}

.message-box textarea {
    font-size: 1.2em;
}

.pollbar4 {
    background-color: #991610;
    border-color: #991610;
}

.pollbar2 {
    background-color: #991610;
    border-color: #991610;
}

.reported {
    background-color: rgb(37, 37, 37) !important;
    border-left: 5px solid #f00 !important;
}

p.post-notice {
    background-color: rgba(35,35,35,0.9)!important;
}

div.rules {
    background-color: #333;
    color: #e5352c;
}

fieldset.polls dl.voted {
    color: #ffffff;
}

p.bbc_mod_head {
    border: 1px solid #da0000;
}

.bbc_mod_text {
    border-color: #da0000;
}

body {
    background-color: rgb(0, 0, 0);
    background-image: none;
    color: #bbb;
}

div.offtopic {
    background-color: rgb(70, 70, 70) !important;
    border-left: 2px solid rgb(224, 15, 21) !important;
}

div.responsive-hide{
    color: rgb(204, 204, 204)
}

a {
    color: #d8d8d8;
}

hr {
    border-color: #000;
    border-top-color: #000;
}

h2 {
    color: #d8d8d8;
}

select {
    color: #000;
}

#inner-wrap {
    background: rgb(42, 42, 42);
    border: solid 2px #000;
}

ul#nav-breadcrumbs {
    background-color: rgb(53, 53, 53);
    border: 1px solid #000;
}


p.author,
p.author a {
    color: #e2e2e2;
    font-size: 12px;
}

.content_block_header_block ul.topiclist li.header {
    background-color: rgb(27, 27, 27);
    background-image: none;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

.wrap {
    background-color: rgb(83, 83, 83);
}

li.row {
    border-bottom-color: rgb(0, 0, 0);
    background-color: rgb(58, 58, 58);
    border-top: solid 1px rgb(0, 0, 0);
    border-right: solid 1px rgb(0, 0, 0);
    border-left: solid 1px rgb(0, 0, 0);
}

li.header {
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

li.row strong {
    color: #f00;
}

table {
    /* border-collapse: collapse; */
    border-spacing: 0;
}

table.table1 tbody tr {
    border-color: #0a0a0a;
}
table.table1 tbody tr {
    border: 1px solid transparent;
}

dl.faq dt {
    color: #f00;
}

.tpotm-head-text {
    font-size: 2.2em;
    text-align: center;
    color: #f00;
}

.tpotm-flex-cell {
    background: 0 0;
    border-width: 2px;
    padding: 3px;
    border-style: solid;
    border-color: #6d6d6d;
    width: 200px;
    height: 66px;
    vertical-align: middle;
    border-radius: 7px;
    box-shadow: 1px 2px 3px #000000d9;
    display: flex;
    align-items: start;
}

.tpotm-div-4 {
    position: relative;
    top: 10px;
    left: 10px;
    text-align: center;
    width: 100px;
    height: 100px;
    border-radius: 100%;
    background-color: #2c2c2c;
}

.tpotm-div-3 {
    margin: 2px auto;
    text-align: center;
    width: 120px;
    height: 120px;
    border-radius: 100%;
    background-color: #f00;
}

.tpotm-div-1 {
    padding: 5px 10px;
    margin-bottom: 4px;
    background-repeat: no-repeat;
    background-position: 100% 0;
    border-radius: 7px;
    position: relative;
    margin: 10px;
    box-shadow: 1px 2px 3px grey;
    font-size: 14px;
    color: #b6b6b6;
}

table.table1 tbody td {
    border-top-color: #585858;
    padding: 10px;
    border-top: 5px solid rgba(66, 66, 66, .42);
}

table.table1 td {
    color: #cacaca;
}

dl.details dt {
    color: #dadadadd;
}

dl.details dd {
    color: #f3f3f3;
}

.search .postprofile {
    width: 15%;
    background-color: #252525;
    border-right: 1px solid #000000;
    margin: 0;
    margin-bottom: -14px;
    padding-top: 4px;
    padding-bottom: 24px;
}

.navbar_footer {
    background-color: rgb(6, 6, 6);
    background-image: none;
}

.nav-b-a {
    background-color: rgb(17, 17, 17);
    background-image: none;
}

.navigation a {
    color: #fff;
    background: rgb(120, 120, 120);
}

.dropdown .dropdown-contents {
    background: rgb(87, 87, 87);
}

.particles_container {
    background: rgb(0, 0, 0);
    background: linear-gradient(90deg, rgb(27, 27, 27) 0%, rgb(46, 46, 46) 100%);
}

.signature {
    border-top-color: #000000;
}

.search-box .inputbox,
.search-box .inputbox:hover,
.search-box .inputbox:focus {
    background-color: #222;
}

dd label {
    color: #c6c6c6;
}

.cp-mini {
    background-color: rgb(120, 120, 120);
}

.cp-main .pm {
    background-color: #2c2c2c;
    padding: 10px;
}

.dropdown li, .dropdown li li{
        border-color: #000;
}

.dropdown .dropdown-contents {
    border-color: #000000;
    box-shadow: 1px 3px 5px rgba(0, 0, 0, 0.38);
}

.dropdown-extended .header,
.dropdown-extended .footer {
    background-color: #3e3e3e ;
    border-color: #000000 !important;
    color: #f9f9f9 !important;
}

.dropdown-extended a.mark_read {
    background-color: #474747;
    box-shadow: 1px 3px 5px rgba(0, 0, 0, .76);
}

.icon.icon-black,
a:hover .icon.icon-black {
    color: #f00;
}

a:hover,
a:active {
    color: #f00;
    text-decoration: underline;
}

.inputbox {
    color: rgb(204, 204, 206);
    background-color: rgb(70, 70, 70);
    border-color: #000;
}

.panel {
    background-color: rgb(53, 53, 53);
    border: 1px solid #000;
    color: #fff;
}

.panel .inputbox {
    background-color: rgb(59, 59, 59);
    border-color: #000;
}

.panel-container .panel li.row {
    border-bottom-color: #000000;
}

.pmlist li.pm_replied_colour,
.pm_replied_colour {
    border: 1px solid #000000;
}

.bg1,
.bg2 {
    background-color: rgb(37, 37, 37);
}

.forabg,
.forumbg,
.viewtopic_wrapper {
    background-color: rgb(10, 10, 10);
    background-image: none;
}

.content {
    color: #ccccce;
}

.postbody {
    color: #ccccce;
}

.post {
    border: 1px solid #000;
}

.post:target .content {
    color: #ccc;
}

.postprofile {
    color: #c5c5c5;
}

.postprofile strong {
    color: #fff;
}

.specialbutton,
.specialbutton .icon,
.specialbutton:hover,
.specialbutton:hover .icon {
    color: #f00 !important;
    background-color: rgb(94, 94, 94);
}

.postlink {
    color: #fff !important;
}

.button {
    background-color: #303030;
    color: #ff0505;
}

a.button1,
input.button1,
input.button3,
a.button2,
input.button2 {
    color: #fff;
    background-color: #292929;
    border-color: #000;
}

.panel .button {
    background-color: #555;
    color: #e4e4e4;
}

.action-bar .button {
    background-color: #5a5a5a;
}

.pagination-s li a,
.pagination-s li span {
    border-radius: 2px;
    padding: 1px 5px;
    border-color: #000000;
    background-color: rgb(70, 70, 70);
}

.pagination li a {
    color: #fff;
    border-color: #000;
    background: none;
}

.pagination-s li span {
    border-color: #000;
    background-color: #333333;
}

.dropdown-extended ul li a, .notification_list dt>a, .dropdown-extended .footer>a{
    border-color: #000 !important;
}

.dropdown-extended ul li {
    border-top-color: #565656;
}

.dropdown-extended .header {
    background-color: #3e3e3e;
}

.dropdown-extended .header {
    text-shadow: 0px 0px 0px #f000;
}

.notification_list p.notification-time {
    color: #FFFF;
}

.mini-panel {
    background-color: #3f3f3f;
    -moz-border-radius: 7px;
    -webkit-border-radius: 7px;
    border-radius: 7px;
    margin-bottom: 5px;
    padding: 5px 10px;
}

.mini-panel h3 {
    font-family: Verdana, Helvetica, Arial, sans-serif;
    color: #ff0000;
    margin: 5px 0 0;
}

dl.mini dt {
    color: #ffffff;
}

.cp-mini {
    background-color: rgb(74, 74, 74);
}

.tabs .tab > a {
    background: #101010;
}

.tabs .activetab > a,
.tabs .activetab > a:hover {
    background-color: #bfbfbf;
}

.pagination li a {
    color: #fff;
    border-color: #000;
}

.pagination li.active span {
    color: #fff;
    border-color: #f00;
    background-color: #545454;
}

.fancy_panel {
    background-color: #232323;
}

.message-box textarea {
    color: #fff;
}

.quotecontent div {
    background-color: rgb(70, 70, 70) !important;
}

blockquote {
    background-color: rgb(70, 70, 70);
    border: 1px solid #000;
}

blockquote blockquote {
    background-color: rgb(70, 70, 70);
    border: 1px solid #000;
}

blockquote blockquote blockquote {
    background-color: rgb(70, 70, 70);
    border: 1px solid #000;
}

blockquote blockquote blockquote blockquote {
    background-color: rgb(70, 70, 70);
    border: 1px solid #000;
}

