@-moz-document regexp("https://(www|books|encrypted|accounts|translate).google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2})?/(?!maps|doodles|advanced|history|preferences|intl|services|work|wallet|fonts|travel/hotels|travel/packages).*") {
/**
Modded a bit from "Google - Clean Dark"
https://userstyles.org/styles/144028/google-clean-dark

Currently excludes
Google Hotels
Google Packages
**/

:root {
    /* Dark Surfaces and Modals */
    --surface-bg-color: #121212;
    --surface-bg-onHover-color: #1e1e1e;
    --surface-bg-dip-color: #000000;
    /* Elevation 1 */
    --modal-1-bg-color: #1b1b1b;
    --modal-1-bg-onHover-color: #202020;
    --modal-1-bg-dip-color: #121212;
    /* Elevation 2 */
    --modal-2-bg-color: #262626;
    --modal-2-bg-onHover-color: #2d2d2d;
    --modal-2-bg-dip-color: #222222;
    /* Elevation 3 */
    --modal-3-bg-color: #2f2f2f;
    --modal-3-bg-onHover-color: #333333;
    --modal-3-bg-dip-color: #272727;
    /* Elevation 4 */
    --modal-4-bg-color: #353535;
    --modal-4-bg-onHover-color: #4f4f4f;
    --modal-4-bg-dip-color: #2e2e2e;
    /* Specific elements*/
    --dark-input-color: #000000;
    --dark-input-onHover-color: rgb(225, 225, 225, 0.05);
    /* Accent colors */
    --white-bold: #cccccc;
    --white-average: #c2c2c2;
    --white-caption: #979da3;
    --white-disabled: #70757a;
    --soft-purple: #aea0d1;
    --soft-blue: #88a7c7;
    --soft-red: #bc4f4f;
    --soft-green: #3d9970;
    --grey-svg-color: #5f6368;
    --blue-svg-color: #4285f4;
    --white-button-color: #b8c2d5;
    /* Border and shadows */
    --silver-border-color: rgba(223, 225, 229, 0.1);
    --silver-border: 1px solid var(--silver-border-color);
    --rounded-pill-radius: 20px;
    --modal-card-radius: 8px;
    --box-shadow-tiny: 0 1px 1px rgba(0, 0, 0, 0.3),
        0 1px 3px 1px rgba(0, 0, 0, 0.15);
    --box-shadow-average: 0 1px 2px rgba(0, 0, 0, 0.3),
        0 1px 3px 1px rgba(0, 0, 0, 0.15);
    --box-shadow-broad: 0 1px 3px rgba(0, 0, 0, 0.3),
        0 4px 8px 3px rgba(0, 0, 0, 0.15);
    --white-to-main-filter-blue-tint: invert(100%) invert(0%) sepia(10%)
        saturate(100%) hue-rotate(180deg) brightness(105%) contrast(75%);
    --white-to-modal-1-filter: invert(100%) invert(8%) sepia(0%) saturate(0%)
        hue-rotate(163deg) brightness(109%) contrast(95%);
    --white-to-modal-1-filter-plus-colors: invert(100%) invert(8%) sepia(0%)
        saturate(150%) hue-rotate(555deg) brightness(109%) contrast(95%);
}

