css:
@-moz-document url-prefix("http://www.ts.kg/") {
body {
    background-image: none !important;
    background-color: #000000 !important;
}

footer,
.oops-news.oops-area,
.hidden-xs.oops-branding.oops-area,
.oops-under.oops-area,
.player-12 .player-4,
#episode-voice-h,
#player-overlay,
.pre-footer.hidden-xs,
.oops-category.oops-area,
.hidden-xs.oops-branding.oops-frame,
.scroll-to-top,
.well.hidden-xs ,
.nav-pills,
.player-4,
iframe,
.oops-under,
.oops-area,
.player-overlay,
table > tbody > tr > td > span:nth-child(1) {
    display: none !important;
}

#player5 {
    margin: 0 auto;
}

#player {
    width: auto;
}
.player-8 {
    float: inherit !important;
}
.app-show-header-title,
.app-show-header-title-original {
    display: inline;
}

.app-show-header-title-original {
    padding-left: 25px;
}

#player,
.player {
    height: calc(100vh - 200px);
    width: 100%;
}

.player-12 {
    width: auto;
}

.player-8 {
    width: 100%;
}

.panel-body {
    display: flex;
    justify-content: center;
}


.app-show-header {
    border: none;
    border-radius: 0;
    margin: 0;
    padding: 0;
}

.app-show-header-title,
.app-show-header-title-original,
.app-show-header-years {
    display: inline;
}

.app-show-header-title-original {
    padding-left: 25px;
}

.main-container {
    background-color: rgba(0, 0, 0, 0.35);
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
    margin-top: 0;
    width: 1245px;
}
.navbar-header img {
    opacity: 0.5;
}
.show-title{
    color: #0e7ddc;
}

.well,
.app-news-date,
.category_nav {
    background-color: rgba(0, 0, 0, 0.3);
    border: 1px solid #222;
}

/*  navbar */
.navbar-default {
    background-color: #222;
    border-color: #080808;
}

.navbar-default .navbar-nav > li > a:hover {
    color: #fff !important;
}

.navbar-default .navbar-nav > .open > a:hover {
    color: #000 !important;
}

.navbar-default .navbar-nav > li > a:focus {
    color: #777;
    outline: auto;
}

.navbar-nav > li > .dropdown-menu {
    margin-top: 0;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
    background-color: #333;
}

.dropdown-menu > li > a:hover {
    background-color: #666;
}

.dropdown-menu > li > a {
    color: #337ab7;
    margin-left: 10px;
    list-style-type: circle;
}

.dropdown-menu .divider {
    background-color: #555;
}

.dropdown-header {
    border-top: 1px solid #555;
    margin-top: 6px;
    margin-left: 10px;
}
/*  end navbar */
/*  player */
#player,
.player {
    height: calc(100vh - 200px);
    width: 100%;
}

.player-12 {
    width: auto;
}

.player-8 {
    width: 100%;
}
/*  end player */
.news-link,
.app-show-header-years a {
    color: #777;
}
.nav .open > a,
.nav .open > a:focus,
.nav .open > a:hover,
.nav > li > a:focus,
.nav > li > a:hover,
.dropdown-menu {
    background-color: #333 !important;
}
input, select, .flagstrap button, .category_nav .btn-group label, #app-show-form-submit-lucky, #app-show-form-submit {
    background-color: #333 !important;
    color: #fff !important;
    border-color:#333 !important;
}

a:hover {
    color: #1b65a7;
}

.bg-success,
.bg-warning,
.panel {
    background-color: #dff0d800;
}

.panel-default,
.panel-default > .panel-heading {
    border-color: #333;
}

.panel-default > .panel-heading {
    color: #777;
    background-color: #2222229c;
}

.panel-body {
    display: flex;
    justify-content: center;
}

.app-show-tags li > a {
    border: 1px solid #555;
}

.show-shots-galery > div > a > img {
    display: inline-block;
    max-width: 100%;
    height: auto;
    padding: 4px;
    line-height: 1.42857143;
    background-color: #333;
    border: 1px solid #333;
    border-radius: 4px;
    -webkit-transition: all .2s ease-in-out;
    -o-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
}


::selection {
    color: #9a9a9a;
    background: #000;
}

.table > tbody > tr > td {
    border-top: 1px solid #555;
}

a.text-muted {
    color: #337ab7;
}

.app-show-episode-title {
    color: #777;
}

body {
    color: #777;
}
}
