css:
@-moz-document domain(rikatillsammans.se){ 
/* Header     */
header.mainheader {
    background-color: #23272A;
    padding-top: 35px;
}

footer.rtfooter {
    background-color: #23272A;
    padding: 30px 0 85px 0;
}

main {
    background-color: #23272A !important;
    color: #99AAB5 !important;
    color: inherit;
    font-family: "Roboto", sans-serif;
}

body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #999;
    background-color: #23272A;
}

p {
    font-family: "Roboto", sans-serif;
    font-size: 16px;
    line-height: 25px;
    color: #99AAB5;
}

.alert-newsletter {
    background: #f8f8f8!important;
    border: 1px solid darkgray!important;
    display: none;
}


h3.index-subheading {
    color: #99AAB5;
    font-size: 125%;
    font-family: 'EamesCenturyModern-BookItalic';
}

/* Artikel bodytext     */
article p {
    font-family: 'arial', sans-serif;
    font-size: 14px;
    line-height: 23px;
    color: #99AAB5;
}

.tabs .ui-tabs-nav .ui-state-active {
    background-color: #23272A;
    padding: 10px 10px 10px 30px;
}

.tabs .ui-tabs-nav li {
    background-image: none;
    background-color: #23272A;
    padding: 10px 10px 10px 30px;
    width: 33.3333%;
    float: left;
}

main .widget .textwidget .newsletter-signup, .widget_mc4wp_form_widget {
    display: none;
}

article .btn-danger {
    
    box-shadow: 0px 0px 0px #999;
}

.tabs .ui-tabs-nav li a {
    font-family: "brandon_textbold", Arial, sans-serif;
    font-size: 14px;
    line-height: 20px;
    color: #fff;
    outline: none;
    outline-offset: 0;
}

main ul {
    list-style: none;
    margin-bottom: 15px!important;
    padding-left: 10px;
    background: #23272A;
}

/* Sidorutan     */
main .widget .textwidget .about-me {
    background-color: #23272A;
    box-shadow: 0px 0px 2px #033cff;
}

.pagination {
    background-color: #23272A;
    font-family: "brandon_textbold", Arial, sans-serif;
    font-size: 12px;
    line-height: 23px;
    color: #999;
    padding: 0 20px;
    box-shadow: 0px 0px 0px #999;
    border-radius: 35px;
    text-transform: uppercase;
    margin-bottom: 20px;
}

form {
    display: block;
    margin-top: 0em;
    background-color: #23272A;
}

div[style] {
   background: #23272A !important;
}

.dwqa-questions-list .dwqa-question-item {
    border-bottom: 0px solid #ddd;
    border-left: 0px solid #ddd;
    border-right: 0px solid #ddd;
    padding: 15px 180px 15px 70px;
}

.dwqa-question-filter {
   background: #23272A !important;
}

.dwqa-question-filter a {
    background: #23272A;
    border: 0px solid #ddd !important;
}

aside div.dwqa_widget_categories_header {
    padding: 25px 15px 5px 15px;
    background: #23272A;
    margin-bottom: 0px;
}

main .widget h3 {
    font-family: "brandon_textbold", Arial, sans-serif;
    font-size: 22px;
    line-height: 28px;
    color: #999;
}

aside div.dwqa_widget_categories {
    
    background: #23272A;
    
}

main .widget .textwidget .rss-signup, .widget_mc4wp_form_widget .rss-signup {
    background-color: #23272A;
}

.dwqa-pagination a, .dwqa-pagination span {
    border: 0px solid #ddd;
}

main aside div.askdwqa-widget, main aside div.search-widget {
    background-color: #23272A;
    border: 0px #fff solid;
    padding: 21px 35px 12px 100px;
    box-shadow: 0 0 0px #dfdfdf;
}

aside .theme_widget_article {
    background-color: #23272A;
}

.alert-warning {
    color: #8a6d3b;
    background-color: #23272A;
    border-color: #23272A;
}

.podcasts-info-widget .podcasts-info-widget-content {
    background: #23272A;
    color: #999;
    text-align: center;
}

.rekommend {
    background-color: #23272A;
    padding: 30px;
    box-shadow: 0px 0px 0px #999;
}

main h2 {
    color: #999;
}

main dl {
    background: #23272A;
    border: 0px solid white;
    box-shadow: 0px 0px 0px #999;
}

main blockquote p, main blockquote ul li, main blockquote ol li, main blockquote cite {
    color: #999;
}

ol.schema-how-to-steps {
    background: #23272A;
    border: 0px solid white;
    box-shadow: 0px 0px 0px #999;
}

main h3 {
    color: #999;
}

.wp-block-yoast-faq-block .schema-faq-section, .qa-block li {
    border: 0px solid #ddd;
    background: #23272A;
   
}

.commentlist li .comment .comment-content .section-wrap {
    background-color: #23272A;
    box-shadow: 0px 0px 0px #999;
}

.commentlist li .comment .comment-content .section-wrap p {
    font-family: "Roboto", sans-serif;
    color: #999;
}

.commentlist .bypostauthor > .comment .comment-content .section-wrap {
    background-color: #23272A;
}

div.mce-toolbar-grp {
    border-bottom: 0px solid #ddd;
    background: #23272A;
    padding: 0;
    position: relative;
}
}
