css:
@-moz-document domain("web.telegram.org") {
/*      Author @Sventra                  */
/*      Based on @RisingFire redesign    */
body {
    background: #1B2735;
    font-family: 'Roboto', sans-serif;
}
.tg_head_split {
    max-width: none;
    height: 100px;
    background: #456A94;
}
.tg_head_logo_wrap {
    width: 20%;
    margin-left: 11.4%;
}
.tg_head_main_wrap {
    width: 53.6%;
}
/* BOTH LEFT SIDE BAR AND CHAT HISTORY */
.im_page_wrap {
    background: none;
    border: none;
    margin-top: -50px;
    margin-bottom: 50px;
    -webkit-box-shadow: none;
    max-width: 1500px;
}
/* LEFT SIDE BAR COLUMN*/
.im_dialogs_col_wrap {
    border-radius: 26px;
    width: 26%;
    background: #212E3E;
    border: 2px solid #1B2735;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 15px 0 rgba(0, 0, 0, 0.19);
    margin-bottom: 20px;
    margin-left: 10px;
}
.im_dialogs_col {
    border-radius: 26px;
}
div.im_dialogs_wrap.nano.has-scrollbar.active-scrollbar {
    height: 100.8%;
    border-radius: 24px;
}

input[type=search] {
    border-radius: 16px;
    background: #1B2735;
    border: 2px solid #1B2735
}
i.icon-search-clear {
    color: #fff;
}
.im_dialog_peer {
    color: #fff;
}

/* Pinned message bar */
.im_history_pinned_wrap {
    border: none;
    background: #456A94;
    padding-bottom: 14px;
    max-height: 50px;
}
.im_message_reply_thumbed .im_message_reply_border, .im_message_reply_thumbed .im_message_reply_wrap {
    height: 32px;
}
.im_message_body {
    background: #283A50;
    padding: 8px;
    border-radius: 8px;
    /*width: fit-content;*/
    color: #fff;
}
.im_message_video,
a.im_message_photo_thumb {
    border-radius: 8px
}
.im_message_reply_border {
    background: #000;
}

.im_message_reply_body {
    color: #fff;
}
a.im_dialog .im_short_message_text {
    color: #aaa;
}
.im_dialog_badge_muted {
    background: #24384e;
}
.im_record_bg {
    background: none;
}
div.im_panel_peer_photo,
a.im_panel_peer_photo {
    display: none;
}
.composer_emoji_panel {
    display: none;
}
/* Active chat */
.im_dialogs_scrollable_wrap .active a.im_dialog {
    border-radius: 26px;
    background: #456A94;
}
.im_dialogs_scrollable_wrap a.im_dialog:hover,
.im_dialogs_scrollable_wrap a.im_dialog_selected {
    background: #32455b;
    border-radius: 26px;
}
.im_send_form {
    background: #212E3E;
    border-radius: 16px;
    color: #fff;
    padding: 10px;
}
.im_message_reply_author {
    color: #eee;
}
.im_message_wrap {
    min-width: 800px;
}
.im_message_views {
    padding: 8px;
}
div.im_message_unread_split {
    background: #32455b;
}


.im_history_select_active .im_message_outer_wrap:hover {
    background: #32455b;
}

.im_message_selected .im_message_outer_wrap {
    background: #435c79;
}
div.im_history_scrollable_wrap.nano-content {
    padding-top: 50px;
}

/* BOTTOM PANEL (WRITE A MESSAGE) */
.im_send_panel_wrap {
    padding-top: 32px;
}

/* EDIT PANEL */
.im_edit_panel_border {
    border-bottom: 1px solid #435c79;
}





/* DROPDOWN */
.tg_head_logo_dropdown .dropdown-menu {
    border-bottom-left-radius: 32px;
    border-bottom-right-radius: 32px;
    border-left-color: transparent;
    border-right-color: transparent;
    background-color: #32455b;
    right:auto;
    left:-1px
}
.tg_head_logo_dropdown .dropdown-menu > li > a {
    color: #fff;
}
.tg_head_logo_dropdown .dropdown-menu > li > a:hover {
    background: #32455b;
}
.dropdown.open .tg_head_btn, .tg_head_btn:hover {
    border-top-right-radius: 32px;
    border-top-left-radius: 32px;
    border-bottom-right-radius: 32px;
    border-bottom-left-radius: 32px;
    background: #32455b;
}
.dropdown.open .tg_head_btn, .tg_head_btn:active {
    border-top-right-radius: 32px;
    border-top-left-radius: 32px;
    border-bottom-left-radius: 0px;
    background: #32455b;
}

/* 3 dots menu */
.tg_head_peer_dropdown .dropdown-menu {
    border-top-left-radius: 32px;
    border-bottom-left-radius: 32px;
    border-bottom-right-radius: 32px;
    border-left-color: transparent;
    border-right-color: transparent;
    background-color: #32455b;
    right:-1px;
}
.tg_head_peer_dropdown .dropdown-menu > li > a {
    color: #fff;
}
.dropdown-menu .divider {
    background-color: #485a6f;
}

/* search */
.im_dialogs_scrollable_wrap a.im_dialog_searchpeer {
    background: #485a6f8f;
}
}
