css:
@-moz-document url-prefix("https://www.dndbeyond.com") {
.ct-ability-summary, .ct-box-background--tall, .ct-sidebar__inner, .ct-skills-box, .ct-saving-throws-box, .ct-armor-class-box, .ct-combat__statuses, .ct-health-summary, .ct-senses-box, .ct-proficiency-groups-box, .ct-inspiration, .ct-speed-box, .ct-proficiency-bonus-box, .ct-initiative-box {
	filter: invert(100%) sepia(0%) saturate(100%) hue-rotate(210deg) brightness(75%) contrast(100%);
}

#content {
	color: #DCDCDC;
}

.ct-ability-summary__secondary, .ct-skills__col--skill, .ct-signed-number__number, .ct-proficiency-groups__group-item, .ct-senses__callout-value, .ct-saving-throws-box__info, .ct-distance-number__number, .ct-armor-class-box__value, .ct-health-summary__hp-number, .ct-health-summary__hp-empty, .ct-health-summary__label, .ct-combat-attack__meta-item, .ct-combat-attack__range-value-long, .ct-defenses-summary__default, .ct-conditions-summary__default, .ct-inventory-item__meta-item, .ct-note-components__component--plain, .page-title, .ddb-characters-listing-header-primary-title {
		color: #5566FF!important;
}

.ct-ability-summary__label, .ct-senses__callout-label, .ct-saving-throws-summary__ability-name, .ct-proficiency-bonus-box__label, .ct-item-name--rarity-common, .ct-skills__col--stat, .ct-proficiency-groups__group-label, .ct-tab-list__nav-item--active, .ct-distance-number__label, .ct-speed-box__label, .ct-speed-box__heading, .ct-speed-box__label, .ct-proficiency-bonus-box__heading, .ct-combat__summary-label, .ct-inspiration__label, .ct-armor-class-box__label, .ct-armor-class-box__label, .ct-health-summary__hp-item-label, .ct-skills__heading, .ct-combat-attack__range-label, .ct-signed-number__sign, .ct-weight-number__label {
		color: #006644!important;
}

.site .character-button-block-small{
		font-size: 10px!important;
}

input[type="text"], input[type="number"], input[type="search"], input[type="password"], input[type="email"], textarea, select{
		border: 1px solid #00FFFF;
		font-family: arial;
}

.ddb-characters-listing-actions__search-box{
		border: 1px solid #00FFFF!important;
		font-family: arial!important;
}


.ct-combat-attack__icon-img, .ct-damage-type-icon__img, .ct-inventory-item__location-slot-icon {
	filter: invert(100%) sepia(100%) saturate(1000%) hue-rotate(300deg) brightness(100%) contrast(100%)!important;
}

.ct-tab-options__header-heading, .ct-slot-manager__slot{
	background-color: #696969;
	color: #000000!important;
}

ct-tab-options__header-heading

.site .character-button, .site .character-button-block, .site .character-button-block-large, .site .character-button-block-oversized, .site .character-button-block-small, .site .character-button-large, .site .character-button-medium, .site .character-button-modal, .site .character-button-modal-cancel, .site .character-button-outline, .site .character-button-oversized, .site .character-button-small{
	font-family: arial;
}

body{
	background-image: none!important;
	background-color: #151515!important;
	color: #DCDCDC;
}
}
