css:
@-moz-document domain(rocketlanguages.com)
{
html, body, body.radical, .container, .main-background, #footer, .panel-body, .panel-header div,
footer[class^='Footer_footer'] {
    background-color: #222 !important;
    color: #ddd !important;
}
p, h1,
div[class^='PageUI_heading'] h1,
div[class^='_sidebar_notesBar__'],
.login-heading, .help-links,
.panel .panel-heading, .card,
.panel-body, .lesson-type-group div,
.ng-scope, .testing .panel.rateable-test .panel-heading h2,
.testing .panel.rateable-test .panel-heading h2,
div[class^='Phrasebox_phrase'],
h3[class^='TopBar_navHeaderAlt'],
div[class^='LessonComponent_imageComponentCaption'],
div[class^='BadgeNotification_notificationContainer__'] h1,
div[class^='BadgeNotification_notificationContainer__'] h2
{
    color: #ddd;
}
a[class*='CourseButton_container'],
div[class^='TopBar_container'],
div[class^='LessonItem_container'],
div[class^='LessonComponentHighlightCard']
{
    background-color: #333 !important;
}
div[class^='LeftHandSideNav_sticky'] {
    background-color: #122b40 !important;
}
h3[class^='TopBar_navHeader_'] {
    color: #2c90b5 !important;
}
.help-links, .checkbox, .page-description, .sidebar .progress-value {
    color: #bbb;
}
.content, .site-sidebar {
    background-color: #333 !important;
}
.text-center {
    color: #bbb;
}
.text-center h1, .site-content h1 {
    color: #2189af;
}
.panel-body {
	background-color: #333 !important;
}
.dashboard-content > div {
    background-color: #444;
}
.panel {
    background-color: #444;
}
.site-content-wrap {
    background-color: #444;
}
[videogular].videogular-container, videogular.videogular-container, .phrasebox-content {
	background-color: #444 !important;
}
.dashboard .module .panel-body .lesson-description,
h3[class^='LessonComponent_lead__'] {
	color: #bbb;
}
.site-header div {
    	background-color: transparent !important;
}
.phrasebox-line .character-name, .transcript-line .character-name {
	background-color: #555;
    color: #fff !important;
}
.rocket-record, rocket-record {
	background-color: transparent;
}
.lesson-content h1, .lesson-content h2, .lesson-content h3 {
    color: #ccc;
}
.lesson-content .component.audio .audio-title {
	color: #ddd;
    background-color: #444;
    border-bottom: 1px solid #444;
}
.extra .audio-title {
    background-color: #555 !important;
}
.form-control {
	color: #fff;
}
.form-control {
	background-color: #444;
}
.text-muted {
	color: #fff !important;
}
.lesson-content a, .back-to-top {
	color: #214077;
}
#email, #password{
    color: #333;
}
button[class*='PhraseStringNotationButtons_notationButton__'] {
    color: #fff !important;
    background-color: #444 !important;
}
button[class*='PhraseStringNotationButtons_notationButton__']:hover {
    color: #fff !important;
    background-color: #555 !important;
}
[class*='TooltipPositioner_container'] {
    color: #fff !important;
    background-color: #444 !important;
    box-shadow:0px 2px 4px #555;
}
.popover {
    background-color: #666 !important;
}
.dashboard .module .panel-body .group-item, .dashboard .module .panel-body .lesson {
	background-color: #444;
}
.dashboard .module .panel-body .group-item.selected, .dashboard .module .panel-body .lesson.selected {
	background-color: #3374aa;
}
.dashboard .module .panel-body .lesson-group-header {
	background-color: #555;
}
.flashcard-container .flashcard .flashcard-card.flippable .face {
    background-color: #666;
}
.flashcard-container .flashcard .card.flippable .face, .flashcard-container .flashcard .card.flippable.flipped .back {
    background-color: #555;
}
.lesson-types-list .lesson-type:hover a {
    color: #444;
}
.modal-dialog .modal-content .rank-upgrade-modal-container,
div[class^='BadgeNotification_notificationContainer__'] {
    background: radial-gradient(ellipse 200% 100% at bottom,#333 50%,#444 0)
}
[playit] .transcript-content.playing .transcript-line.active-character rocket-record {
    background-color: #1b618b; /* [playit] .transcript-content.playing .transcript-line.active-character rocket-record  #a5d1eb*/
} 
.sidebar div[sliding-panel] {
    background-color: #555;
}
.progress-widget>.progress-title a, .text-dark-gray, #tools-content h5, .sidebar .sidebar-title {
    color: #ddd !important;
}
.lesson-content .transcript .panel {
    background-color: #333;
}
.lesson-types-list .lesson-type .lesson-type-toggle, .lesson-types-list .lesson-type .menu-item {
    color: #ddd;
}
.lesson-types-list .lesson-type .lesson-type-toggle:hover, .lesson-types-list .lesson-type .menu-item:hover {
    color: #aaa;
}
.lesson-types-list .lesson-type:hover a {
    color: #aaa;
}
.lesson-content .component.markdown.markdown-tip {
    background-color: #3c6dc8;
}
.lesson-content .component.markdown.markdown-culture {
    background-color: #3f5e42;
}
.lesson-content .component.markdown.markdown-culture h1, .lesson-content .component.markdown.markdown-culture h2, .lesson-content .component.markdown.markdown-culture h3, .markdown-culture .component-content p {
    color: #ddd;
}
.lesson-content .component.markdown table thead {
    background-color: #444;
}
.lesson-content .component.markdown table tr:nth-of-type(2n) {
    background-color: #3a3a3a;
}
.lesson-content .component.audio.lesson vg-controls.iac>.controls-container .subtitle-container p,
div[class^='AudioPlayerUI_transcript__'] {
    color: #2189af !important;
}
.course-lesson-menus .product-list .panel.active .panel-heading, .course-lesson-menus .product-list .panel .panel-heading {
    background-color: #2189af;
}
[videogular] vg-controls.audio vg-scrub-bar-current-time, [videogular] vg-controls.iac vg-scrub-bar-current-time, videogular vg-controls.audio vg-scrub-bar-current-time, videogular vg-controls.iac vg-scrub-bar-current-time {
    background-color: #2781a1!important
}
div[class^='SeekBar_inner__'] {
    background-color: #6dbcd9 !important;
}
.lesson-content .component.audio.lesson vg-controls.iac>.controls-container vg-time-display,
[videogular] vg-controls.audio vg-time-display, [videogular] vg-controls.iac vg-time-display, videogular vg-controls.audio vg-time-display, videogular vg-controls.iac vg-time-display,
div[class^='SeekBar_container__'] {
    background-color: #666 !important;
    color: #ddd !important
}
.lesson-content .component.audio.lesson vg-controls.iac>.controls-container .play-skip .skip-button-container .btn,
.rocket-record .iconButton.pause, .rocket-record .iconButton.play, rocket-record .iconButton.pause, rocket-record .iconButton.play, button[class^="btn btn-primary full-width ng-binding"],
.rocket-record-level-selector .rr-levels .rr-level-button,
button[class*='Button_secondary'],
a[class*='Button_secondary']
{
    background-color: #309cc3 !important;
}

.lesson-content .component.audio.lesson vg-controls.iac>.controls-container .download .btn {
    background-color: #2781a1 !important;
}
.lesson-content .component.audio.lesson vg-controls.iac>.controls-container .download .btn.hover,
.lesson-content .component.audio.lesson vg-controls.iac>.controls-container .download .btn:active,
.lesson-content .component.audio.lesson vg-controls.iac>.controls-container .download .btn:hover {
    background-color: #206b86 !important;
}
.lesson-content .component.audio.lesson vg-controls.iac>.controls-container .play-skip .skip-button-container .btn.hover, .lesson-content .component.audio.lesson vg-controls.iac>.controls-container .play-skip .skip-button-container .btn:active, .lesson-content .component.audio.lesson vg-controls.iac>.controls-container .play-skip .skip-button-container .btn:hover,
.rocket-record .iconButton.pause:hover, .rocket-record .iconButton.play:hover, rocket-record .iconButton.pause:hover,
.rocket-record .iconButton.play:hover,
button[class^="btn btn-primary full-width ng-binding"]:hover,
.rocket-record-level-selector .rr-levels .rr-level-button.active,
.rocket-record-level-selector .rr-levels .rr-level-button:hover:not(.active) {
    background-color: #2781a1 !important;
}
.lesson-types-list .lesson-type:active .level-modules-container, .lesson-types-list .lesson-type:hover .level-modules-container {
    background-color: #555;
}
.lesson-types-list .lesson-type:active .level-modules-container, .lesson-types-list .lesson-type:hover .level-modules-container
h4,
.lesson-types-list .lesson-type:active .level-modules-container, .lesson-types-list .lesson-type:hover .level-modules-container
a {
    color: #ddd;
}
.lesson-content .component.audio .play, .btn.btn-danger, .lesson-content .component.audio .pause,
button[class*='Button_primary__'] {
    background-color: #cd403d!important;
}
div[class^='TestIcon_container__'][class^='TestIcon'] {
    background-color: #444 !important;
}
a[class*='Button_blue__'] {
    background-color: #2781a1 !important;
    box-shadow: 0 2px #206b86 !important;
}
section[class^='AudioPopout_container__'] {
    background-color: #333 !important;
    border: 1px solid #122b40 !important;
}
div[class^='TestUI_container'],
div[class^='TestUI_container']:focus {
    border: 2px solid #206b86;
    outline: none;
}
div[class*='LessonComponent_markdownComponent-tip__'] {
    background-color: #206b86 !important;
}
div[class^='PageUI_tagNote__'] {
    color: #ddd;
    background-color: #444;
}
div[class^='PageUI_tagNote__'] [class*='PageUI_tagNoteTitle__'] strong {
    color: #eee !important;
}
div[class*='LessonComponent_markdownComponent-culture__'] {
    background-color: #3c643e !important;
}
div[class*='LessonComponent_markdownComponent-culture__'] h2 {
    color: #afcfb1 !important;
}
div[class*='LessonComponent_markdownComponent-alt'] {
    background-color: #55285f !important;
}
div[class*='LessonComponent_markdownComponent-alt'] h2 {
    color: #9b42af !important;
}
}
