@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.camp-firefox.de/forum/) {

div.content {
  overflow-x: auto !important;
}

div.content a:visited {
  color: #581C90 !important;
}

dl.postprofile {
  width: 15% !important;
}

div.postbody {
  width: 84% !important;
}

dl.attachbox {
  width: 98% !important;
}

div#smilies #logo {
  display: none !important;
}

div.signature a, div.signature a span {
  color: #368AD2 !important;
}

div.signature a:visited {
  color: #581C90 !important;
}

div.signature, div.signature span {
  color: black !important;
  font-weight: normal !important;
  font-size: x-small !important;
  font-style: normal !important;
}

div#header_bg {
  display: none !important;
}

div.navbar {
  margin-top: 3px !important;
}

div#mcs_header_bg {
  background-color: #CADCEB !important;
  background-image: none !important;
  margin-left: auto !important;
  margin-right: auto !important;
  width: 960px !important;
}

form#search label#textBeforeSearch, form#search a {
  color: #105289 !important;
}

div#qr_showeditor_div {
  display: none !important;
}

}
