/* ==UserStyle==
@name           BaiDu Flat P
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Enlarge font size, reduce saturation, and remove unnecessary elements.
@author         fiammanda
==/UserStyle== */

@-moz-document domain("www.baidu.com") {
    ::selection {
        color: #eee;
        background: #333;
    }
    html, html body {
        font-size: 16px;
    }
    body {
        text-align: justify;
        line-height: 1.75 !important;
        color: #333;
    }
    body, button, input, select, textarea, th, td, .p1, .p2, .s_ipt, .bdsug li, .soutu-layer #soutu-url-kw, #page {
        font-family: "Noto Sans CJK SC", sans-serif;
    }
    em:not([class]) {
        color: inherit;
    }
    a, a em, span {
        text-decoration: none !important;
    }
    a, .op_dict3_highlight {
        color: #3173d0;
        transition: all .2s ease-in-out;
    }
    p a, #rs a {
        filter: grayscale(.5);
    }
    a:visited {
        color: #6356ae;
    }
    a:hover {
        opacity: .75;
    }
    .s-sp-menu a {
        color: #555;
    }
    .s_ipt_wr, #head_wrapper.s-down #kw, .s_form_login #kw {
        border-radius: 2px 0 0 2px;
        overflow: hidden;
        transition: all .25s ease-in-out;
    }
    .s_ipt_wr.iptfocus, .s-title-img .nobg_s_fm_focus, .c-tip-custom-input-focus,.c-tip-si-input-focus {
        border-color: #666 !important;
        transition: all .25s ease-in-out;
    }
    .s_ipt {
        margin: 5px 0 0 10px;
    }
    .s_btn, .s-down .btn, .s-title-img .btn, .soutu-url-btn {
        background: #555 !important;
        border: none !important;
        box-shadow: none !important;
        transition: all .2s ease-in-out;
    }
    .s_btn, .s-down .btn, .s-title-img .btn, .soutu-url-btn {
        border-radius: 0 2px 2px 0;
    }
    .s_btn.btnhover, .s-down .btn.btnhover, .s-down .btn:hover, .s-title-img .btn.btnhover, .s-title-img .btn:hover, .soutu-url-btn:hover {
        opacity: .75;
        box-shadow: none;
    }
    .s_btn_wr_h {
        border: none;
    }
    .soutu-env-result .soutu-layer, .soutu-env-imgresult .soutu-layer {
        width: 638px;
    }
    .soutu-layer .soutu-url-wrap, .soutu-layer .soutu-url-btn {
        height: 32px;
    }
    .soutu-layer .soutu-url-btn {
        margin-top: 0 !important;
        box-shadow: 0 1px 0 #555 !important;
    }
    .soutu-btn {
        transition: all .2s ease-in-out;
    }
    .soutu-btn:hover {
        background-position: 0 0;
        opacity: .75;
    }
    .soutu-env-newindex .soutu-no-skin .soutu-url-wrap {
        width: 535px;
        height: 38px;
    }
    .soutu-layer {
        border-color: #555 !important;
        border-radius: 2px;
        overflow: hidden;
    }
    .soutu-layer .soutu-close:hover {
        background-position: -20px 0;
    }
    .soutu-layer .upload-wrap {
        font-size: 15px;
        border-color: #999;
        border-radius: 2px;
        transition: all .2s ease-in-out;
    }
    .soutu-layer .upload-wrap:hover {
        border-color: #666;
    }
    .soutu-layer .upload-icon, .st_com_abstract_novelicon {
        display: none;
    }
    #head_wrapper.s-down .soutu-env-newindex .soutu-url-btn, .soutu-env-newindex .soutu-no-skin .soutu-url-btn {
        height: 38px;
    }
    .bdsug {
        top: 40px !important;
        border-radius: 2px;
    }
    .bdsug li {
        padding: 4px 10px;
        width: 518px;
        font-size: 15px;
        line-height: 1.5;
        color: #333;
        border-bottom: 1px solid #eee;
        cursor: pointer;
        transition: all .2s ease-in-out;
    }
    .bdsug li:last-child {
        border: none;
    }
    .bdsug b, .bdsug-store b, .bdsug-store span {
        font-weight: 400;
        color: #3173d0;
    }
    .bdsug-store-del {
        top: 5px;
        text-decoration: none;
    }
    .bdsug .bdsug-feedback-wrap {
        line-height: 25px;
    }
    .bdsug .bdsug-feedback {
        text-decoration: none;
        cursor: pointer;
    }
    .bdpfmenu, .usermenu {
        border-radius: 2px;
    }
    .bdpfmenu a, .usermenu a {
        text-align: center;
    }
    .bdpfmenu a:hover, .bdpfmenu a:active, #u .usermenu a:hover, #u .usermenu a:active {
        background: #555;
    }
    .search_tool, .search_tool_conter span {
        transition: all .2s ease-in-out;
    }
    #result_logo {
        visibility: hidden;
        pointer-events: none;
    }
    #head.s_down {
        box-shadow: none;
    }
    #content_right {
        display: none;
    }
    #content_left, .c-container, .search_tool_conter, .nums {
        width: 640px;
    }
    #content_left .result-op, #content_left .result {
        margin: 2em 0;
    }
    #s_tab b {
        border: none;
        font-weight: 400;
    }
    #s_tab a:last-child {
        position: relative;
    }
    #s_tab a:last-child::after {
        content: "";
        position: absolute;
        left: -486px;
        bottom: 0;
        width: 100%;
        height: 2px;
        background: #999;
        transition: left .5s ease-in-out;
    }
    #s_tab a:nth-child(2):hover ~ a:last-child::after {
        left: -432px;
    }
    #s_tab a:nth-child(3):hover ~ a:last-child::after {
        left: -378px;
    }
    #s_tab a:nth-child(4):hover ~ a:last-child::after {
        left: -324px;
    }
    #s_tab a:nth-child(5):hover ~ a:last-child::after {
        left: -270px;
    }
    #s_tab a:nth-child(6):hover ~ a:last-child::after {
        left: -216px;
    }
    #s_tab a:nth-child(7):hover ~ a:last-child::after {
        left: -162px;
    }
    #s_tab a:nth-child(8):hover ~ a:last-child::after {
        left: -108px;
    }
    #s_tab a:nth-child(9):hover ~ a:last-child::after {
        left: -54px;
    }
    #s_tab a:last-child:hover::after {
        left: 0;
    }
    .m, a.m {
        color: #999;
    }
    .t {
        margin: .25em 0;
    }
    .c {
        font-size: 14px;
        color: #666;
    }
    .c-tip-con {
        border-radius: 2px;
        box-shadow: none;
    }
    .c-tip-custom-input, .c-tip-si-input {
        border-radius: 1px;
    }
    .c-tip-con .c-tip-timerfilter li .c-tip-custom-submit, .c-tip-con .c-tip-timerfilter li .c-tip-timerfilter-si-submit {
        border-radius: 1px;
        padding: 3px 10px;
    }
    .c-icon {
        vertical-align: -3px;
    }
    .c-abstract {
        font-size: 14px;
    }
    .c-span18 {
        width: 500px;
    }
    .c-span6.general_image_pic {
        padding-top: .5em;
    }
    .c-img, .c-img4, .c-btn {
        border-radius: 2px;
    }
    .c-img {
        opacity: .75;
    }
    .c-showurl {
        color: #666;
    }
    .c-row {
        margin: .5em 0;
    }
    .c-border {
        width: 620px;
        border-radius: 2px;
        box-shadow: none;
    }
    .op_dict3_lineone_result, .op_dict3_linetwo_result {
        width: 620px;
    }
    .op_dict3_lineone_result span {
        width: 560px;
    }
    .c-tip-icon, .c-row.c-gap-top + a {
        display: none;
    }
    .c-container, .c-container td {
        font-size: 14px;
    }
    .c-btn {
        line-height: 1.5;
    }
    .c-btn:hover {
        border-color: #999;
    }
    .c-text {
        vertical-align: -2px;
        border-radius: 1px;
    }
    .c-text-public {
        background-color: #3173d0;
    }
    .c-text-danger {
        background-color: #7086a6;
    }
    .c-gap-right:not(.op_dict_text1) {
        display: inline-block;
        margin-top: .5em;
    }
    .c-offset, .op-tieba-general-mainpl {
        padding-left: 0;
    }
    .c-tabs-nav .c-tabs-nav-selected {
        border-top-color: #7086a6;
    }
    .op-tieba-general-maintable td {
        padding: .25em 0;
    }
    .op-tieba-general-lookmore {
        display: none;
    }
    .op-tieba-general-mainpl, .op-bk-polysemy-move {
        margin: .5em 0;
    }
    .op-tieba-general-graycolor, [style="color:#666;float:right"] {
        font-size: 13px !important;
    }
    .op_dict3_how_read, .op-dict3-repeat {
        filter: hue-rotate(25deg) grayscale(.25);
    }
    .op_dict3_liju_read {
        top: 0;
    }
    .op_exactqa_detail_s_answer {
        margin: .5em 0;
    }
    .op_exactqa_detail_s_answer span, .op_exactqa_detail_s_answer .op_exactqa_detail_s_answer_scroll_content {
        font-family: "Noto Serif CJK SC", serif;
        font-size: 16px;
    }
    .nvl-bookstore-wrap .title-label {
        padding: 2px 4px 3px;
        vertical-align: -3px;
        filter: grayscale(1);
        pointer-events: none;
    }
    .nvl-bookstore-wrap .read-btn {
        background: #555;
        border-radius: 2px;
        box-shadow: none;
    }
    .nvl-bookstore-wrap .new-label {
        background: #7086a6;
    }
    .nvl-bookstore-wrap .source {
        padding: 0;
        color: #666;
    }
    .nvl-bookstore-wrap .source::before {
        content: none;
    }
    .chapter-list .c-row {
        margin: 0;
    }
    .general_image_pic a {
        opacity: .75;
    }
    .general_image_pic a:hover {
        opacity: 1;
    }
    .subLink_answer li {
        margin: .5em 0;
    }
    .f13 {
        margin: .5em 0 0;
        font-size: 13px;
    }
    .fk {
        visibility: hidden;
        pointer-events: none;
    }
    .nor-src-icon, .nor-src-icon-v {
        display: none;
    }
    span[style="color:#008000"] {
        opacity: .75;
        filter: hue-rotate(100deg) grayscale(.5);
    }
    #rs .tt {
        font-weight: 400;
    }
    #page a {
        color: #666;
    }
    #page a:hover {
        opacity: 1;
    }
    #page .n, #page .pc {
        border-radius: 2px;
        transition: all .2s ease-in-out;
    }
    #page .n:hover, #page a:hover .pc {
        background: #f9f9f9;
        border-color: #e1e2e3;
    }
    #head_wrapper {
        height: 40%;
    }
    #head_wrapper .s-p-top {
        height: 100%;
    }
    #s_lg_img {
        display: none;
    }
    #s_wrap {
        display: none;
    }
    #s_top_wrap {
        background: #fff;
    }
    #s_icons {
        font-size: 12px;
    }
    .weather-setting-content {
        text-align: center;
        border-radius: 0 0 4px 0;
        box-shadow: none;
    }
    .s-mod-setweather .lunar-mod .lunar-setting-btn {
        width: auto;
        background: none;
        transition: all .2s ease-in-out;
    }
    .s-mod-setweather .lunar-mod span, .s-mod-setweather .everyday-item p {
        font-size: 13px;
    }
    #u_sp .s_bri {
        background: #666;
        border-color: #666;
    }
    #bottom_layer {
        font-size: 12px;
    }
    #s-bottom-layer-hide-card-btn {
        display: none;
    }
    [style="display:block !important;visibility:visible !important"] {
        height: 0;
    }
    body:not([link]) {
        background: url("https://source.unsplash.com/featured/1920x1080") center/cover;
    }
    body:not([link]) #wrapper {
        opacity: .9;
    }
}
