css:
@-moz-document domain("blog.worldofwarships.com") {
/*
 * Author: Spedwards/TheNorsePantheon
 */
.content__filters,
.filters-selector {
    background-color: rgba(10,36,61);
}

.filters .filter__title,
.filters .filter__add,
.filters-selector__title,
.filters-selector__categories label,
.filters-selector__controls span {
    color: #ffffff;
    border-color: #ffffff;
}

.article__header time,
.article__tags li {
    color: #77eeee;
}

.article__title {
    color: #ffffff;
}

html,
.content {
    background-color: #102031;
}

p, li {
    color: #ccc;
}

.article:not(.cke_editable) .spoiler-title {
    color: #00cccc;
    border-color: #00cccc;
}

.article:not(.cke_editable) .spoiler-content {
    background-color: hsla(0,0%,100%,.08);
    color: #ccc;
}
}

@-moz-document url-prefix("https://blog.worldofwarships.com/blog") {
.container-content {
    background-color: #0b344d;
}
}
