css:
@-moz-document domain("20min.ch") {
[class^='Navigation'],
[class^='Navigation']:hover,
[class^='NavigationCategory_toggle__'],
[class^='Layout'] {
	background-color: var(--header-background-color);
    border-bottom-color: var(--header_border-color);
    color: var(--titres-color);
}

[class^='Navigation'] [class^='Navigation'],
[class^='NavigationItem_item__'][class^='NavigationItem'][class^='NavigationItem'] {
    color: var(--titres-color);
}

[class^='NavigationCategory_dropdown'] [class^='NavigationCategory_list'],
[class^='NavigationCategory_'] [class^='NavigationCategory_list'],
[class^='_app_site'],
[class^='_app_site'][class^='_app_site'] {
    background-color: var(--header-background-color);
}

[class^='Article_article'],
[class^='Rating'],
[class^='Rating'][class^='Rating'],
[class^='Activity'],
[class^='ReportDialog'],
[class^='ReportDialog'][class^='ReportDialog'],
[class^='TeaserContent'],
[class^='Comment'],
[class^='Teaser_teaser'][class^='Teaser_'],
[class^='Article_'][class^='Article_'],
[class^='CockpitNavigation_cockpitMenu'],
[class^='GadgetContainer_cockpitMenu'],
[class^='ActivityBar'][class^='ActivityBar'] {
    background-color: var(--article-background-color);
}

[class^='CommentInput'] {
    color: var(--text-color)
}

}

@-moz-document domain("20min.ch") {

:root {
    --article-background-color: #6281ae;
    --header-background-color: #2d466a;
    --header_border-color: #dfe6f0;
    --titres-color: rgba(200, 200, 200, .95);
    --text-color: #1a1a1a;
}
}
