css:
@-moz-document domain(boards.4channel.org) {
 	:root {
	--background: #1d1d1d;  
    --card-background: #272727;
    --link: #afb3b3;
    --text: #ddd;
}

img[alt="4chan"] {
    display: none;
}

#bannerCnt {
    position: relative;
    z-index: -1;
    background: url("https://archive-media-1.nyafuu.org/vp/image/1373/63/1373632785427.gif");
}

body {
 	background: var(--background);
    padding: 0 50px;
    color: var(--text);
}

body::-webkit-scrollbar {
  width: 10px;
}
 
body::-webkit-scrollbar-track {
  background: var(--background);
}
 
body::-webkit-scrollbar-thumb {
  background: var(--link);
}

.pagelist.desktop {
 	background: var(--card-background);   
}

.pagelist.desktop .prev input,
.pagelist.desktop .next input {
    color: transparent;
    background: transparent;
    border: none;
    width: 50px;
    height: 18px;
    cursor: pointer;
}

.pagelist.desktop .prev input:focus,
.pagelist.desktop .next input:focus {
 	outline: none;   
}

.pagelist.desktop .prev,
.pagelist.desktop .next {
    position: fixed;
    left: 0;
    z-index: 1;
    color: transparent;
}

.pagelist.desktop .next span {
 	display: none;   
}

.pagelist.desktop .prev *:hover,
.pagelist.desktop .next *:hover {
 	color: transparent;
    border: none;
}

.depagelink {
 	display: none;
}

.pagelist.desktop .prev {
 	bottom: 20px;
}

.pagelist.desktop .next {
   bottom: 0;
}

.pagelist.desktop .prev::after,
.pagelist.desktop .next::after {
    background: var(--link);
    color: var(--background);
    position: fixed;
    z-index: -1;
    width: 50px;
    font-size: 14px;
    text-align: center;
    font-weight: bold;
    height: 18px;
    border-radius: 5px;
    box-shadow: 0px 4px 10px rgba(0,0,0,0.25);
    left: 0;
}

.pagelist.desktop .prev::after {
    content: '<<';
    bottom: 20px;
}

.pagelist.desktop .next::after {
    content: '>>';
    bottom: 0;
}

.postInfo.desktop {
    margin-bottom: 15px;
}

h3 {
 	color: var(--text);   
}

* {
 	margin: 0;
    padding: 0;
    box-sizing: border-box;
}

a.quotelink,
a.quoteLink,
a {
 	color: var(--link) !important;
}

blockquote {
 	color: var(--text);
}

div.post.reply {
 	background: var(--card-background); 
    border: none;
    margin-left: 10px;
}

.thread {
 	border-radius: 15px;
    box-shadow: 0px 4px 10px rgba(0,0,0,0.25);
    margin: 25px 0 !important;
    background: var(--card-background); 
    padding: 25px;
}

div.postContainer.replyContainer {
    position: relative;
 	margin: 10px 0;
    padding: 5px;
    border-radius: 5px;
    box-shadow: 0px 4px 10px rgba(0,0,0,0.25);
    overflow: hidden;
}

div.sideArrows {
    color: transparent;
    height: 100%;
    width: 3px;
    background: var(--link);
    position: absolute;
    top: -1px;
}

.file .fileThumb img {
 	border-radius: 5px;
    box-shadow: 0px 4px 10px rgba(0,0,0,0.25);
}

.pages strong a {
    color: var(--text) !important;
}
   
}

