css:
@-moz-document domain("flvs.net") {
#mainbody {
	background: black;
}
.info-section, .info-section h1 {
	background: black;
	color: white;
}
.navbar.navbar-fixed-top {
	background: black;
}
@media (max-width: 1200px) {
	.nav-expander {
		background: black;
	}
}
.home-callouts {
	background: black;
}
@media (min-width: 768px) {
	.col-sm-4 {
		color: white;
	}
}
.info-section h1, .info-section h2, .info-section h3 {
	color: white;
}
.navbar-header.pull-left a {
	color: white;
}
.navbar-header li a, .open>.dropdown-menu {
	background: black;
	color: white;
}
.container, .footer {
	background: black;
}
textarea, input {
	background: grey !important;
}
}

@-moz-document url-prefix("https://login.flvs.net/") {
#wrap, #footer {
	background: black !important;
}
}

@-moz-document url-prefix("https://learn.flvs.net/") {
* {
	background: black;
	color: white;
}
}

@-moz-document url-prefix("https://vsa.flvs.net/default.aspx") {
@media (min-width: 768px) {
	.id-bar-list {
		color: white;
	}
}
@media (min-width: 768px) {
	body {
		background: black;
	}
}
canvas {
	background-image: radial-gradient(grey, grey, black, black);
}
input[type=text], input[type=password] {
	background: transparent !important;
}
}

@-moz-document url-prefix("https://sjvs.flvs.net/educator/entercourse.cgi") {
td {
	background: black;
	color: white;
}
}

@-moz-document url-prefix("https://sjvs.flvs.net/webdav/educator"), url-prefix("https://sjvs.flvs.net/educator"), url-prefix("https://lti.flvs.net/") {
@media screen and (max-width: 844px) {
	.content {
		background: black;
	}
}
.content {
	background: black !important;
}
#content {
	background: black !important;
	color: white;
}
.skipimg {
	background: #b9b9b9;
}
.wrapper {
	background: #282828;
}
#content h3, #content h2 {
	color: #808080;
}
#menua, .menu_after {
	background: black;
}
#nav_bar {
	background: black !important;
}
.breadcrumbs {
	background: black
}
html, body {
	background-color: black !important;
}
.tabs div.panel, .well.infobox, .well.assignment {
	background: black !important;
}
tr, th, td {
	background: black !important;
}
.well {
	background: #262626 !important;
}
.option-choice {
	background: black;
}
.option-selected {
	background: #202020;
}
.input-xxlarge {
	color: white;
}
.points-left-column, .points-right-column {
	background: black;
}
}

@-moz-document url-prefix("https://lti.flvs.net") {
footer {
	background: black;
}
}
