css:
@-moz-document url-prefix("https://www.lightnovel.cn/forum.php?"), url-prefix("https://www.lightnovel.cn/thread")
{
    :root{
    --reading-bgcolor: /*[[reading-bgcolor]]*/;
    --reading-color: /*[[reading-color]]*/;
    --reading-font-size: /*[[reading-font-size]]*/;
    --para-indent: /*[[para-indent]]*/;
    }
    #toptb,
    #hd,
    #vfastpostform,
    .cm,
    .rate,
    .plm,
    .pls,
    .psth,
    .xs1,
    .pi,
    i.pstatus,
    .po.hin,
    .modact {
        display: none;
    }
    .pl.bm{
        background-color: var(--reading-bgcolor);
    }
    .t_f {
        -webkit-font-smoothing: subpixel-antialiased !important;
        -moz-osx-font-smoothing: auto !important;
        font-size: var(--reading-font-size);
        font-family: "Sarasa UI SC", "Microsoft YaHei", "Microsoft JhengHei", sans-serif;
        color: var(--reading-color);
    }
    .t_f font{
        font-size: var(--reading-font-size) !important;
        font-variant-numeric: tabular-nums;
        font-family: "Sarasa UI SC", "Microsoft YaHei", "Microsoft JhengHei", sans-serif;
        color: var(--reading-color);
    }
    div {
    text-indent: calc(var(--reading-font-size) * 2);
    margin-top: var(--para-indent);
    }
}
