@-moz-document domain("olx.ua") {
.Button--primary.svelte-w9wr0y.svelte-w9wr0y {background-color:#ffae00;}

/* Online user color */
.offer-sidebar .user-online {color: #62db00;}
.offer-sidebar .user-online:before {background: #62db00;}

/* Global font change */
html, body, input, textarea, select, option, optgroup, button, div, p, ul, span {font-family:'Open Sans' !important;}

ul.AdPageBox__Advantages .svelte-t0q483 {display: none !important;}
.AdPageBox svelte-o0h475 {padding-bottom: 0px !important;}
.AdPageBox__cost-caption.svelte-o0h475 {margin: 16px 0 0;}


/* Site top panel change */
#header-container .navi {background:#219020;}
.olx-website-rebranded__o {box-shadow: 0 0 0 12px #fff;}
.olx-website-rebranded__l {background: #fff;}
.olx-website-rebranded__x:before, .olx-website-rebranded__x:after {background: #fff;}
#header-container .navi .postnewlink:hover {box-shadow: none; background:#fff; color:#002f34;}
.notifycircle.new {background: #ff0000; color:#fff;}

/* Add funds */
.olx-button--secondary:hover {box-shadow: inset 0 0 0 2px #002f34;}


/* OLX Delivery badge change */
.delivery-badge {background:#ff8a00;}
.delivery-badge__icon {color:#fff;}
.delivery-flaggedoffers-badge__icon {color:#ff8a00;}

form.search .checkboxsepa .small.olx-delivery-filter-label, form.search .checkboxsepa .icon.f_checkbox.selected + .small {color:#ff8a00;}


/* Item page on site */
.offerdescription .offerobserve [data-icon^="star"], .offerdescription .offerobserve [data-icon^="star"].selected {color: #f53434;}
.quickcontact #contact_methods_below li [data-icon="phone"] {background:#3A77FF !important; color:#fff;}
.button.br3.large.fright {box-shadow: none; background:#3A77FF !important; color:#fff;}

.icon.paid {background: #ffda24;}
table.offers .price {font-size: 2em !important;}


/* Remove shit loading animation */
.listOverlay > div { width: 60px; height: 60px; position: relative; overflow: hidden;  text-indent: -1000em; top: 30px !important;   border: 16px solid #fff; border-radius: 50%; border-top: 16px solid #219020;  animation: spin 1s linear infinite;} 
@-webkit-keyframes spin {
  0% { -webkit-transform: rotate(0deg); }
  100% { -webkit-transform: rotate(360deg); }
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.listOverlay > div:before, .listOverlay > div:after {display: none;}



/* Contact block change */
.offer-sidebar__buttons .contact-button {padding:0;}
.contact-button.button-email {background: #3A77FF; box-shadow: none;}
.contact-button.button-email:hover {background: #3A77FF; box-shadow: none; transform: translateY(-0.25em); opacity: 0.9; color:#fff;}
.contact-button.link-phone {background: none; box-shadow: none !important;}
.contact-button.link-phone:hover {box-shadow: none; text-decoration: underline; }
.Button--primary.svelte-w9wr0y.svelte-w9wr0y:hover:not(:disabled) {border: none; background-color: #ffae00; color:#fff; transform: translateY(-0.25em); opacity: 0.9;}
.offer-sidebar__box--business {background:#fff;}
.AdPageBox.svelte-o0h475 {padding-top: 10px;}
.offer-user__actions {text-align:center;}
.offer-user__details .user-since { background:#f7c332; color:#fff; font-size: 1.2em; font-weight: bold; padding:5px 0; border-radius: 20px; margin-bottom: 10px;}


/* Add to FAVS in search */
.tdnone.inlblk.rel.normal, .tdnone.inlblk.selected {background: #e04444; box-shadow: none !important; color:#fff;}
a.link.hn:hover > *, a.tdnone:hover .link.hn > * {color:#fff;}
.tdnone.inlblk.rel.normal .link, .tdnone.inlblk.selected .link {color:#fff !important;}

.fblock.fleft.option-search-criteria [data-icon="star-filled"]:before {content:'âœ–'; color:#fff; position:relative; top:-1px;}



.myoffersnew__image [data-icon="courier_logo"], .myoffersnew__image [data-icon="delivery"] {right:0px; bottom:0px; position: relative; background: transparent !important; color:#fff;}
.thead__cloud {background: #9cd4ffb8 !important;}



/* Add\Edit item inputs change */
.chipschoice li > a.is-selected, .chipschoice li > span.is-selected {background: #3A77FF; color:#fff;}
.chipschoice li > a, .chipschoice li > span {background: #e2e2e2;}
form.default .fblock.complete .status.icon:before {color:#02da00 !important;}
.photos-show-mini li.with-photo.first-child > div[data-cover-label]:after {background:#f7c332;}
.button.big4.br3.large {box-shadow: none; color: #fff; background: #6393ff; }


/* Add/Edit item - OLX Delivery block change */
.WeightSelector.svelte-13u27k6 {flex-flow: row-reverse; }
.WeightSelectorHeader.svelte-1rx39t1 {flex: 1 60%;}
.WeightSelectorHeader__delivery__img.svelte-1rx39t1 {display: none;}
.WeightSelector.svelte-13u27k6 {background:#f7c332 !important; }
.Link.svelte-1319phz {text-decoration: none; color:#fff !important; font-weight: bold;}
.Chip.WeightSelectorBody__options__chip.svelte-1toax9r.Chip--choice {background: #fff4d5 !important; }
.Chip.WeightSelectorBody__options__chip.svelte-1toax9r.Chip--active {background: #3A77FF !important; }
.Chip.WeightSelectorBody__options__chip.svelte-1toax9r.Chip--active span {color:#fff !important; }


/* Remove garbage */
.messagebox.lheight22.large.marginbott25.br2.js-warning-message, .accounthead__about {display: none;}
.safety-quickcontact {display: none  !important;}
.messages-spam-warning {display: none !important;}
.offer-user__headline {display: none !important;}


/* Added item confirm */
.confirm-polaroid__main {background: #fff;}
.confirm-polaroid__actions li:first-child a {box-shadow: none; color: #fff; background: #3A77FF; }
.confirm-polaroid__actions li:first-child a:hover {box-shadow: none; color: #fff; background: #6393ff; }


/* No more free items notification */
.paid-subbscriptions-notice .arrow-on-item a, .paid-subbscriptions-notice .arrow-on-item a:hover {box-shadow: none; background: #6e98f7; color: #fff;}
ul.packagesubscription-layer-nnl-single li .countercell .button, ul.packagesubscription-layer-nnl-single li .countercell .button > * {background: #3A77FF; border-radius: 5px; color:#fff;}


/* Chat */
.button.fright.br3 {box-shadow: none; background:#3A77FF !important; color:#fff;}


/* Items list in presonal profile admin */
.myoffersnew__action a.button {box-shadow: none; background: #ececec; color: #848484;}
.myoffersnew__action a.button:hover {box-shadow: none;}
.myoffersnew__messages .button.inverted.messagesOffer {box-shadow: none; background-color: #ceddff; color: #fff; font-size: 1.3em; }
.myoffersnew__messages .button.messagesOffer {box-shadow: none; background-color: #4f85ff; color: #fff; font-size: 1.3em; }
.myoffersnew__stats {background-color: #fff2c5;}
.myoffersnew__price span {font-size: 1.5em;}




}
