css:
@-moz-document domain("www.superstart.se"), domain("superstart.se") {
/* Author: Simon Gullstrand */
/* header */

body #header {
    height: 150px;
}
body #header .header_bg {
    height: 150px;
}
#logo {
 	display: none;
}
/*
#header {
    display:none !important;
}
*/

/* Blocks */
body .expblock, body .draggable {
	-webkit-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.16);
    -moz-box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.16);
    box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.16);
    border-radius: 5px;
    margin-top: 10px;
    margin-left: auto;
    margin-right: auto;
}
body .draggable {
	width: 95% !important;
}
body .draggable .rbtop {
    background:transparent !important;
    border: none;
    padding-top: 8px;
}
body .draggable .expblock .rbright {
    border: none;
}

/* Scrollbar - looks like OSX */
::-webkit-scrollbar-thumb {
    min-height: 28px;
    padding: 100px 0 0;
    background-color: rgba(0, 0, 0, .4);
    background-clip: padding-box;
    border: 3px solid transparent;
    border-radius: 8px;
    -webkit-box-shadow: inset 1px 1px 0 rgba(0, 0, 0, .1), inset 0 -1px 0 rgba(0, 0, 0, .07);
    box-shadow: inset 1px 1px 0 rgba(0, 0, 0, .1), inset 0 -1px 0 rgba(0, 0, 0, .07);
}
::-webkit-scrollbar-track {
    background: transparent;
}

}
