css:
@-moz-document domain(olx.ua), domain(olx.bg), domain(olx.ro) {

.offer-sidebar .user-online {
    color: #62db00;
}
.offer-sidebar .user-online:before {
    background: #62db00;
}
html, body, input, textarea, select, option, optgroup, button, div, p, ul, span {
    font-family:'Open Sans', Tahoma !important;
}

.olx-website-rebranded__o {
    box-shadow: 0 0 0 12px #5b2b82;
}
.olx-website-rebranded__l {
    background: #bfbe00;
}
.olx-website-rebranded__x::before, .olx-website-rebranded__x::after {
    background: #ff8300;
}

.offer-sidebar__buttons .contact-button.link-phone,
.button.inverted, .olx-button--secondary {
    box-shadow: inset 0 0 0 2px #0198d0;
    color: #0198d0;
}
.offer-sidebar__buttons .contact-button.link-phone:hover,
.button.inverted:hover, .olx-button--secondary:hover {
    background: #00a3df;
    box-shadow: inset 0 0 0 2px #00a3df;
    color: #fff;
}

#homeShowAllAds,
.offer-sidebar__buttons .contact-button,
.paid-subbscriptions-notice .arrow-on-item a,
.button, 
.olx-button--primary {
    color: #fff;
    background: #0098d0;
    box-shadow: none;
}

#homeShowAllAds:hover,
.offer-sidebar__buttons .contact-button:hover,
.paid-subbscriptions-notice .arrow-on-item a:hover,
.button:hover, 
.olx-button--primary:hover {
    background: #00a3df;
    box-shadow: none;
    color: #fff;
}

.Button--primary.svelte-w9wr0y.svelte-w9wr0y{
	 background-color: #ff8300;
}
.Button--primary.svelte-w9wr0y.svelte-w9wr0y:hover:not(:disabled) {
    border: none;
    background-color: #ff8300;
    color:#fff;
    transform: translateY(-0.15em);
    opacity: 0.9;
}

.delivery-badge{
	background-color: #ff8300;
    color: #fff;
    width: 40px;
    height: 40px;
}


#header-container .navi .postnewlink {
    box-shadow: none;
}
#header-container .navi .postnewlink:hover {
    box-shadow: none;
    background: #ccc;
}
#header-container .navi {
    background: #fff;
    border-bottom: 1px solid #eaeaea;
}
#header-container .navi[data-icon="star"],
#header-container .navi [data-icon="star"],
#header-container .navi a  {
    color: #4d6169 !important;
}
#header-container .link:hover > *, a.tdnone:hover .link > * {
    color: #4d6169 !important;
    background-color: #eaeaea !important;
}
#header-container .tabs2 a.link:hover {
    background-color: #eaeaea;
    border-radius: 10px 10px 0 0;
}
#header-container .tabs2 .link:hover span {
    background-color: transparent !important;
}

.detailpage table.breadcrumb li .link:hover, 
.contact-grid .breadcrumb li .link:hover,
.breadcrumb ul .link:hover > *{
	color: #666 !important;
}

.userbox-dd ul li a:hover {
    background-color: #eaeaea !important;
}
.maincategories .maincategories-list .li .item a {
    padding: 10px;
    border-radius: 20px;
}
.maincategories .maincategories-list .li .item .cat-icon {
    width: 68px;
    height: 68px;
}
.maincategories .maincategories-list .li .item a span:first-child {
    font-size: 14px;
}
.maincategories .maincategories-list .li .item a:hover {
    background-color: #eaeaea;
    color: #666;
}
.maincategories .maincategories-list .li .item a:hover span:first-child {
    background-color: transparent !important;
    color: #666;
}
.subcategories-list .link:hover > *, a.tdnone:hover .link > * {
    background-color: transparent !important;
}
.maincategories .subcategories-list li {
    margin-top: 0;
}
.maincategories .subcategories-list li a {
    padding: 10px 10px 10px 24px;
    border-radius: 10px;
}
.maincategories .subcategories-list li a:hover {
    background: #f4f4f4;
}
.maincategories .subcategories-list a.inlblk::before {
    top: 12px;
}
.maincategories .subcategories-list[id^="subcatLink_"] .link:hover > span {
    color: #df0000;
}
.subnav2 ul.breaklist li a, .subnav2 ul.breaklist li.selected {
    padding: 13px 19px 14px;
}
.subnav2 ul.breaklist li a:hover {
    color: #666;
}
.link:hover > *, a.tdnone:hover .link > * {
    background-color: transparent;
    color: #333;
}

ul.categorySelectList li.hover, 
ul.categorySelectList li.selected{
	background: #eaeaea !important;
}
ul.categorySelectList li.hover a, 
ul.categorySelectList li.selected a{
	color: #002f34 !important;
}
.dropdown dd ul li a:hover,
ul.categorySelectList li ul li a:hover{
	background: #eaeaea !important;
    color: #002f34 !important;
}

.suggestinput li:hover, 
.numeric-suggest li:hover, 
.suggestinput li.active, 
.numeric-suggest li.active,
.regions-layer .column li a:hover{
	background: #eaeaea !important;
    color: #002f34 !important;
}

.regions-layer .column li a:hover::first-letter, 
.regions-layer .column li a:hover > *,
.suggestinput li:hover > *{
	color: #002f34 !important;
}


#header-container .tdnone.inlblk.rel.normal .link:hover > *, 
#header-container .tdnone.inlblk.rel.normal a.tdnone:hover .link > *, 
#header-container .tdnone.inlblk.selected  .link:hover > *, 
#header-container .tdnone.inlblk.selected  a.tdnone:hover .link > * {
	background: transparent !important;
    color: #fff !important;
}
#header-container .option-search-criteria > a:hover{
	background: #b83030 !important;
}

.flagged-offers__list li h4, .offerdescription .descriptioncontent .large {
    font-size: 14px;
    line-height: 22px;
}

.favgallerybox > li .card .price,
.pricelabel__value {
    font-weight: bold;
    color: #ff8300;
}
.not-enough-results-banner,
.ad-share,
#offers_table iframe{
    display: none !important;
}
.tdnone.inlblk.rel.normal, .tdnone.inlblk.selected {
    background: #e04444;
    box-shadow: none !important;
    color:#fff;
}
.tdnone.inlblk.rel.normal .link, .tdnone.inlblk.selected .link {
    color:#fff;
}
.notifycircle.new {
    background: #ff0000;
    color:#fff;
}

#footer-container .siteinfobox{
	background-color: #eee;
}
  
}
