css:
/* i really want this to be global */

#panel-chathistory-content .panel-content {
    background-color: #2d2d2d !important;
    color: white;
}
.chat-message .participant-name {
    width: 100%;
    color: #cacaca;
}
.side-panel-container header {
    background-color: #2d2d2d;
    border-bottom: .0625rem solid #2d2d2d;
    color: white;
}
[type=reset], [type=submit], button, html[type=button] {
    color: white;
}
.chat-history__notice.ng-scope {
    color: white;
    background-color: #2d2d2d;
    border-bottom: #2d2d2d;
}
#panel-chathistory-content footer {
    border-top: .0625rem solid #2d2d2d;
    background-color: #2d2d2d;
    padding: .75rem 1.5rem;
}
#panel-controls .panel-controls {
    padding-right: 3.75rem;
    background-color: #2d2d2d;
    color: white;
}
.panel-controls .panel-control.active {
    position: relative;
    background-color: #545454;
    color: #ffffff;
    box-shadow: inset 0 -4px 0 0 currentColor, 1px 0 0 0 rgba(0, 0, 0, .2), -1px 0 0 0 rgba(0, 0, 0, .2);
    border-radius: 0;
    cursor: default;
    z-index: 1;
}
.chat-history__activity-item--chat.user {
    background-color: #292929;
    box-shadow: inset 4px 0 0 #c56ed5;
}
.item-list ul.item-list__item-children>li:first-child .item-list__item-wrapper {
    background-color: #1f1f1f;
}
.item-list__item-wrapper {
    background-color: #2d2d2d;
}
textarea#message-input {
    border-radius: 10px;
    background-color: #212121;
    color: white;
}
.panel-controls .panel-control:hover {
    background-color: #c56ed5;
}
.scroll-content.panel-content.top-spacing {
    background-color: #2d2d2d;
    color: white;
}
h2.ng-scope {
    color: white;
}
.menu-list__control, .menu-list__control:focus {
    color: #d2d2d2;
    background-color: transparent;
}
label.ng-scope {
    color: white;
}
.scroll-content.panel-content {
    background-color: #2d2d2d;
}
h3.nosharing-message.ng-scope {
    color: #e0dede;
}
h2.moderator-header.category-header {
    background-color: #2d2d2d;
    color: white;
}
.participant-details {
    background-color: #2d2d2d;
    color: #dadada;
}
h2.participant-header.category-header {
    background-color: #2d2d2d;
    color: white;
}
.category-links .category-header, .category-links .category-header:focus, .category-links .category-header:hover {
    background-color: #2d2d2d;
    color: #c56ed5;
}
#panel-participants .participant-list-item-inner {
    background-color: #2d2d2d;
    padding-left: .3125rem;
}
h2.category-header.panel-sharing__category-header.ng-scope {
    background-color: #2d2d2d;
}
span.ng-scope {
    color: #b7b7b7 !important;
}
div#body-content {
    background-color: #2d2d2d;
    color: white;
}
.item-list__item:active {
    background: #353535;
    color: #fff
}
.item-list ul.item-list__item-children {
    background: #2d2d2d;
}
h2.category-header.panel-sharing__category-header.ng-scope {
    background-color: #2d2d2d;
}
.menu-list__no-control {
    color: white;
}
bb-svg-icon.telephony-icon.ng-isolate-scope {
    color: #afafaf;
}
button.button.menu-list__control.focus-item {
    background-color: #2d2d2d;
}
.chat-history__activity-item--chat.moderator {
    background-color: #292929;
    box-shadow: inset 4px 0 0 #26d7fd;
}
h2.presenter-header.category-header {
    background-color: #2d2d2d;
}
.dropdown-pane, .f-dropdown {
    box-shadow: 0 0 0 4px rgba(38, 38, 38, .075);
    padding: 0;
    background-color: #2d2d2d;
}
span.name.ng-binding {
    color: white;
}
.inactive-header.category-header {
    background-color: #2d2d2d !important;
}
.panel-controls .panel-control[draggable=true] .drag-icon {
    color:white;
}
.chat-input {
    border-top: .0625rem solid #2d2d2d;
    background-color: #2d2d2d;
}
