css:
@-moz-document url-prefix("https://qmplus.qmul.ac.uk") {
body {
    margin: 0;
    font-family: "Source Sans Pro","Helvetica Neue",Arial,sans-serif;
    font-size: .875rem;
    font-weight: 400;
    line-height: 1.5;
    color: #000 !important;
    text-align: left;
    background-color: #000 !important;
}
.bg-primary {
    background-color: #000!important;
}
.format-landingpage .coursebanner .container-fluid .coursename {
    padding: 1rem;
    background: #000000 !important;
    color: #fff;
    margin: 0;
    display: inline-block;
    font-size: 1.115625rem;
}
.btn-primary, .block-region .block#mod_quiz_navblock .singlebutton button, #page-site-index .subscribelink a, .pagelayout-login a.login {
    color: #fff;
    background-color: #000000;
    border-color: #000000;
    -webkit-box-shadow: none;
    box-shadow: none;
}
format-landingpage .sections>.section:not(.orphaned) .sectionheader {
    background: #000000 !important;
    color: #ffffff;
}
a {
    color: #000000;
    text-decoration: none;
    background-color: transparent;
    -webkit-text-decoration-skip: objects;
}
.block-region .block .block-inner>.header {
    background-color: #000000;
    border: none;
    border-radius: 0;
}
.format-landingpage .sections>.section:not(.orphaned) .sectionheader {
    background: #000;
    color: #fff;
}
div.news-ticker {
    margin: 0 0 30px 0;
    font-style: italic;
    position: relative;
    padding: 0 0 0 75px;
    white-space: nowrap;
    line-height: 30px;
    height: 40px;
    min-height: 40px;
    overflow: hidden;
    border: 1px solid #000;
}
.format-landingpage .coursebanner {
    padding-top: 100px;
    min-height: 145px;
    border-bottom: 5px solid #fff0;
    -webkit-background-size: cover;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
}
div.news-ticker:before {
    display: inline-block;
    font-style: normal;
    background: #fff;
    padding: 5px 13px 5px 13px;
    height: 100%;
    color: #000000;
    font-weight: 400;
    position: absolute;
    top: 0;
    left: 0;
    font-size: 1.2em;
    text-transform: uppercase;
}
.format-landingpage .sections .usersection .avatar {
    border: 2px solid #000000;
    margin-right: .5rem;
}
.text-primary {
    color: #000!important;
}
div.news-ticker-container {
    position: relative;
    clear: both;
    background: #000000;
}
.card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .well {
    position: relative;
    display: flex;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-color: #ccc;
    -webkit-background-clip: border-box;
    background-clip: border-box;
    border: 1px solid rgba(0,0,0,.125);
    border-radius: .25rem;
}
#page-footer {
    margin-top: 1em;
    padding: 1em 0;
    background: #000000;
    color: #fff;
}
.bg-white {
    background-color: #999!important;
}
div.outer div.backpack {
    width: 100%;
    background: rgba(0, 0, 0, 0.92);
    padding-top: 2rem;
    position: fixed;
    right: 0;
    top: 70px;
    height: calc(100% - 70px);
    overflow-y: auto;
    color: #f5f5f5;
    z-index: 3;
    -webkit-transition: top .3s linear;
    -o-transition: top .3s linear;
    transition: top .3s linear;
}
div.outer div.backpack .content .pinnedmodules .module.hasimage .overlay {
    content: "";
    display: block;
    width: calc(100% - -2px);
    height: calc(100% - -2px);
    background: #282727;
    border: 1px solid rgba(0, 90, 154, 0);
    position: absolute;
    top: -1px;
    left: -1px;
}
div.outer div.backpack .content .moduletabs .tab .link.pinnedlink, div.outer div.backpack .content .widgettabs .tab .link.pinnedlink {
    padding-left: 2rem;
    -webkit-background-size: 9px 13px;
    background-size: 9px 13px;
    background-repeat: no-repeat;
    background-position: .75rem 50%;
}
div.outer div.backpack .content .moduletabs .tab .link.active, div.outer div.backpack .content .widgettabs .tab .link.active {
    background: #000000;
}
div.outer div.backpack .content .moduletabs .tab .link, div.outer div.backpack .content .widgettabs .tab .link {
    border-radius: .2rem;
    border: 1px solid #ffffff;
    color: #fff;
    line-height: 1.5;
    font-weight: 400;
}
div.outer div.backpack .content .pinnedmodules .module.hasimage .overlay {
    content: "";
    display: block;
    width: calc(100% - -2px);
    height: calc(100% - -2px);
    background: #282727;
    border: 1px solid rgba(0, 0, 0, 0.9);
    position: absolute;
    top: -1px;
    left: -1px;
}
iv.outer div.backpack .content .mywidgets .widgets-widget .widget-title {
    font-size: 1.25rem;
    color: #000000;
    font-weight: 700;
    text-transform: uppercase;
}
div.outer div.backpack .user .usermenu .menu-link.logout {
    background: #3c3c3c;
    border-radius: .25rem;
}
.block-region .block.block_widgets .block-inner .btn-primary, .block-region .block.block_widgets .block-inner .block#mod_quiz_navblock .singlebutton button, .block-region .block#mod_quiz_navblock .singlebutton .block.block_widgets .block-inner button, .block-region .block.block_widgets .block-inner #page-site-index .subscribelink a, #page-site-index .subscribelink .block-region .block.block_widgets .block-inner a, .block-region .block.block_widgets .block-inner .pagelayout-login a.login, .pagelayout-login .block-region .block.block_widgets .block-inner a.login {
    color: #fff;
    background-color: #3c3c3c;
    border-color: #005a9a00;
    -webkit-box-shadow: none;
    box-shadow: none;
}
div.outer div.backpack .content .mywidgets .widgets-widget .widget-title {
    font-size: 1.25rem;
    color: #000000;
    font-weight: 700;
    text-transform: uppercase;
}
.block-region .block.block_widgets .block-inner a {
    color: #000000;
}
.block-region .block.block_widgets .block-inner .text-primary {
    color: #5f054e!important;
}
div.outer div.backpack .content .mywidgets .widgets-widget .widget-item {
    margin-bottom: .5rem;
    background-color: #888;
    border-color: #c1c1c1;
}
#region-main .course-content .qmultabs .qmultabitem.pincoursetab .pincourse.pinned {
    background-color: #000;
}
#region-main .course-content .qmultabs .qmultabitem .qmultablink.active {
    background-color: #fff;
    color: #000000;
    border-bottom-color: #fff;
}
#region-main .course-content ul.ctopics.topics li.section.main .content .sectionhead .the_toggle, #region-main .course-content ul.qmulctopics li.section.main .content .sectionhead .the_toggle {
    display: flex;
    flex-wrap: wrap;
    position: relative;
    margin: 0;
    background-image: none;
    padding: .75rem .75rem .75rem 3.5rem;
    background-color: #000000;
    border: 1px solid #dcdbdb;
}
#region-main .course-content ul.ctopics.topics li.section.main .content .sectionhead .the_toggle:hover, #region-main .course-content ul.qmulctopics li.section.main .content .sectionhead .the_toggle:hover {
    background-color: #000000;
}
.btn-link {
    font-weight: 400;
    color: #ffffff;
    background-color: transparent;
}
}
