/* ==UserStyle==
@name               Tronscan Dark (Shadow) dank-mode
@description        This is the Tronscan Dark (Shadow) dank-mode Edition for Tronscan.org
@author             zirs3d
@license            CC0-1.0
@version            0.0.9
@namespace https://greasyfork.org/users/517035
==/UserStyle== */

@-moz-document domain("tronscan.org"), domain("tronscan.io") {
/** Disable **/
div {
	background: transparent !important;
	background-color: transparent;
}


/** Button **/
button:selected {
	color: #f8f8f2 !important;
}

.table-hover .block-status-tag span,
.table-hover tr:hover .block-status-tag span {
	color: #343434 !important;
}

.ant-message-notice-content,
.ant-message-notice-content span {
	background-color: #cbcbcb !important;
	color: black !important;
}

.helpLinkContent {
	background: #676767 !important;
}

.help.commonLi .img:not(:hover),
.table-hover .truncate-ellipsis span img,
.contract-header img,
.address-title img {
	-webkit-filter: invert(1);
	filter: invert(1);
}

a.leaflet-control-zoom-in,
a.leaflet-control-zoom-out,
.leaflet-bar a:hover {
	background-color: #4d4d4d !important;
}

.contract-list .contract-filter > div.active {
	background: #d0021b !important;
}

.contract-list .contract-filter > div:not(.active) {
	background: #cbcbcb !important;
	color: #676767 !important;
}

.tabbable .nav-link:not(.active) {
	color: black !important;
	background-color: #bebebe;
}

.transfers-Container .ant-table-wrapper {
	margin-top: 50px !important;
}


/** Text **/
p,
div,
li,
a,
h1,
h2,
h3,
h4,
h5,
.card p span,
.card h3,
.card h3 span,
.card h3.text-primary span,
h4 span,
h5 span,
.dropdown-menu.footer-languages a,
.index_page_search,
.list-item-word span,
.list-group-item .media .mr-1 span,
.ant-pagination .ant-pagination-item-active a,
.transaction-new td,
.account-new .table th,
.account-new .table td,
.time-filter .time-style .time-hms,
.ant-table-row td {
	color: #f8f8f2 !important;
	font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Helvetica Neue, Arial, Noto Sans, sans-serif, Apple Color Emoji, Segoe UI Emoji, Segoe UI Symbol, Noto Color Emoji !important;
}

.pc-home-splash .card-body {
	background-color: transparent !important;
}

.pc-home-splash .panel-group {
	background-color: #3e4348f2 !important;
}

p.active-code {
	color: #343434!important
}

.monaco-menu .action-item,
.monaco-menu .action-menu-item {
	background-color: #616161 !important;
}

.monaco-menu .action-menu-item:hover {
	color: #c9c9c9 !important;
}

.monaco-editor .view-lines .view-line {
	font-family: Consolas, "Courier New", monospace !important;
}

.ant-table a div {
	color: #c53027!important;
}

.ant-table-row .address-tag span {
	font-size: 14px;
	color: #a2a2a2 !important;
}

.account_main .panel div {
	background: #3e4348f2 !important;
}

.exchange-list-tab a.cur,
.dropdown-menu.footer-languages a:hover:not(.btn),
.ant-pagination a {
	color: #c53027!important;
	text-decoration: none;
}

.exchange-list-tab,
.exchange-list-tab li {
	background: #343434!important;
}

.exchange-list-explain .new_price span:not(.col-green) {
	color: #989898 !important;
}

.chart-page .header-chart-panel .group .button.selected {
	background: #5f5f5f !important;
}

.context-menu-wrapper.topmenu,
.context-menu.action-separator td {
	background-color: #282a36 !important;
	background: #282a36 !important;
	border-color: #282a36 !important
}

.context-menu-hover {
	background-color: #4d4f59 !important;
}

.ant-pagination .ant-pagination-item:not(.ant-pagination-item-active):not(:hover) {
	background-color: #f8f8f2 !important;
}

.breadcrumb,
.btn,
.dropdown-menu .dropdown-item,
.nav-pills .nav-link,
.nav-tabs .nav-link,
.navbar .nav-link,
.notice-wrap a,
.pagination,
.panel-group .col-tag span,
.quick-links a,
.sandstone {
	font-size: 0.65rem !important;
}

.panel-group .col-tag {
	color: #c23631 !important;
	background: #3c3c3c !important;
}

.notice-wrap .item:hover .title,
.table_pos div,
.list-item-word .mr-2 span {
	color: #ada997 !important;
}

.list-group-item .media span {
	color: #c23631 !important;
}

.list-group-item {
	background-color: #3c3c3c !important;
}

.input-group.dropdown .newSearchInput {
	background: #dedede !important;
}

.newSearchInput::placeholder,
.ant-input::placeholder {
	color: #707070 !important;
}

.TRXPrice span,
input,
.footerContainer .text,
.table-hover span,
.table-responsive th,
.block-status-tag span {
	font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Helvetica Neue, Arial, Noto Sans, sans-serif, Apple Color Emoji, Segoe UI Emoji, Segoe UI Symbol, Noto Color Emoji !important;
}


/** Root **/
body {
	background-color: #282a36 !important;
	color: #f8f8f2 !important;
}


/** Popup **/
.question-mark i {
	color: #f8f8f2 !important;
}

.question-mark {
	background: #6b6b6b !important
}

.ant-tooltip-inner,
.ant-tooltip-arrow:before {
	background: #3c3c3c !important;
	background-color: #3c3c3c !important;
}

.tooltip .tooltip-inner {
	background: #6b6b6b !important;
	background-color: #6b6b6b !important;
}

.bs-tooltip-bottom .arrow:before {
	border-bottom-color: #6b6b6b !important
}

.bs-tooltip-top .arrow:before {
	border-top-color: #6b6b6b !important
}

.bs-tooltip-right .arrow:before {
	border-right-color: #6b6b6b !important
}

.bs-tooltip-left .arrow:before {
	border-left-color: #6b6b6b !important
}

.modal-content,
.sweet-alert {
	background: #151515 !important;
}

.header-overlap._transactionViewer div div:first-of-type {
	background: rgba(0, 0, 0, 0.4) !important;
}

.modal-backdrop {
	background: black !important;
}


/** Logo **/
.logo {
	display: block;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	background: url(https://i.imgur.com/iJqKZZW.png) no-repeat;
	width: 125px;
	height: 30px;
	padding-left: 125px;
}

.card-header {
	background-color: #3c3c3c !important;
}

.card-header h5 span {
	padding: 10px;
}

.card-header .fa.color-grey-100,
.card-header span,
.TRXPrice span {
	color: #f8f8f2 !important;
}

.greenPrice span {
	color: #11dea6 !important;
}

.redPrice span {
	color: #c53228 !important;
}


/** Navigation **/
nav {
	background-color: #6a737d33 !important;
}

.nav-item span.nav-link span,
a.nav-link span {
	color: #f8f8f2 !important;
}

.nav-item:hover span.nav-link span,
.nav-link.active span,
a.nav-link:hover span,
.nav-login-wallet .dropdown-item:hover span {
	color: #c23631!important;
	cursor: pointer;
}

.dropdown-item,
.dropdown-menu li,
div.dropdown-menu {
	background-color: #3e4348f2 !important;
}

.dropdown-item:focus {
	background-color: #4d4d4d !important;
}

.dropdown-header,
.dropdown-menu .text-muted {
	color: #ada997 !important;
}

.more-menu-line {
	z-index: 500;
}

.nav-testnet-dropdown-menu {
	background-color: #f8f8f2 !important;
	padding-right: 5px !important;
	margin-right: 20px !important;
}


/** Highcharts **/
rect.highcharts-background,
rect.highcharts-legend-box {
	fill: #6a737d33 !important;
}

.highcharts-legend-item text {
	fill: #ada997 !important;
	color: #ada997 !important;
}

rect.highcharts-button-box {
	fill: #ada997 !important;
}

tspan.highcharts-text-outline {
	stroke: none !important;
}

.highcharts-label text {
	fill: white !important;
}

path.highcharts-label-box.highcharts-tooltip-box {
	fill: #a8a8a8 !important;
}

.transferBlockSec rect.highcharts-background {
	fill: transparent !important;
}

.highcharts-menu {
	background: #4a4a4abf !important;
}


/** Statistics **/
.data-items,
.data-items h2,
.ant-table-tbody td {
	background: #3e4348f2 !important;
}

.top-data .data-account .ant-table-row.ant-table-row-level-0:last-child td {
	background: #2e2e2e !important;
}


/* Calendar dropdown picker **/
.ant-calendar-picker-container {
	background-color: #353535 !important;
}

.ant-calendar-selected-day .ant-calendar-date {
	background-color: #c23631 !important;
}

.ant-calendar-today .ant-calendar-date {
	border-color: #c64844 !important;
}

.ant-calendar-range .ant-calendar-in-range-cell:before {
	background: #a12d29;
}

/** Transaction Detail **/
.ant-table thead th {
	background-color: #6a737d33 !important;
	color: #f8f8f2 !important;
}

.ant-table .ant-table-tbody > tr:hover > td {
	background-color: #444444bd !important;
}

.ant-pagination .ant-select-dropdown {
	background-color: #4d4d4d !important;
}

.ant-pagination .ant-select-dropdown-menu-item-selected,
.ant-pagination .ant-select-dropdown-menu-item-selected:hover,
.ant-pagination .ant-select-dropdown-menu-item:hover {
	background-color: #8a8a8a;
}

.card.table_pos {
	margin-top: 10px !important;
}

.transaction-new .card.list-style-header,
.transaction-new .card.list-style-body,
.table-hover tbody tr:hover,
.list-style-header tbody tr:hover,
.table-responsive th,
.table-responsive td {
	background-color: #3e4348f2 !important;
}

.table-hover span,
.table-hover tr:hover span,
.table-responsive th,
.table-responsive td {
	color: #f8f8f2 !important;
}

.item-belong {
	background: url(https://i.imgur.com/liCnaRz.png) 4px 4px / 9px 9px no-repeat !important;
	background-position: 4px 4px !important;
}

.badge-success {
	background: #169410;
	color: #2eac28;
}


/** Token Detail */
.tokenDetail {
	background-color: transparent !important;
}

.tokenDetail th,
.tokenDetail td {
	color: #f8f8f2 !important;
}



/** Token Tracker **/
.token-list-wrap .create-token-btn {
	color: #c23631 !important;
}

.token-num-wrap div {
	background-color: #3e4348f2 !important;
}

.token-list-wrap .token-num-wrap > div > div:last-child > div:first-child {
	color: #c23631 !important;
}


/** Token Record **/
.tokencreated .steps-item.is-wait {
	background-image: url(https://i.imgur.com/sf445VT.png) !important;
	background-size: 100% 100% !important;
	color: #333 !important;
}

.tokencreated .steps-item.is-process {
	background-image: url(https://i.imgur.com/uLx0ldm.png) !important;
	background-size: 100% 100% !important;
	color: #333 !important;
}

.tokencreated .steps-item:first-child.is-process {
	background-image: url(https://i.imgur.com/6dTG6rp.png) !important;
	background-size: 100% 100% !important;
}

.list-style-header,
.card-body,
main row,
._nodemap .col-md-6 .card {
	background-color: #3e4348f2 !important;
}

.row.mt-3 .card-body._node {
	background-color: #888 !important;
}

.row.mt-3 ._node._node span {
	color: #1e1e1e !important;
}

.select-trc-item.false div h1,
.select-trc-item.false span {
	color: #8e8e8e !important;
}

.tokencreated .select-trc-item {
	background-image: url(https://i.imgur.com/yILNRXH.png) !important;
	background-size: 100% 100% !important;
}

.tokencreated .select-trc-item.is-active {
	background-image: url(https://i.imgur.com/jk74v7a.png) !important;
}

.text-muted {
	color: #c2c2c2!important;
}

.token-list-wrap .token-list-action .token-disabled-exchange.btn span {
	color: #636363 !important;
}


/** Contract Deployment **/
.compile-button {
	background: #676767 !important;
}

.compile-button.active {
	background: #c23631 !important;
}

.tokencreated .card-body.no-select-contract {
	background: #646464 !important;
}

.ant-form-item-label span {
	color: #f8f8f2 !important;
}

.ant-form-item-label div,
.ant-form-item-label a span {
	color: #c23631 !important;
}

.ant-select-dropdown-menu li {
	background-color: #313131;
}

.ant-select-dropdown-menu-item-active:not(.ant-select-dropdown-menu-item-disabled) {
	background-color: #676767 !important;
	color: #121212;
}


/** Representatives **/
.represent__table th,
.represent__table td {
	background: #3c3c3c !important
}

.represent__table table.sr {
	background-image: none;
}

.represent__table__content {
	color: #f8f8f2 !important
}

.represent__table__content .text-success,
.represent__table__content:hover .text-success {
	color: #93c54b!important;
}

.represent__table__content a div {
	color: #c23631!important;
}


/** Votes **/
._voteOverview .card-body div span {
	color: #ff1c85;
	font-weight: 600;
}

._voteOverview .bg-white {
	background-color: #676767 !important;
}

.vote-table tr td.up .text,
.vote-table tr td.up .text span {
	color: #69c265 !important;
}

._voteOverview a.font-weight-bold div,
.vote-table tr td.down .text span {
	color: #c23631 !important;
}
}
