css:
@-moz-document domain("steamid.io") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #000;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #000;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #C70008;
        }

body, .container, header.row, .navbar-default.navbar.row, svg {
    background: #000;
}
* {
    color: #fff;
}
#content, .panel-heading, .panel-footer, td {
    background: #111 !important;
    border-color: #222 !important;
}
input, textarea, .input-lg.dropdown-toggle.btn-default.btn, .dropdown-menu>li>a, .dropdown-menu {
    background: #222 !important;
    border-color: #333 !important;
    color: #fff !important;
}
.alert-success {
    background-color: #1d201b;
    border-color: #32392c;
    color: #52a254;
}
.checkbox {
	background: #0000;
}
}
