/*
--------------------------------------------------------------
Colours and backgrounds for forms.css
-------------------------------------------------------------- */
/* General form styles
----------------------------------------*/
select {
    border-color: var(--border);
    background-color: var(--bkgd-light);
    color: var(--text-regular);
}

label {
    color: #425067;
}

option.disabled-option {
    color: graytext;
}

/* Definition list layout for forms
---------------------------------------- */
dd label {
    color: #333;
}

fieldset.fields1 {
    background-color: transparent;
}

/* Hover effects */
fieldset dl:hover dt label {
    color: var(--text-regular);
}

fieldset.fields2 dl:hover dt label {
    color: inherit;
}

/* Quick-login on index page */
fieldset.quick-login input.inputbox {
    background-color: #F2F3F3;
}

/* Posting page styles
----------------------------------------*/
.message-box textarea {
    color: var(--text-regular);
    background: var(--bkgd-medium);
}

.message-box textarea.drag-n-drop {
    outline-color: rgba(102, 102, 102, 0.5);
}

.message-box textarea.drag-n-drop-highlight {
    outline-color: rgba(17, 163, 234, 0.5);
}

/* Input field styles
---------------------------------------- */
.inputbox {
    background-color: var(--bkgd-light);
    border-color: var(--border);
    color: var(--text-regular);
}

.inputbox:-moz-placeholder {
    color: var(--text-regular);
}

.inputbox::-webkit-input-placeholder {
    color: var(--text-regular);
}

.inputbox:hover {
    border-color: var(--border-hover);
}

.inputbox:focus {
    border-color: var(--border-focus);
}

.inputbox:focus:-moz-placeholder {
    color: transparent;
}

.inputbox:focus::-webkit-input-placeholder {
    color: transparent;
}


/* Form button styles
---------------------------------------- */
a.button1,
input.button1,
input.button3,
a.button2,
input.button2 {
    color: var(--text-regular);
    background-color: var(--buttongreen-color);
    /* Old browsers */
    /* FF3.6+ */
    background-image: -webkit-linear-gradient(top, var(--buttongreen-grad1) 0%, var(--buttongreen-grad2) 100%);
    background-image: linear-gradient(to bottom, var(--buttongreen-grad1) 0%, var(--buttongreen-grad2) 100%);
    /* W3C */
/*     filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#D2D2D2', endColorstr='#EFEFEF', GradientType=0); */
    /* IE6-9 */
}

a.button1,
input.button1 {
    border-color: var(--border);
}

input.button3 {
    background-image: none;
}

/* Alternative button */
a.button2,
input.button2,
input.button3 {
    border-color: var(--border);
}

/* <a> button in the style of the form buttons */
a.button1,
a.button2 {
    color: #000000;
}

/* Hover states */
a.button1:hover,
input.button1:hover,
a.button2:hover,
input.button2:hover,
input.button3:hover {
    border-color: var(--border-hover);
    color: var(--text-regular);
    background-color: var(--buttongreen-hover-color);
    /* Old browsers */
    /* FF3.6+ */
    background-image: -webkit-linear-gradient(top, var(--buttongreen-hover-grad1) 0%, var(--buttongreen-hover-grad2) 100%);
    background-image: linear-gradient(to bottom, var(--buttongreen-hover-grad1) 0%, var(--buttongreen-hover-grad2) 100%);
    /* W3C */
/*     filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#EFEFEF', endColorstr='#D2D2D2', GradientType=0); */
    /* IE6-9 */
}

/* Focus states */
input.button1:focus,
input.button2:focus,
input.button3:focus {
    border-color: var(--border);
    color: var(--text-regulargreen);
}

input.disabled {
    color: #666666;
}

/* jQuery popups
---------------------------------------- */
.phpbb_alert {
    background-color: #FFFFFF;
    border-color: var(--border-color);
}
.darken {
    background-color: #000000;
}

.dropdown-extended ul li {
    background-color: var(--bkgd-medium);
    border-top-color: var(--border);
}

.dropdown-extended ul li:hover {
    background-color: var(--bkgd-medium-hover);
    color: var(--text-regular);
}

.dropdown-extended .header,
.dropdown-extended .footer {
    border-color: var(--border);
    color: var(--border);
}

.dropdown-extended .footer {
    background-color: var(--button-color);
    /* Old browsers */
    /* FF3.6+ */
    background-image: -webkit-linear-gradient(top, var(--button-grad1) 0%, var(--button-grad2) 100%);
    background-image: linear-gradient(to bottom, var(--button-grad1) 0%, var(--button-grad2) 100%);
    /* W3C */
    text-shadow: none;
    color: var(--text-regulargreen) !important;
/*     filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#F1F8FF', endColorstr='#CADCEB', GradientType=0); */
    /* IE6-9 */
    border-top-style: solid;
    border-top-width: 1px;
}

.dropdown-extended .header {
    background-color: var(--bkgd-dark);
    /* Old browsers */
    /* FF3.6+ */
    background-image: -webkit-linear-gradient(top, var(--bkgd-dark-hover) 0%, var(--bkgd-dark) 100%);
    background-image: linear-gradient(to bottom, var(--bkgd-dark-hover) 0%, var(--bkgd-dark) 100%);
    filter: brightness(120%);
    /* W3C */
    text-shadow: none;
    color: var(--text-regular);
/*     filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#F1F8FF', endColorstr='#CADCEB', GradientType=0); */
    /* IE6-9 */
}

.dropdown .pointer {
    border-color: #B9B9B9 transparent;
}

.dropdown .pointer-inner {
    border-color: #FFF transparent;
}

.dropdown-extended .pointer-inner {
    border-color: #F1F8FF transparent;
}

.dropdown .dropdown-contents {
    background: var(--bkgd-light);
    border-color: #B9B9B9;
    box-shadow: 1px 3px 5px rgba(0, 0, 0, 0.2);
}

.dropdown-up .dropdown-contents {
    box-shadow: 1px 0 5px rgba(0, 0, 0, 0.2);
}

.dropdown li,
.dropdown li li {
    border-color: #DCDCDC;
}

.dropdown li.separator {
    border-color: #DCDCDC;
}

