css:
@-moz-document url-prefix("https://client.foldingathome.org/"), url-prefix("http://127.0.0.1:7396") {
body {
    background-image: none;
    background-color: #333;
}

#cp.panel {
    background-color: #222;
    color: #eee;
}

#box-all-slots {
    background-color: #333;
}

#slot-0,
#slot-1 {
    background: #333;
}

#slot-0.active,
#slot-1.active {
    background: #222;
}

.panel-header,
#cp .panel-top,
#cp .panel-pu {
    background: #404040;
}

a,
#cp .hlarge {
    color: #048bb9;
}

a.btn:hover {
    background: #222;
}

.middle {
    display: none
}

.sbHolder {
    background-color: #222;
}

.sbSelector {
    color: #048bb9;
}

.sbFocus {
    background-color: #202020;
}

.ui-widget-content {
    background: #fff;
}

.ui-widget-header {
    background: #048bb9 !important;
}

.ui-progressbar .ui-progressbar-value {
    color: #222;
}

.ui-widget-content,
.panel {
    border: none;
    box-shadow: none;
}

.panel {
    padding-bottom: 5px;
}

.button {
    background: none;
    color: #eee;
}

#btn-stop {
    background: #821414;
}

#slots .circle > span {
    display: none;
}

.box-pu li .run,
.box-pu li .upload,
.box-pu li .download {
    background-color: #2b772b;
    background-image: none;
    border: none;
}

.box-pu li .pause,
.box-pu li .finish {
    background-color: #a19921;
    background-image: none;
    border: none;
}

.circle.pausecircle.pause span {
    display: none;
}
}
