css:
@-moz-document url-prefix("https://typetest.io/") {
:root {
    /* change main colors here */
  --main-bg-color:   black;
    --main-color: 	white;
    --current-word-bg: black;
    --correct: green;
    --incorrect: red;
    /* change text */
    --font-family: cursive; /* examples: cursive, fantasy, monospace */
    --font-size: 35px;
    --letter-spacing: 3px;
    --line-height: 3rem;
}




/* don't change this code */


.correct {
    color: var(--correct);
}
.incorrect{
    color: var(--incorrect);
}


:root {
    --text-light: var(--main-bg-color);
    --blue-dark: var(--main-bg-color);
    --blue-main: var(--main-bg-color);
    --blue-light: var(--main-bg-color);
    --grey-0: var(--main-bg-color);
    --grey-1: var(--main-bg-color);
    --grey-2: var(--main-bg-color);
    --grey-3: var(--main-bg-color);
    --grey-4: var(--main-bg-color);
    --danger: var(--main-bg-color);
}
body {
    background-color: var(--main-bg-color);
    color: var(--main-color)
}
#text-container {
    background-color: var(--main-bg-color);
    color: var(--main-color);
    font-family: var(--font-family);
    letter-spacing: var(--letter-spacing);
    font-size: var(--font-size);
    line-height: var(--line-height);
    width: 100%;
}
input {
    background-color: var(--main-bg-color);
    color: var(--main-color)
}
.test-word.current {
    overflow: visible;
    background-color: var(--current-word-bg);
    color: var(--main-color);
}
#title-main {
    background-color: var(--main-bg-color);
    color: var(--main-color)
}
.highlight {
    color: var(--main-color);
}
.button-highlight {
    background-color: var(--main-bg-color);
    color: var(--main-color)
}
#results {
    background-color: var(--main-bg-color);
    color: var(--main-color)
}
#results-list li:nth-child(odd) {
    background-color: var(--main-bg-color);
    color: var(--main-color)
}
li {
    background-color: var(--main-bg-color);
    color: var(--main-color)
}
.widget {
    background-color: var(--main-bg-color);
    color: var(--main-color)
}
}
